Property.Value:
BEGIN
	SET NOCOUNT ON;
	IF @Date IS NULL SET @Date = DATEADD(DAY,-30,GETDATE());

	BEGIN TRANSACTION
		DELETE FROM [Server].[LongRunningQuery] WHERE [DateTaken] < @Date;
	COMMIT
	BEGIN TRANSACTION
		DELETE FROM [Server].[SessionInfo] WHERE [DateTaken] < @Date;
	COMMIT
END

BEGIN SET NOCOUNT ON;
DECLARE @cmd NVARCHAR(MAX) = N'';
-- Scripting Out the Logins To Be Created SELECT @cmd+= N' -- Logins To Be Created -- IF (SUSER_ID('+QUOTENAME(SP.name,'''')+') IS NOT NULL) BEGIN DROP LOGIN ' +QUOTENAME(SP.name)+ '; END CREATE LOGIN ' +QUOTENAME(SP.name)+ CASE WHEN SP.type_desc = 'SQL_LOGIN' THEN ' WITH PASSWORD = ' +CONVERT(NVARCHAR(MAX),SL.password_hash,1)+ ' HASHED, CHECK_EXPIRATION = ' + CASE WHEN SL.is_expiration_checked = 1 THEN 'ON' ELSE 'OFF' END +', CHECK_POLICY = ' +CASE WHEN SL.is_policy_checked = 1 THEN 'ON,' ELSE 'OFF,' END ELSE ' FROM WINDOWS WITH' END +' DEFAULT_DATABASE=[' +SP.default_database_name+ '], DEFAULT_LANGUAGE=[' +SP.default_language_name+ '] ' + CASE SP.type WHEN 'S' THEN ',SID = ' + CONVERT(NVARCHAR(MAX),SP.sid,1) COLLATE SQL_Latin1_General_CP1_CI_AS --AS [-- Logins To Be Created --] ELSE '' END FROM sys.server_principals AS SP LEFT JOIN sys.sql_logins AS SL ON SP.principal_id = SL.principal_id WHERE SP.type IN ('S','G','U') AND SP.name NOT LIKE '##%##' AND SP.name NOT LIKE 'NT AUTHORITY%' AND SP.name NOT LIKE 'NT SERVICE%' AND SP.name <> ('sa') AND EXISTS(SELECT SP.name INTERSECT SELECT @LoginName); /* IF (SUSER_ID('centerity') IS NULL) BEGIN CREATE LOGIN [centerity] WITH PASSWORD = 0x0200A9E5B3C5F4F92EAC3C975F46A0DB3A55AD37ED1B1353A1DE6B665BBBECF857DDBB4BBCE5643F7C1C3D62FCF104D1CE3D9D178D4B5324F2451F5FBE17F54B2C28078AF8D9 HASHED, CHECK_EXPIRATION = OFF, CHECK_POLICY = OFF, DEFAULT_DATABASE=[master], DEFAULT_LANGUAGE=[us_english] END; */ -- Scripting Out the Role Membership to Be Added SELECT @cmd+= N' -- Server Roles the Logins Need to be Added -- EXEC master..sp_addsrvrolemember @loginame = N''' + SL.name + ''', @rolename = N''' + SR.name + ''' ' --AS [-- Server Roles the Logins Need to be Added --] FROM master.sys.server_role_members SRM INNER JOIN master.sys.server_principals SR ON SR.principal_id = SRM.role_principal_id INNER JOIN master.sys.server_principals SL ON SL.principal_id = SRM.member_principal_id WHERE SL.type IN ('S','G','U') AND SL.name NOT LIKE '##%##' AND SL.name NOT LIKE 'NT AUTHORITY%' AND SL.name NOT LIKE 'NT SERVICE%' AND SL.name <> ('sa') AND EXISTS(SELECT SL.name INTERSECT SELECT @LoginName);
-- Scripting out the Permissions to Be Granted SELECT @cmd+= N' -- Server Level Permissions to Be Granted -- ' + CASE WHEN SrvPerm.state_desc <> 'GRANT_WITH_GRANT_OPTION' THEN SrvPerm.state_desc ELSE 'GRANT' END + ' ' + SrvPerm.permission_name + ' TO [' + SP.name + ']' + CASE WHEN SrvPerm.state_desc <> 'GRANT_WITH_GRANT_OPTION' THEN '' ELSE ' WITH GRANT OPTION' END collate database_default --AS [-- Server Level Permissions to Be Granted --] FROM sys.server_permissions AS SrvPerm INNER JOIN sys.server_principals AS SP ON SrvPerm.grantee_principal_id = SP.principal_id WHERE SP.type IN ( 'S', 'U', 'G' ) AND SP.name NOT LIKE '##%##' AND SP.name NOT LIKE 'NT AUTHORITY%' AND SP.name NOT LIKE 'NT SERVICE%' AND SP.name <> ('sa') AND EXISTS(SELECT SP.name INTERSECT SELECT @LoginName);
PRINT @cmd; END

BEGIN SET NOCOUNT ON;
DECLARE @cmd NVARCHAR(MAX) = N'';
-- Scripting Out the Logins To Be Created SELECT @cmd = @cmd + N' INSERT msdb.dbo.sysssispackages VALUES(' + ISNULL(N'''' + name + N'''',N'NULL') + N', ' + ISNULL(N'N''' + CONVERT(NVARCHAR(36),id) + N'''', N'NULL') + N', ' + ISNULL(N'N''' + description + N'''', N'N''Copied Maintenance Plan''') + N', ' + ISNULL(N'N''' + CONVERT(NVARCHAR(25),createdate,121) + N'''', N'NULL') + N', ' + ISNULL(N'N''' + CONVERT(NVARCHAR(36),folderid) + N'''', N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(MAX),ownersid,1), N'NULL') + N', ' + vp.Pkg + N', ' + ISNULL(CONVERT(NVARCHAR(50),packageformat), N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(50),packagetype), N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(50),vermajor), N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(50),verminor), N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(50),verbuild), N'NULL') + N', ' + ISNULL(N'N''' + vercomments + N'''', N'NULL') + N', ' + ISNULL(N'N''' + CONVERT(NVARCHAR(36),verid) + N'''', N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(2),isencrypted), N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(MAX),readrolesid,1), N'NULL') + N', ' + ISNULL(CONVERT(NVARCHAR(MAX),writerolesid,1),N'NULL') + N'); GO
' FROM msdb.dbo.sysssispackages AS P CROSS APPLY (SELECT TOP 1 ISNULL(CONVERT(NVARCHAR(MAX),cast( cast( replace( cast( CAST(P.packagedata AS VARBINARY(MAX)) AS varchar(max) ), @@SERVERNAME, N'ANAF\ANAF') as XML) as VARBINARY(MAX)),1) ,N'NULL') Pkg)VP WHERE (P.name LIKE 'mp%' AND P.description IS NULL) AND (@MaintenancePlanName IS NULL OR P.name = @MaintenancePlanName);
EXECUTE [_Admin].[dbo].[PrintMax] @cmd; SELECT @cmd END

BEGIN SET NOCOUNT ON;
--IF OBJECT_ID('tempdb..#MPLog') IS NOT NULL DROP TABLE #MPLog; --CREATE TABLE #MPLog(MP_Name NVARCHAR(1000),FLog NVARCHAR(MAX)); --EXEC [_Admin_].[usp_GetMPLogs];
IF EXISTS(SELECT TOP 1 1 FROM SYS.databases D WHERE D.name = 'SSIS') BEGIN SELECT j.name [JobName], js.step_id [StepID], js.step_name [StepName], --js.database_name [ExecutingDBOnJob], CASE WHEN JSS.[SubSystem] = 'Maintenance Plans(SSIS)' AND MP.Error COLLATE DATABASE_DEFAULT != '' THEN LR.last_run_outcome + ' + Minor Errors' WHEN LR.last_run_datetime >= ISNULL(xSDT.StartDateTime,JxA.run_requested_date) OR ST.StepID IS NULL THEN LR.last_run_outcome ELSE 'Did not run' END [Outcome], CASE WHEN LR.last_run_datetime >= ISNULL(xSDT.StartDateTime,JxA.run_requested_date) THEN case WHEN ST.StepID IS NULL THEN null else LR.last_run_datetime END ELSE NULL END [LastRunDatetime] , [Utility].[ufn_DATEDIFF2String](CASE WHEN LR.last_run_datetime >= ISNULL(xSDT.StartDateTime,JxA.run_requested_date) THEN case WHEN ST.StepID IS NULL THEN null else LR.last_run_datetime END ELSE NULL END,GETDATE()) [Diff], JSS.[SubSystem], CASE WHEN LR.last_run_datetime >= xSDT.StartDateTime THEN CASE WHEN JSS.[SubSystem] = 'Maintenance Plans(SSIS)' THEN CASE WHEN MP.Error COLLATE DATABASE_DEFAULT = '' THEN JH.message ELSE ISNULL(MP.Error COLLATE DATABASE_DEFAULT,JH.message) END WHEN LR.last_run_outcome = 'Failed' AND JS.subsystem = 'SSIS' THEN JH.message WHEN LR.last_run_outcome = 'Failed' AND JS.subsystem = 'TSQL' THEN JH.message ELSE NULL END ELSE NULL END [Message], CASE WHEN J.description LIKE '%report server%' THEN 'Report Server, ' ELSE '' END + ISNULL('Alert - ' + Al.name + ', ','') + ISNULL('Schedule - ' + SCH.name,'') [Caller] FROM msdb.dbo.sysjobs j INNER JOIN msdb.dbo.sysjobsteps js ON j.job_id = js.job_id CROSS APPLY(SELECT TOP 1 CASE WHEN patINDEX('%"Maintenance Plans\%',js.command) > 0 THEN 'Maintenance Plans(SSIS)' ELSE CASE js.subsystem WHEN 'ActiveScripting' THEN 'ActiveX Script' WHEN 'CmdExec' THEN 'Operating system (CmdExec)' WHEN 'PowerShell' THEN 'PowerShell' WHEN 'Distribution' THEN 'Replication Distributor' WHEN 'Merge' THEN 'Replication Merge' WHEN 'QueueReader' THEN 'Replication Queue Reader' WHEN 'Snapshot' THEN 'Replication Snapshot' WHEN 'LogReader' THEN 'Replication Transaction-Log Reader' WHEN 'ANALYSISCOMMAND' THEN 'SQL Server Analysis Services Command' WHEN 'ANALYSISQUERY' THEN 'SQL Server Analysis Services Query' WHEN 'SSIS' THEN 'SQL Server Integration Services Package' WHEN 'TSQL' THEN 'Transact-SQL script (T-SQL)' ELSE js.subsystem END END AS [SubSystem]) JSS LEFT JOIN (SELECT DISTINCT Ij.name,ISNULL(IIF(LAG(Ijs.on_success_step_id) OVER(ORDER BY Ijs.step_id) = 0,Ijs.step_id,LAG(Ijs.on_success_step_id) OVER(ORDER BY Ijs.step_id)),Ijs.step_id) StepID FROM msdb.dbo.sysjobs Ij inner join msdb.dbo.sysjobsteps Ijs on Ij.job_id = Ijs.job_id ) ST ON ST.StepID = JS.step_id and ST.name = j.name CROSS APPLY (SELECT TOP 1 msdb.dbo.agent_datetime( case when js.last_run_date = 0 then NULL else js.last_run_date end, case when js.last_run_time = 0 then NULL else js.last_run_time end) last_run_datetime, case WHEN ST.StepID IS NULL THEN 'Disabled' when js.last_run_outcome = 0 then 'Failed' when js.last_run_outcome = 1 then 'Succeeded' when js.last_run_outcome = 2 then 'Retry' when js.last_run_outcome = 3 then 'Canceled' else 'Unknown' end AS last_run_outcome ) LR LEFT JOIN msdb.dbo.sysjobhistory JH ON j.job_id = JH.job_id AND JH.step_id = JS.step_id AND msdb.dbo.agent_datetime(JH.run_date,JH.run_time) = case WHEN ST.StepID IS NULL THEN null else LR.last_run_datetime END LEFT JOIN msdb..sysalerts Al ON Al.job_id = j.job_id OUTER APPLY (SELECT TOP 1 s2.name FROM msdb..sysjobschedules S INNER JOIN msdb..sysschedules S2 ON S2.schedule_id = S.schedule_id WHERE j.job_id = s.job_id)SCH --LEFT JOIN #MPLog MP ON j.name LIKE MP.MP_Name + '%' OUTER APPLY (SELECT REPLACE(REPLACE(T.Error,'<X>',''),'</X>','') FROM (SELECT ld.error_message X FROM msdb..sysmaintplan_plans mp INNER JOIN msdb..sysmaintplan_subplans msp ON mp.id = msp.plan_id OUTER APPLY (SELECT TOP 1 * FROM msdb..sysmaintplan_log mpl WHERE msp.subplan_id = mpl.subplan_id ORDER BY mpl.start_time DESC)mpl LEFT JOIN msdb..sysmaintplan_logdetail ld ON mpl.task_detail_id = ld.task_detail_id WHERE j.name LIKE mp.name + '%' FOR XML PATH(''))T(Error))MP(Error) OUTER APPLY (SELECT TOP 1 [StartDateTime] = msdb.dbo.agent_datetime( CASE WHEN xjs.last_run_date = 0 then NULL else xjs.last_run_date end, CASE WHEN xjs.last_run_time = 0 then NULL else xjs.last_run_time end) FROM msdb.dbo.sysjobsteps xjs LEFT JOIN (SELECT ja.job_id, j.name AS job_name, ja.start_execution_date, ISNULL(last_executed_step_id,0)+1 AS current_executed_step_id, Js.step_name FROM msdb.dbo.sysjobactivity ja LEFT JOIN msdb.dbo.sysjobhistory jh ON ja.job_history_id = jh.instance_id INNER JOIN msdb.dbo.sysjobs j ON ja.job_id = j.job_id INNER JOIN msdb.dbo.sysjobsteps js ON ja.job_id = js.job_id AND ISNULL(ja.last_executed_step_id,0)+1 = js.step_id WHERE ja.session_id = (SELECT TOP 1 session_id FROM msdb.dbo.syssessions ORDER BY agent_start_date DESC) AND start_execution_date is not null AND stop_execution_date is null)Ac ON aC.job_id = xjs.job_id WHERE j.job_id = xjs.job_id AND (Ac.start_execution_date != msdb.dbo.agent_datetime(case when xjs.last_run_date = 0 then NULL else xjs.last_run_date end, case when xjs.last_run_time = 0 then NULL else xjs.last_run_time end) or Ac.start_execution_date is null) order by xjs.step_id)xSDT OUTER APPLY (SELECT TOP 1 ja.run_requested_date FROM msdb.dbo.sysjobactivity ja WHERE j.job_id = ja.job_id ORDER BY ja.run_requested_date desc)JxA OUTER APPLY( SELECT TOP 1 MESSAGE--,event_message_id,package_name,event_name,message_source_name,package_path,execution_path,message_type,message_source_type FROM SSISDB.catalog.event_messages em WHERE em.package_name COLLATE database_default = RIGHT( SUBSTRING(js.command,0,patINDEX('%.dtsx%',js.command)), CHARINDEX( '\', REVERSE( SUBSTRING(js.command,0,patINDEX('%.dtsx%',js.command))) + '\' ) - 1 ) +N'.dtsx' --AND em.operation_id = (SELECT MAX(execution_id) FROM SSISDB.catalog.executions) AND event_name = 'OnError' ORDER BY event_message_id DESC )SS
WHERE j.enabled = 1 ORDER BY j.name,js.step_id; END ELSE BEGIN SELECT j.name [JobName], js.step_id [StepID], js.step_name [StepName], --js.database_name [ExecutingDBOnJob], CASE WHEN JSS.[SubSystem] = 'Maintenance Plans(SSIS)' AND MP.Error COLLATE DATABASE_DEFAULT != '' THEN LR.last_run_outcome + ' + Minor Errors' WHEN LR.last_run_datetime >= ISNULL(xSDT.StartDateTime,JxA.run_requested_date) OR ST.StepID IS NULL THEN LR.last_run_outcome ELSE 'Did not run' END [Outcome], CASE WHEN LR.last_run_datetime >= ISNULL(xSDT.StartDateTime,JxA.run_requested_date) THEN case WHEN ST.StepID IS NULL THEN null else LR.last_run_datetime END ELSE NULL END [LastRunDatetime] , [Utility].[ufn_DATEDIFF2String](CASE WHEN LR.last_run_datetime >= ISNULL(xSDT.StartDateTime,JxA.run_requested_date) THEN case WHEN ST.StepID IS NULL THEN null else LR.last_run_datetime END ELSE NULL END,GETDATE()) [Diff], JSS.[SubSystem], CASE WHEN LR.last_run_datetime >= xSDT.StartDateTime THEN CASE WHEN JSS.[SubSystem] = 'Maintenance Plans(SSIS)' THEN CASE WHEN MP.Error COLLATE DATABASE_DEFAULT = '' THEN JH.message ELSE ISNULL(MP.Error COLLATE DATABASE_DEFAULT,JH.message) END WHEN LR.last_run_outcome = 'Failed' AND JS.subsystem = 'SSIS' THEN JH.message WHEN LR.last_run_outcome = 'Failed' AND JS.subsystem = 'TSQL' THEN JH.message ELSE NULL END ELSE NULL END [Message], CASE WHEN J.description LIKE '%report server%' THEN 'Report Server, ' ELSE '' END + ISNULL('Alert - ' + Al.name + ', ','') + ISNULL('Schedule - ' + SCH.name,'') [Caller] FROM msdb.dbo.sysjobs j INNER JOIN msdb.dbo.sysjobsteps js ON j.job_id = js.job_id CROSS APPLY(SELECT TOP 1 CASE WHEN patINDEX('%"Maintenance Plans\%',js.command) > 0 THEN 'Maintenance Plans(SSIS)' ELSE CASE js.subsystem WHEN 'ActiveScripting' THEN 'ActiveX Script' WHEN 'CmdExec' THEN 'Operating system (CmdExec)' WHEN 'PowerShell' THEN 'PowerShell' WHEN 'Distribution' THEN 'Replication Distributor' WHEN 'Merge' THEN 'Replication Merge' WHEN 'QueueReader' THEN 'Replication Queue Reader' WHEN 'Snapshot' THEN 'Replication Snapshot' WHEN 'LogReader' THEN 'Replication Transaction-Log Reader' WHEN 'ANALYSISCOMMAND' THEN 'SQL Server Analysis Services Command' WHEN 'ANALYSISQUERY' THEN 'SQL Server Analysis Services Query' WHEN 'SSIS' THEN 'SQL Server Integration Services Package' WHEN 'TSQL' THEN 'Transact-SQL script (T-SQL)' ELSE js.subsystem END END AS [SubSystem]) JSS LEFT JOIN (SELECT DISTINCT Ij.name,ISNULL(IIF(LAG(Ijs.on_success_step_id) OVER(ORDER BY Ijs.step_id) = 0,Ijs.step_id,LAG(Ijs.on_success_step_id) OVER(ORDER BY Ijs.step_id)),Ijs.step_id) StepID FROM msdb.dbo.sysjobs Ij inner join msdb.dbo.sysjobsteps Ijs on Ij.job_id = Ijs.job_id ) ST ON ST.StepID = JS.step_id and ST.name = j.name CROSS APPLY (SELECT TOP 1 msdb.dbo.agent_datetime( case when js.last_run_date = 0 then NULL else js.last_run_date end, case when js.last_run_time = 0 then NULL else js.last_run_time end) last_run_datetime, case WHEN ST.StepID IS NULL THEN 'Disabled' when js.last_run_outcome = 0 then 'Failed' when js.last_run_outcome = 1 then 'Succeeded' when js.last_run_outcome = 2 then 'Retry' when js.last_run_outcome = 3 then 'Canceled' else 'Unknown' end AS last_run_outcome ) LR LEFT JOIN msdb.dbo.sysjobhistory JH ON j.job_id = JH.job_id AND JH.step_id = JS.step_id AND msdb.dbo.agent_datetime(JH.run_date,JH.run_time) = case WHEN ST.StepID IS NULL THEN null else LR.last_run_datetime END LEFT JOIN msdb..sysalerts Al ON Al.job_id = j.job_id OUTER APPLY (SELECT TOP 1 s2.name FROM msdb..sysjobschedules S INNER JOIN msdb..sysschedules S2 ON S2.schedule_id = S.schedule_id WHERE j.job_id = s.job_id)SCH --LEFT JOIN #MPLog MP ON j.name LIKE MP.MP_Name + '%' OUTER APPLY (SELECT REPLACE(REPLACE(T.Error,'<X>',''),'</X>','') FROM (SELECT ld.error_message X FROM msdb..sysmaintplan_plans mp INNER JOIN msdb..sysmaintplan_subplans msp ON mp.id = msp.plan_id OUTER APPLY (SELECT TOP 1 * FROM msdb..sysmaintplan_log mpl WHERE msp.subplan_id = mpl.subplan_id ORDER BY mpl.start_time DESC)mpl LEFT JOIN msdb..sysmaintplan_logdetail ld ON mpl.task_detail_id = ld.task_detail_id WHERE j.name LIKE mp.name + '%' FOR XML PATH(''))T(Error))MP(Error) OUTER APPLY (SELECT TOP 1 [StartDateTime] = msdb.dbo.agent_datetime( CASE WHEN xjs.last_run_date = 0 then NULL else xjs.last_run_date end, CASE WHEN xjs.last_run_time = 0 then NULL else xjs.last_run_time end) FROM msdb.dbo.sysjobsteps xjs LEFT JOIN (SELECT ja.job_id, j.name AS job_name, ja.start_execution_date, ISNULL(last_executed_step_id,0)+1 AS current_executed_step_id, Js.step_name FROM msdb.dbo.sysjobactivity ja LEFT JOIN msdb.dbo.sysjobhistory jh ON ja.job_history_id = jh.instance_id INNER JOIN msdb.dbo.sysjobs j ON ja.job_id = j.job_id INNER JOIN msdb.dbo.sysjobsteps js ON ja.job_id = js.job_id AND ISNULL(ja.last_executed_step_id,0)+1 = js.step_id WHERE ja.session_id = (SELECT TOP 1 session_id FROM msdb.dbo.syssessions ORDER BY agent_start_date DESC) AND start_execution_date is not null AND stop_execution_date is null)Ac ON aC.job_id = xjs.job_id WHERE j.job_id = xjs.job_id AND (Ac.start_execution_date != msdb.dbo.agent_datetime(case when xjs.last_run_date = 0 then NULL else xjs.last_run_date end, case when xjs.last_run_time = 0 then NULL else xjs.last_run_time end) or Ac.start_execution_date is null) order by xjs.step_id)xSDT OUTER APPLY (SELECT TOP 1 ja.run_requested_date FROM msdb.dbo.sysjobactivity ja WHERE j.job_id = ja.job_id ORDER BY ja.run_requested_date desc)JxA
WHERE j.enabled = 1 ORDER BY JSS.[SubSystem],j.name,js.step_id; END END

BEGIN SET NOCOUNT ON;
DECLARE @Silance BIT = 0;
DECLARE @output nvarchar(max) DECLARE @maintplan NVARCHAR(1000) DECLARE @MP_Name NVARCHAR(1000) IF OBJECT_ID('tempdb..#MPLog') IS NULL CREATE TABLE #MPLog(MP_Name NVARCHAR(1000),FLog NVARCHAR(MAX)); ELSE SET @Silance = 1;
--Registry DECLARE @regvalue varchar(1000) DECLARE @regKey varchar(100) DECLARE @Path varchar(1000) --SQL Versions BEGIN IF OBJECT_ID('tempdb..#checkversion') IS NOT NULL DROP TABLE #checkversion; CREATE TABLE #checkversion ( version nvarchar(128), common_version AS SUBSTRING(version, 1, CHARINDEX('.', version) + 1 ), major AS PARSENAME(CONVERT(VARCHAR(32), version), 4), minor AS PARSENAME(CONVERT(VARCHAR(32), version), 3), build AS PARSENAME(CONVERT(varchar(32), version), 2), revision AS PARSENAME(CONVERT(VARCHAR(32), version), 1) );
INSERT INTO #checkversion (version) SELECT CAST(SERVERPROPERTY('ProductVersion') AS NVARCHAR(128)) ; SELECT @regKey = 'SOFTWARE\Microsoft\Microsoft SQL Server\MSSQL' + major + '.MSSQLServer\SQLServerAgent' FROM #checkversion; END
--Registry BEGIN EXEC master.dbo.xp_regread @rootkey='HKEY_LOCAL_MACHINE', @key=@regKey, @value_name='ErrorLogFile', @value=@regvalue OUTPUT, @output = 'no_output'
SELECT @Path = REPLACE(@regvalue,'\SQLAGENT.OUT','') END
--Get Dir From Path BEGIN IF OBJECT_ID('tempdb..#Files') IS NOT NULL DROP TABLE #Files; SELECT * INTO #Files FROM [dbo].[DirectoryList] (@Path,'*.txt');
DECLARE CRmaintplan CURSOR FOR SELECT MP.name,@Path + '\' + Fi.Name FROM MSDB..sysmaintplan_plans MP CROSS APPLY (SELECT TOP 1 F.Name FROM #Files F WHERE F.Name LIKE MP.name + '%' ORDER BY DateCreated DESC )Fi; END
OPEN CRmaintplan FETCH NEXT FROM CRmaintplan INTO @MP_Name,@maintplan WHILE @@FETCH_STATUS = 0 BEGIN
EXECUTE [dbo].[FileRead] @maintplan,@output OUTPUT; INSERT #MPLog SELECT @MP_Name,@output
FETCH NEXT FROM CRmaintplan INTO @MP_Name,@maintplan END CLOSE CRmaintplan DEALLOCATE CRmaintplan
IF @Silance = 0 SELECT * FROM #MPLog END

BEGIN
    SET NOCOUNT ON
    DECLARE @serverName VARCHAR(500);
	DECLARE @sql VARCHAR(400);
	DECLARE @cmd NVARCHAR(MAX) = N'';
	DECLARE @cmdNotification NVARCHAR(MAX) = N'';
	IF @Operator IS NULL
		SELECT	TOP 1 @Operator = S.name
		FROM	msdb..sysoperators S
		WHERE	S.name LIKE '%DBA%' OR S.name LIKE '%Admin%'
	SET @serverName = @@ServerName;
	SET @sql = 'powershell.exe -c "Get-WmiObject -ComputerName '
		+ QUOTENAME(@serverName, '''')
		+ ' -Class Win32_Volume -Filter ''DriveType = 3'' | select name,capacity,freespace | foreach{$_.name+''|''+$_.capacity/1048576+''%''+$_.freespace/1048576+''*''}"';
	IF OBJECT_ID('tempdb..#Result') IS NOT NULL DROP TABLE #Result
	CREATE TABLE #Result ( list VARCHAR(255) );
	INSERT  #Result
			EXEC xp_cmdshell @sql;

	SELECT C.DriveName ,
	        C.[Capacity(GB)] ,
			b.[Byte],
			b.[MB],
			b.[GB],
			ISNULL(D.LastSample,0)LastSample,
	        C.[Freespace(GB)],'
IF EXISTS(SELECT TOP 1 1 FROM msdb..sysalerts S WHERE name=N''' + D.Description + ''')
	EXEC msdb.dbo.sp_delete_alert @name=N''' + D.Description + '''
EXEC msdb.dbo.sp_add_alert @name=N''' + D.Description + ''', 
	@message_id=0, 
	@severity=0, 
	@enabled=1, 
	@delay_between_responses=10800, 
	@include_event_description_in=1, 
	@category_name=N''[Uncategorized]'', 
	@wmi_namespace=N''\\.\root\CIMV2'', 
	@wmi_query=N''SELECT * FROM __instancemodificationevent within 3600 WHERE targetinstance isa ''''CIM_LogicalDisk'''' and targetinstance.freespace < ' + CONVERT(VARCHAR(50),b.[Byte]) + ' and targetinstance.name=''''' + C.DriveAlert + ''''''', 
	@job_id=N''00000000-0000-0000-0000-000000000000'';'[Script],
'
EXEC msdb.dbo.sp_add_notification @alert_name=N''' + D.Description + ''', @operator_name=N''' + @Operator +  ''', @notification_method = 1;
'[notification]
	INTO	#Action
	FROM    #Result
			CROSS APPLY (SELECT RTRIM(LTRIM(SUBSTRING(list, 1, CHARINDEX('|', list) - 1))) AS DriveName,RTRIM(LTRIM(SUBSTRING(list, 1, CHARINDEX('|', list) - 2))) AS DriveAlert,
			ROUND(CAST(RTRIM(LTRIM(SUBSTRING(list, CHARINDEX('|', list) + 1,
											 ( CHARINDEX('%', list) - 1 )
											 - CHARINDEX('|', list)))) AS FLOAT)
				  / 1024, 0) AS [Capacity(GB)],
				ROUND(CAST(RTRIM(LTRIM(SUBSTRING(list, CHARINDEX('%', list) + 1,
	                                         ( CHARINDEX('*', list) - 1 )
	                                         - CHARINDEX('%', list)))) AS FLOAT)
	              / 1024, 0) AS [Freespace(GB)])c
			INNER JOIN [_Admin_].[DriveAlert] d ON C.DriveName = D.DriveName
			CROSS APPLY (SELECT CONVERT(BIGINT,[Capacity(GB)]*(D.[Percent]/100.0)*(POWER(1024,3))) [Byte],
								CONVERT(INT,[Capacity(GB)]*(D.[Percent]/100.0)*(POWER(1024,2)))[MB],
								CONVERT(INT,[Capacity(GB)]*(D.[Percent]/100.0))[GB])b
	WHERE   list LIKE '[A-Z][:]%'
	ORDER BY drivename;

	SELECT	@cmd += A.Script,
			@cmdNotification += [notification]
	FROM	#Action A
	WHERE	A.MB != A.LastSample

	EXEC sp_executesql @cmd;
	EXEC sp_executesql @cmdNotification;

	UPDATE D
	SET		LastSample = A.MB
	FROM	[_Admin_].[DriveAlert] D
			INNER JOIN #Action A ON A.DriveName = D.DriveName
	WHERE	A.MB != D.LastSample;

END

CREATE PROCEDURE [dbo].[CommandExecute]

@Command nvarchar(max),
@CommandType nvarchar(max),
@Mode int,
@Comment nvarchar(max) = NULL,
@DatabaseName nvarchar(max) = NULL,
@SchemaName nvarchar(max) = NULL,
@ObjectName nvarchar(max) = NULL,
@ObjectType nvarchar(max) = NULL,
@IndexName nvarchar(max) = NULL,
@IndexType int = NULL,
@StatisticsName nvarchar(max) = NULL,
@PartitionNumber int = NULL,
@ExtendedInfo xml = NULL,
@LogToTable nvarchar(max),
@Execute nvarchar(max)

AS

CREATE PROCEDURE [dbo].[DatabaseBackup]

@Databases nvarchar(max) = NULL,
@Directory nvarchar(max) = NULL,
@BackupType nvarchar(max),
@Verify nvarchar(max) = 'N',
@CleanupTime int = NULL,
@CleanupMode nvarchar(max) = 'AFTER_BACKUP',
@Compress nvarchar(max) = NULL,
@CopyOnly nvarchar(max) = 'N',
@ChangeBackupType nvarchar(max) = 'N',
@BackupSoftware nvarchar(max) = NULL,
@CheckSum nvarchar(max) = 'N',
@BlockSize int = NULL,
@BufferCount int = NULL,
@MaxTransferSize int = NULL,
@NumberOfFiles int = NULL,
@CompressionLevel int = NULL,
@Description nvarchar(max) = NULL,
@Threads int = NULL,
@Throttle int = NULL,
@Encrypt nvarchar(max) = 'N',
@EncryptionAlgorithm nvarchar(max) = NULL,
@ServerCertificate nvarchar(max) = NULL,
@ServerAsymmetricKey nvarchar(max) = NULL,
@EncryptionKey nvarchar(max) = NULL,
@ReadWriteFileGroups nvarchar(max) = 'N',
@OverrideBackupPreference nvarchar(max) = 'N',
@NoRecovery nvarchar(max) = 'N',
@URL nvarchar(max) = NULL,
@Credential nvarchar(max) = NULL,
@MirrorDirectory nvarchar(max) = NULL,
@MirrorCleanupTime int = NULL,
@MirrorCleanupMode nvarchar(max) = 'AFTER_BACKUP',
@AvailabilityGroups nvarchar(max) = NULL,
@Updateability nvarchar(max) = 'ALL',
@LogToTable nvarchar(max) = 'N',
@Execute nvarchar(max) = 'Y'

AS

CREATE PROCEDURE [dbo].[DatabaseIntegrityCheck]

@Databases nvarchar(max) = NULL,
@CheckCommands nvarchar(max) = 'CHECKDB',
@PhysicalOnly nvarchar(max) = 'N',
@NoIndex nvarchar(max) = 'N',
@ExtendedLogicalChecks nvarchar(max) = 'N',
@TabLock nvarchar(max) = 'N',
@FileGroups nvarchar(max) = NULL,
@Objects nvarchar(max) = NULL,
@MaxDOP int = NULL,
@AvailabilityGroups nvarchar(max) = NULL,
@AvailabilityGroupReplicas nvarchar(max) = 'ALL',
@Updateability nvarchar(max) = 'ALL',
@LockTimeout int = NULL,
@LogToTable nvarchar(max) = 'N',
@Execute nvarchar(max) = 'Y'

AS

BEGIN SET NOCOUNT ON; --build up helper table for types used later to limit rows we will query DECLARE @typelist TABLE(rowid INT IDENTITY, TYPE VARCHAR(255), typename varchar(255) PRIMARY KEY(type,typename)); --feel free to add more in here if you need currently this only supports number, string and date INSERT INTO @typelist(type,typename) VALUES ('number','decimal'), ('number','numeric'), ('string','char'), ('number','smallint'), ('string','varchar'), ('date','datetime'), ('string','nvarchar'), ('string','text'), ('string','ntext'), ('number','int'), ('number','bigint'), ('date','smalldatetime'), ('number','float'), ('number','money'); --now remove the temp tbles if they already exists
IF OBJECT_ID('tempdb..#TempCols') IS NOT NULL DROP TABLE #TempCols;
IF OBJECT_ID('tempdb..#TempBruteForce') IS NOT NULL DROP TABLE #TempBruteForce;
--create the temp table needed for the search CREATE TABLE #TempCols(id INT IDENTITY PRIMARY KEY, tbl VARCHAR(255) NOT null, col VARCHAR(255) NOT null, TYPE varchar(255)); CREATE TABLE #TempBruteForce(id INT IDENTITY PRIMARY KEY, tbl VARCHAR(255) NOT null, tblqry Nvarchar(max), cnt INT DEFAULT (0),processed BIT DEFAULT(0),sqltxt Nvarchar(max),errortxt NVARCHAR(max));
--there shouldnt be a huge need to a index here so im skipping that
--now we have 2 seperate ways to deal with this, one for strings and one for the rest IF(@type = 'string') BEGIN INSERT INTO #TempCols(tbl,col,type) SELECT DISTINCT '[' + ss.name + '].[' + t.name + ']' AS tblname, c.name AS colname, st.name AS coltype FROM sys.tables t INNER JOIN sys.columns c ON c.object_id = t.object_id INNER JOIN sys.types st ON st.system_type_id = c.system_type_id AND st.name != 'sysname' INNER JOIN sys.schemas ss ON ss.schema_id = t.schema_id WHERE EXISTS (SELECT 1 FROM @typelist WHERE type = @type AND typename = st.name) AND (st.name IN ('ntext','text') OR c.max_length >= LEN(@searchvalue))
--now lets combine these to lessen the load on the server dependent on what was requested by grouping these by table --then combining all the columns into one where clause, this will reduce the number of searches to the number of tables with qualifying columns in them --changing it a little to work with all the differnt options available IF(@fuzzy = 1 AND @fuzzyoperator = 'beginswith') BEGIN --begins with search INSERT INTO #TempBruteForce(tbl,tblqry,cnt,processed,sqltxt,errortxt) SELECT t.tbl,'select count(1) from ' + tbl + ' where 1 = 1 and ('+REPLACE(x.csvvalue,'|',' or ')+')',0,0,NULL,null FROM #TempCols t OUTER APPLY ( SELECT STUFF(( SELECT '|' + ('[' + tt.col + '] LIKE '''+@searchvalue+'%''') FROM #TempCols tt WHERE tt.tbl = t.tbl FOR XML PATH(''), TYPE ).value('.[1]', 'nvarchar(max)' ), 1, 1, '') AS csvvalue ) x GROUP BY tbl,x.csvvalue END ELSE IF(@fuzzy = 1 AND @fuzzyoperator = 'endswith') BEGIN --ends with search INSERT INTO #TempBruteForce(tbl,tblqry,cnt,processed,sqltxt,errortxt) SELECT t.tbl,'select count(1) from ' + tbl + ' where 1 = 1 and ('+REPLACE(x.csvvalue,'|',' or ')+')',0,0,NULL,null FROM #TempCols t OUTER APPLY ( SELECT STUFF(( SELECT '|' + ('[' + tt.col + '] LIKE ''%'+@searchvalue+'''') FROM #TempCols tt WHERE tt.tbl = t.tbl FOR XML PATH(''), TYPE ).value('.[1]', 'nvarchar(max)' ), 1, 1, '') AS csvvalue ) x GROUP BY tbl,x.csvvalue END ELSE IF(@fuzzy = 0) BEGIN --string exact match, using like to work around text and ntext columns but with no wildcards INSERT INTO #TempBruteForce(tbl,tblqry,cnt,processed,sqltxt,errortxt) SELECT t.tbl,'select count(1) from ' + tbl + ' where 1 = 1 and ('+REPLACE(x.csvvalue,'|',' or ')+')',0,0,NULL,null FROM #TempCols t OUTER APPLY ( SELECT STUFF(( SELECT '|' + ('[' + tt.col + '] LIKE '''+@searchvalue+'''') FROM #TempCols tt WHERE tt.tbl = t.tbl FOR XML PATH(''), TYPE ).value('.[1]', 'nvarchar(max)' ), 1, 1, '') AS csvvalue ) x GROUP BY tbl,x.csvvalue END ELSE BEGIN --default to contains INSERT INTO #TempBruteForce(tbl,tblqry,cnt,processed,sqltxt,errortxt) SELECT t.tbl,'select count(1) from ' + tbl + ' where 1 = 1 and ('+REPLACE(x.csvvalue,'|',' or ')+')',0,0,NULL,null FROM #TempCols t OUTER APPLY ( SELECT STUFF(( SELECT '|' + ('charindex('''+@searchvalue+''',[' + tt.col + '], 1) > 0') FROM #TempCols tt WHERE tt.tbl = t.tbl FOR XML PATH(''), TYPE ).value('.[1]', 'nvarchar(max)' ), 1, 1, '') AS csvvalue ) x GROUP BY tbl,x.csvvalue end
END ELSE IF(@type = 'number') BEGIN --build up the columns for number search INSERT INTO #TempCols(tbl,col,type) SELECT DISTINCT '[' + ss.name + '].[' + t.name + ']' AS tblname, c.name AS colname, st.name AS coltype FROM sys.tables t INNER JOIN sys.columns c ON c.object_id = t.object_id INNER JOIN sys.types st ON st.system_type_id = c.system_type_id AND st.name != 'sysname' INNER JOIN sys.schemas ss ON ss.schema_id = t.schema_id WHERE EXISTS (SELECT 1 FROM @typelist WHERE type = @type AND typename = st.name)
--build up query texts INSERT INTO #TempBruteForce(tbl,tblqry,cnt,processed,sqltxt,errortxt) SELECT t.tbl,'select count(1) from ' + tbl + ' where 1 = 1 and ('+REPLACE(x.csvvalue,'|',' or ')+')',0,0,NULL,null FROM #TempCols t OUTER APPLY ( SELECT STUFF(( SELECT '|' + ('[' + tt.col + '] = '+@searchvalue+'') FROM #TempCols tt WHERE tt.tbl = t.tbl FOR XML PATH(''), TYPE ).value('.[1]', 'nvarchar(max)' ), 1, 1, '') AS csvvalue ) x GROUP BY tbl,x.csvvalue END ELSE IF(@type = 'date') BEGIN --build up TABLE AND col list OF datetime columns INSERT INTO #TempCols(tbl,col,type) SELECT DISTINCT '[' + ss.name + '].[' + t.name + ']' AS tblname, c.name AS colname, st.name AS coltype FROM sys.tables t INNER JOIN sys.columns c ON c.object_id = t.object_id INNER JOIN sys.types st ON st.system_type_id = c.system_type_id AND st.name != 'sysname' INNER JOIN sys.schemas ss ON ss.schema_id = t.schema_id WHERE EXISTS (SELECT 1 FROM @typelist WHERE type = @type AND typename = st.name) --in this case we cast datetimes as daes to do a simple comparison, mainly to avoid a 2 millisec difference causing a non match. --Its better to error on the side of more rows idenified then less. INSERT INTO #TempBruteForce(tbl,tblqry,cnt,processed,sqltxt,errortxt) SELECT t.tbl,'select count(1) from ' + tbl + ' where 1 = 1 and ('+REPLACE(x.csvvalue,'|',' or ')+')',0,0,NULL,null FROM #TempCols t OUTER APPLY ( SELECT STUFF(( SELECT '|' + ('cast([' + tt.col + '] as date) = cast('''+@searchvalue+''' as date)') FROM #TempCols tt WHERE tt.tbl = t.tbl FOR XML PATH(''), TYPE ).value('.[1]', 'nvarchar(max)' ), 1, 1, '') AS csvvalue ) x GROUP BY tbl,x.csvvalue END ELSE BEGIN --if the type cant be determined just error the process out RAISERROR ('Unknown type encountered! string, number, and date are the only accepted types', 0, 20) WITH NOWAIT; END
--declarations DECLARE @rowcnt INT; DECLARE @operator VARCHAR(200); DECLARE @where varchar(200); DECLARE @sqltxt NVARCHAR(max); DECLARE @SQL NVARCHAR(max); DECLARE @SQL_cnt NVARCHAR(max); DECLARE @ParmDefinition nVARCHAR(500); DECLARE @I INT; DECLARE @id INT; DECLARE @tbl varchar(255); DECLARE @col VARCHAR(255); DECLARE @fldtype VARCHAR(255); DECLARE @counter INT;
DECLARE dbnames CURSOR LOCAL FORWARD_ONLY FAST_FORWARD FOR SELECT id,tbl,tblqry FROM #TempBruteForce
OPEN dbnames FETCH NEXT FROM dbnames INTO @id,@tbl,@sqltxt
WHILE @@FETCH_STATUS = 0 BEGIN SELECT @SQL_cnt = 'SELECT @IOUT = ('+@sqltxt+');'; SELECT @SQL = @sqltxt; SET @ParmDefinition = N'@IOUT INT OUTPUT';
BEGIN TRY EXEC sp_executesql @SQL_cnt, @ParmDefinition, @IOUT=@I OUTPUT;
--get count results and update the temptable, mark as processed UPDATE #TempBruteForce SET cnt = @I, sqltxt = @sql, processed = 1 WHERE id = @id;
--if @i is greater than 0 than lets print the query to the messages, allows a preview of data found before it finishes, --it can take an hour on large databases with large tables and lots of rows. so sometimes i dont like to wait. IF(@I > 0) BEGIN RAISERROR (@SQL, 0, 1) WITH NOWAIT; END ELSE BEGIN --i like to at least print the table out for ones nothing was found in, mainly to tell where its at in the process DECLARE @temptxt VARCHAR(255); SET @temptxt = @tbl + ': [no data found]' RAISERROR (@temptxt, 0, 1) WITH NOWAIT; end
END TRY BEGIN CATCH --in this case i am interested in what errors so lets just record that one back in the temp table as an error PRINT('Error occurred in table: '+@tbl+''); UPDATE t SET processed = 1,errortxt = @sqltxt FROM #TempBruteForce t where t.id = @id; END CATCH
FETCH NEXT FROM dbnames INTO @id,@tbl,@sqltxt END
CLOSE dbnames DEALLOCATE dbnames
SELECT 'Search Results'; SELECT * FROM #TempBruteForce WHERE cnt > 0 ORDER BY cnt DESC; SELECT 'Errors Encountered'; SELECT * FROM #TempBruteForce WHERE LEN(errortxt) > 0;
SET NOCOUNT OFF;
END

-- =============================================
-- Author:		John Imel
-- Create date: 2016/05/02
-- Update date: 
-- Description:	http://www.sqlservercentral.com/scripts/bruteforce/140542/
