*/' ELSE IF @OutputType = 'SCHEMA' BEGIN SELECT FieldList = '[Priority] TINYINT, [FindingsGroup] VARCHAR(50), [Finding] VARCHAR(200), [DatabaseName] NVARCHAR(128), [URL] VARCHAR(200), [Details] NVARCHAR(4000), [QueryPlan] NVARCHAR(MAX), [QueryPlanFiltered] NVARCHAR(MAX), [CheckID] INT'
END ELSE /* IF @OutputType = 'SCHEMA' */ BEGIN
/* We start by creating #BlitzResults. It's a temp table that will store all of the results from our checks. Throughout the rest of this stored procedure, we're running a series of checks looking for dangerous things inside the SQL Server. When we find a problem, we insert rows into #BlitzResults. At the end, we return these results to the end user.
#BlitzResults has a CheckID field, but there's no Check table. As we do checks, we insert data into this table, and we manually put in the CheckID. For a list of checks, visit http://FirstResponderKit.org. */ DECLARE @StringToExecute NVARCHAR(4000) ,@curr_tracefilename NVARCHAR(500) ,@base_tracefilename NVARCHAR(500) ,@indx int ,@query_result_separator CHAR(1) ,@EmailSubject NVARCHAR(255) ,@EmailBody NVARCHAR(MAX) ,@EmailAttachmentFilename NVARCHAR(255) ,@ProductVersion NVARCHAR(128) ,@ProductVersionMajor DECIMAL(10,2) ,@ProductVersionMinor DECIMAL(10,2) ,@CurrentName NVARCHAR(128) ,@CurrentDefaultValue NVARCHAR(200) ,@CurrentCheckID INT ,@CurrentPriority INT ,@CurrentFinding VARCHAR(200) ,@CurrentURL VARCHAR(200) ,@CurrentDetails NVARCHAR(4000) ,@MSSinceStartup DECIMAL(38,0) ,@CPUMSsinceStartup DECIMAL(38,0);
IF OBJECT_ID('tempdb..#BlitzResults') IS NOT NULL DROP TABLE #BlitzResults; CREATE TABLE #BlitzResults ( ID INT IDENTITY(1, 1) , CheckID INT , DatabaseName NVARCHAR(128) , Priority TINYINT , FindingsGroup VARCHAR(50) , Finding VARCHAR(200) , URL VARCHAR(200) , Details NVARCHAR(4000) , QueryPlan [XML] NULL , QueryPlanFiltered [NVARCHAR](MAX) NULL );
/* You can build your own table with a list of checks to skip. For example, you might have some databases that you don't care about, or some checks you don't want to run. Then, when you run sp_Blitz, you can specify these parameters: @SkipChecksDatabase = 'DBAtools', @SkipChecksSchema = 'dbo', @SkipChecksTable = 'BlitzChecksToSkip' Pass in the database, schema, and table that contains the list of checks you want to skip. This part of the code checks those parameters, gets the list, and then saves those in a temp table. As we run each check, we'll see if we need to skip it.
Really anal-retentive users will note that the @SkipChecksServer parameter is not used. YET. We added that parameter in so that we could avoid changing the stored proc's surface area (interface) later. */ IF OBJECT_ID('tempdb..#SkipChecks') IS NOT NULL DROP TABLE #SkipChecks; CREATE TABLE #SkipChecks ( DatabaseName NVARCHAR(128) , CheckID INT , ServerName NVARCHAR(128) ); CREATE CLUSTERED INDEX IX_CheckID_DatabaseName ON #SkipChecks(CheckID, DatabaseName);
IF @SkipChecksTable IS NOT NULL AND @SkipChecksSchema IS NOT NULL AND @SkipChecksDatabase IS NOT NULL BEGIN SET @StringToExecute = 'INSERT INTO #SkipChecks(DatabaseName, CheckID, ServerName ) SELECT DISTINCT DatabaseName, CheckID, ServerName FROM ' + QUOTENAME(@SkipChecksDatabase) + '.' + QUOTENAME(@SkipChecksSchema) + '.' + QUOTENAME(@SkipChecksTable) + ' WHERE ServerName IS NULL OR ServerName = SERVERPROPERTY(''ServerName'');' EXEC(@StringToExecute) END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 106 ) AND (select convert(int,value_in_use) from sys.configurations where name = 'default trace enabled' ) = 1 BEGIN select @curr_tracefilename = [path] from sys.traces where is_default = 1 ; set @curr_tracefilename = reverse(@curr_tracefilename); select @indx = patindex('%\%', @curr_tracefilename) ; set @curr_tracefilename = reverse(@curr_tracefilename) ; set @base_tracefilename = left( @curr_tracefilename,len(@curr_tracefilename) - @indx) + '\log.trc' ; END
/* If the server has any databases on Antiques Roadshow, skip the checks that would break due to CTEs. */ IF @CheckUserDatabaseObjects = 1 AND EXISTS(SELECT * FROM sys.databases WHERE compatibility_level < 90) BEGIN SET @CheckUserDatabaseObjects = 0; PRINT 'Databases with compatibility level < 90 found, so setting @CheckUserDatabaseObjects = 0.'; PRINT 'The database-level checks rely on CTEs, which are not supported in SQL 2000 compat level databases.'; PRINT 'Get with the cool kids and switch to a current compatibility level, Grandpa. To find the problems, run:'; PRINT 'SELECT * FROM sys.databases WHERE compatibility_level < 90;'; END
/* If the server is Amazon RDS, skip checks that it doesn't allow */ IF LEFT(CAST(SERVERPROPERTY('ComputerNamePhysicalNetBIOS') AS VARCHAR(8000)), 8) = 'EC2AMAZ-' AND LEFT(CAST(SERVERPROPERTY('MachineName') AS VARCHAR(8000)), 8) = 'EC2AMAZ-' AND LEFT(CAST(SERVERPROPERTY('ServerName') AS VARCHAR(8000)), 8) = 'EC2AMAZ-' BEGIN INSERT INTO #SkipChecks (CheckID) VALUES (6); INSERT INTO #SkipChecks (CheckID) VALUES (29); INSERT INTO #SkipChecks (CheckID) VALUES (30); INSERT INTO #SkipChecks (CheckID) VALUES (31); INSERT INTO #SkipChecks (CheckID) VALUES (40); /* TempDB only has one data file */ INSERT INTO #SkipChecks (CheckID) VALUES (57); INSERT INTO #SkipChecks (CheckID) VALUES (59); INSERT INTO #SkipChecks (CheckID) VALUES (61); INSERT INTO #SkipChecks (CheckID) VALUES (62); INSERT INTO #SkipChecks (CheckID) VALUES (68); INSERT INTO #SkipChecks (CheckID) VALUES (69); INSERT INTO #SkipChecks (CheckID) VALUES (73); INSERT INTO #SkipChecks (CheckID) VALUES (79); INSERT INTO #SkipChecks (CheckID) VALUES (92); INSERT INTO #SkipChecks (CheckID) VALUES (94); INSERT INTO #SkipChecks (CheckID) VALUES (96); INSERT INTO #SkipChecks (CheckID) VALUES (98); INSERT INTO #SkipChecks (CheckID) VALUES (100); /* Remote DAC disabled */ INSERT INTO #SkipChecks (CheckID) VALUES (123); INSERT INTO #SkipChecks (CheckID) VALUES (177); END /* Amazon RDS skipped checks */
/* That's the end of the SkipChecks stuff. The next several tables are used by various checks later. */ IF OBJECT_ID('tempdb..#ConfigurationDefaults') IS NOT NULL DROP TABLE #ConfigurationDefaults; CREATE TABLE #ConfigurationDefaults ( name NVARCHAR(128) , DefaultValue BIGINT, CheckID INT );
IF OBJECT_ID ('tempdb..#Recompile') IS NOT NULL DROP TABLE #Recompile; CREATE TABLE #Recompile( DBName varchar(200), ProcName varchar(300), RecompileFlag varchar(1), SPSchema varchar(50) );
IF OBJECT_ID('tempdb..#DatabaseDefaults') IS NOT NULL DROP TABLE #DatabaseDefaults; CREATE TABLE #DatabaseDefaults ( name NVARCHAR(128) , DefaultValue NVARCHAR(200), CheckID INT, Priority INT, Finding VARCHAR(200), URL VARCHAR(200), Details NVARCHAR(4000) );
IF OBJECT_ID('tempdb..#DBCCs') IS NOT NULL DROP TABLE #DBCCs; CREATE TABLE #DBCCs ( ID INT IDENTITY(1, 1) PRIMARY KEY , ParentObject VARCHAR(255) , Object VARCHAR(255) , Field VARCHAR(255) , Value VARCHAR(255) , DbName NVARCHAR(128) NULL )
IF OBJECT_ID('tempdb..#LogInfo2012') IS NOT NULL DROP TABLE #LogInfo2012; CREATE TABLE #LogInfo2012 ( recoveryunitid INT , FileID SMALLINT , FileSize BIGINT , StartOffset BIGINT , FSeqNo BIGINT , [Status] TINYINT , Parity TINYINT , CreateLSN NUMERIC(38) );
IF OBJECT_ID('tempdb..#LogInfo') IS NOT NULL DROP TABLE #LogInfo; CREATE TABLE #LogInfo ( FileID SMALLINT , FileSize BIGINT , StartOffset BIGINT , FSeqNo BIGINT , [Status] TINYINT , Parity TINYINT , CreateLSN NUMERIC(38) );
IF OBJECT_ID('tempdb..#partdb') IS NOT NULL DROP TABLE #partdb; CREATE TABLE #partdb ( dbname NVARCHAR(128) , objectname NVARCHAR(200) , type_desc NVARCHAR(128) )
IF OBJECT_ID('tempdb..#TraceStatus') IS NOT NULL DROP TABLE #TraceStatus; CREATE TABLE #TraceStatus ( TraceFlag VARCHAR(10) , status BIT , Global BIT , Session BIT );
IF OBJECT_ID('tempdb..#driveInfo') IS NOT NULL DROP TABLE #driveInfo; CREATE TABLE #driveInfo ( drive NVARCHAR , SIZE DECIMAL(18, 2) )
IF OBJECT_ID('tempdb..#dm_exec_query_stats') IS NOT NULL DROP TABLE #dm_exec_query_stats; CREATE TABLE #dm_exec_query_stats ( [id] [int] NOT NULL IDENTITY(1, 1) , [sql_handle] [varbinary](64) NOT NULL , [statement_start_offset] [int] NOT NULL , [statement_end_offset] [int] NOT NULL , [plan_generation_num] [bigint] NOT NULL , [plan_handle] [varbinary](64) NOT NULL , [creation_time] [datetime] NOT NULL , [last_execution_time] [datetime] NOT NULL , [execution_count] [bigint] NOT NULL , [total_worker_time] [bigint] NOT NULL , [last_worker_time] [bigint] NOT NULL , [min_worker_time] [bigint] NOT NULL , [max_worker_time] [bigint] NOT NULL , [total_physical_reads] [bigint] NOT NULL , [last_physical_reads] [bigint] NOT NULL , [min_physical_reads] [bigint] NOT NULL , [max_physical_reads] [bigint] NOT NULL , [total_logical_writes] [bigint] NOT NULL , [last_logical_writes] [bigint] NOT NULL , [min_logical_writes] [bigint] NOT NULL , [max_logical_writes] [bigint] NOT NULL , [total_logical_reads] [bigint] NOT NULL , [last_logical_reads] [bigint] NOT NULL , [min_logical_reads] [bigint] NOT NULL , [max_logical_reads] [bigint] NOT NULL , [total_clr_time] [bigint] NOT NULL , [last_clr_time] [bigint] NOT NULL , [min_clr_time] [bigint] NOT NULL , [max_clr_time] [bigint] NOT NULL , [total_elapsed_time] [bigint] NOT NULL , [last_elapsed_time] [bigint] NOT NULL , [min_elapsed_time] [bigint] NOT NULL , [max_elapsed_time] [bigint] NOT NULL , [query_hash] [binary](8) NULL , [query_plan_hash] [binary](8) NULL , [query_plan] [xml] NULL , [query_plan_filtered] [nvarchar](MAX) NULL , [text] [nvarchar](MAX) COLLATE SQL_Latin1_General_CP1_CI_AS NULL , [text_filtered] [nvarchar](MAX) COLLATE SQL_Latin1_General_CP1_CI_AS NULL )
/* Used for the default trace checks. */ DECLARE @TracePath NVARCHAR(256); SELECT @TracePath=CAST(value as NVARCHAR(256)) FROM sys.fn_trace_getinfo(1) WHERE traceid=1 AND property=2;
SELECT @MSSinceStartup = DATEDIFF(MINUTE, create_date, CURRENT_TIMESTAMP) FROM sys.databases WHERE name='tempdb';
SET @MSSinceStartup = @MSSinceStartup * 60000;
SELECT @CPUMSsinceStartup = @MSSinceStartup * cpu_count FROM sys.dm_os_sys_info;
/* If we're outputting CSV, don't bother checking the plan cache because we cannot export plans. */ IF @OutputType = 'CSV' SET @CheckProcedureCache = 0;
/* Only run CheckUserDatabaseObjects if there are less than 50 databases. */ IF @BringThePain = 0 AND 50 <= (SELECT COUNT(*) FROM sys.databases) AND @CheckUserDatabaseObjects = 1 BEGIN SET @CheckUserDatabaseObjects = 0; PRINT 'Running sp_Blitz @CheckUserDatabaseObjects = 1 on a server with 50+ databases may cause temporary insanity for the server and/or user.'; PRINT 'If you''re sure you want to do this, run again with the parameter @BringThePain = 1.'; END
/* Sanitize our inputs */ SELECT @OutputDatabaseName = QUOTENAME(@OutputDatabaseName), @OutputSchemaName = QUOTENAME(@OutputSchemaName), @OutputTableName = QUOTENAME(@OutputTableName)
/* Get the major and minor build numbers */ SET @ProductVersion = CAST(SERVERPROPERTY('ProductVersion') AS NVARCHAR(128)); SELECT @ProductVersionMajor = SUBSTRING(@ProductVersion, 1,CHARINDEX('.', @ProductVersion) + 1 ), @ProductVersionMinor = PARSENAME(CONVERT(varchar(32), @ProductVersion), 2)
/* Whew! we're finally done with the setup, and we can start doing checks. First, let's make sure we're actually supposed to do checks on this server. The user could have passed in a SkipChecks table that specified to skip ALL checks on this server, so let's check for that: */ IF ( ( SERVERPROPERTY('ServerName') NOT IN ( SELECT ServerName FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID IS NULL ) ) OR ( @SkipChecksTable IS NULL ) ) BEGIN
/* Our very first check! We'll put more comments in this one just to explain exactly how it works. First, we check to see if we're supposed to skip CheckID 1 (that's the check we're working on.) */ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 1 ) BEGIN
/* Below, we check master.sys.databases looking for databases that haven't had a backup in the last week. If we find any, we insert them into #BlitzResults, the temp table that tracks our server's problems. Note that if the check does NOT find any problems, we don't save that. We're only saving the problems, not the successful checks. */ INSERT INTO #BlitzResults ( CheckID , DatabaseName , Priority , FindingsGroup , Finding , URL , Details ) SELECT 1 AS CheckID , d.[name] AS DatabaseName , 1 AS Priority , 'Backup' AS FindingsGroup , 'Backups Not Performed Recently' AS Finding , 'http://BrentOzar.com/go/nobak' AS URL , 'Database ' + d.name + ' last backed up: ' + COALESCE(CAST(MAX(b.backup_finish_date) AS VARCHAR(25)),'never') AS Details FROM master.sys.databases d LEFT OUTER JOIN msdb.dbo.backupset b ON d.name COLLATE SQL_Latin1_General_CP1_CI_AS = b.database_name COLLATE SQL_Latin1_General_CP1_CI_AS AND b.type = 'D' AND b.server_name = SERVERPROPERTY('ServerName') /*Backupset ran on current server */ WHERE d.database_id <> 2 /* Bonus points if you know what that means */ AND d.state NOT IN(1, 6, 10) /* Not currently offline or restoring, like log shipping databases */ AND d.is_in_standby = 0 /* Not a log shipping target database */ AND d.source_database_id IS NULL /* Excludes database snapshots */ AND d.name NOT IN ( SELECT DISTINCT DatabaseName FROM #SkipChecks WHERE CheckID IS NULL ) /* The above NOT IN filters out the databases we're not supposed to check. */ GROUP BY d.name HAVING MAX(b.backup_finish_date) <= DATEADD(dd, -7, GETDATE()) OR MAX(b.backup_finish_date) IS NULL; /* And there you have it. The rest of this stored procedure works the same way: it asks: - Should I skip this check? - If not, do I find problems? - Insert the results into #BlitzResults */
END
/* And that's the end of CheckID #1.
CheckID #2 is a little simpler because it only involves one query, and it's more typical for queries that people contribute. But keep reading, because the next check gets more complex again. */
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 2 ) BEGIN INSERT INTO #BlitzResults ( CheckID , DatabaseName , Priority , FindingsGroup , Finding , URL , Details ) SELECT DISTINCT 2 AS CheckID , d.name AS DatabaseName , 1 AS Priority , 'Backup' AS FindingsGroup , 'Full Recovery Mode w/o Log Backups' AS Finding , 'http://BrentOzar.com/go/biglogs' AS URL , ( 'The ' + CAST(CAST((SELECT ((SUM([mf].[size]) * 8.) / 1024.) FROM sys.[master_files] AS [mf] WHERE [mf].[database_id] = d.[database_id] AND [mf].[type_desc] = 'LOG') AS DECIMAL(18,2)) AS VARCHAR) + 'MB log file has not been backed up in the last week.' ) AS Details FROM master.sys.databases d WHERE d.recovery_model IN ( 1, 2 ) AND d.database_id NOT IN ( 2, 3 ) AND d.source_database_id IS NULL AND d.state <> 1 /* Not currently restoring, like log shipping databases */ AND d.is_in_standby = 0 /* Not a log shipping target database */ AND d.source_database_id IS NULL /* Excludes database snapshots */ AND d.name NOT IN ( SELECT DISTINCT DatabaseName FROM #SkipChecks WHERE CheckID IS NULL ) AND NOT EXISTS ( SELECT * FROM msdb.dbo.backupset b WHERE d.name COLLATE SQL_Latin1_General_CP1_CI_AS = b.database_name COLLATE SQL_Latin1_General_CP1_CI_AS AND b.type = 'L' AND b.backup_finish_date >= DATEADD(dd, -7, GETDATE()) ); END
/* Next up, we've got CheckID 8. (These don't have to go in order.) This one won't work on SQL Server 2005 because it relies on a new DMV that didn't exist prior to SQL Server 2008. This means we have to check the SQL Server version first, then build a dynamic string with the query we want to run: */
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 8 ) BEGIN IF @@VERSION NOT LIKE '%Microsoft SQL Server 2000%' AND @@VERSION NOT LIKE '%Microsoft SQL Server 2005%' BEGIN SET @StringToExecute = 'INSERT INTO #BlitzResults (CheckID, Priority, FindingsGroup, Finding, URL, Details) SELECT 8 AS CheckID, 230 AS Priority, ''Security'' AS FindingsGroup, ''Server Audits Running'' AS Finding, ''http://BrentOzar.com/go/audits'' AS URL, (''SQL Server built-in audit functionality is being used by server audit: '' + [name]) AS Details FROM sys.dm_server_audit_status' EXECUTE(@StringToExecute) END; END
/* But what if you need to run a query in every individual database? Hop down to the @CheckUserDatabaseObjects section.
And that's the basic idea! You can read through the rest of the checks if you like - some more exciting stuff happens closer to the end of the stored proc, where we start doing things like checking the plan cache, but those aren't as cleanly commented.
If you'd like to contribute your own check, use one of the check formats shown above and email it to Help@BrentOzar.com. You don't have to pick a CheckID or a link - we'll take care of that when we test and publish the code. Thanks! */
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 93 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 93 AS CheckID , 1 AS Priority , 'Backup' AS FindingsGroup , 'Backing Up to Same Drive Where Databases Reside' AS Finding , 'http://BrentOzar.com/go/backup' AS URL , CAST(COUNT(1) AS VARCHAR(50)) + ' backups done on drive ' + UPPER(LEFT(bmf.physical_device_name, 3)) + ' in the last two weeks, where database files also live. This represents a serious risk if that array fails.' Details FROM msdb.dbo.backupmediafamily AS bmf INNER JOIN msdb.dbo.backupset AS bs ON bmf.media_set_id = bs.media_set_id AND bs.backup_start_date >= ( DATEADD(dd, -14, GETDATE()) ) WHERE UPPER(LEFT(bmf.physical_device_name COLLATE SQL_Latin1_General_CP1_CI_AS, 3)) IN ( SELECT DISTINCT UPPER(LEFT(mf.physical_name COLLATE SQL_Latin1_General_CP1_CI_AS, 3)) FROM sys.master_files AS mf ) GROUP BY UPPER(LEFT(bmf.physical_device_name, 3)) END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 119 ) AND EXISTS ( SELECT * FROM sys.all_objects o WHERE o.name = 'dm_database_encryption_keys' ) BEGIN SET @StringToExecute = 'INSERT INTO #BlitzResults (CheckID, Priority, FindingsGroup, Finding, DatabaseName, URL, Details) SELECT 119 AS CheckID, 1 AS Priority, ''Backup'' AS FindingsGroup, ''TDE Certificate Not Backed Up Recently'' AS Finding, db_name(dek.database_id) AS DatabaseName, ''http://BrentOzar.com/go/tde'' AS URL, ''The certificate '' + c.name + '' is used to encrypt database '' + db_name(dek.database_id) + ''. Last backup date: '' + COALESCE(CAST(c.pvt_key_last_backup_date AS VARCHAR(100)), ''Never'') AS Details FROM sys.certificates c INNER JOIN sys.dm_database_encryption_keys dek ON c.thumbprint = dek.encryptor_thumbprint WHERE pvt_key_last_backup_date IS NULL OR pvt_key_last_backup_date <= DATEADD(dd, -30, GETDATE())'; EXECUTE(@StringToExecute); END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 3 ) BEGIN INSERT INTO #BlitzResults ( CheckID , DatabaseName , Priority , FindingsGroup , Finding , URL , Details ) SELECT TOP 1 3 AS CheckID , 'msdb' , 200 AS Priority , 'Backup' AS FindingsGroup , 'MSDB Backup History Not Purged' AS Finding , 'http://BrentOzar.com/go/history' AS URL , ( 'Database backup history retained back to ' + CAST(bs.backup_start_date AS VARCHAR(20)) ) AS Details FROM msdb.dbo.backupset bs WHERE bs.backup_start_date <= DATEADD(dd, -60, GETDATE()) ORDER BY backup_set_id ASC; END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 178 ) AND EXISTS (SELECT * FROM msdb.dbo.backupset bs WHERE bs.type = 'D' AND bs.compressed_backup_size >= 50000000000 /* At least 50GB */ AND DATEDIFF(SECOND, bs.backup_start_date, bs.backup_finish_date) <= 60 /* Backup took less than 60 seconds */ AND bs.backup_finish_date >= DATEADD(DAY, -14, GETDATE()) /* In the last 2 weeks */) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 178 AS CheckID , 200 AS Priority , 'Performance' AS FindingsGroup , 'Snapshot Backups Occurring' AS Finding , 'http://BrentOzar.com/go/snaps' AS URL , ( CAST(COUNT(*) AS VARCHAR(20)) + ' snapshot-looking backups have occurred in the last two weeks, indicating that IO may be freezing up.') AS Details FROM msdb.dbo.backupset bs WHERE bs.type = 'D' AND bs.compressed_backup_size >= 50000000000 /* At least 50GB */ AND DATEDIFF(SECOND, bs.backup_start_date, bs.backup_finish_date) <= 60 /* Backup took less than 60 seconds */ AND bs.backup_finish_date >= DATEADD(DAY, -14, GETDATE()) /* In the last 2 weeks */ END
