IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 104 ) BEGIN INSERT INTO #BlitzResults ( [CheckID] , [Priority] , [FindingsGroup] , [Finding] , [URL] , [Details] ) SELECT 104 AS [CheckID] , 230 AS [Priority] , 'Security' AS [FindingsGroup] , 'Login Can Control Server' AS [Finding] , 'http://BrentOzar.com/go/sa' AS [URL] , 'Login [' + pri.[name] + '] has the CONTROL SERVER permission - meaning they can do absolutely anything in SQL Server, including dropping databases or hiding their tracks.' AS [Details] FROM sys.server_principals AS pri WHERE pri.[principal_id] IN ( SELECT p.[grantee_principal_id] FROM sys.server_permissions AS p WHERE p.[state] IN ( 'G', 'W' ) AND p.[class] = 100 AND p.[type] = 'CL' ) AND pri.[name] NOT LIKE '##%##' END
