IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 22 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT cd.CheckID , 200 AS Priority , 'Non-Default Server Config' AS FindingsGroup , cr.name AS Finding , 'http://BrentOzar.com/go/conf' AS URL , ( 'This sp_configure option has been changed. Its default value is ' + COALESCE(CAST(cd.[DefaultValue] AS VARCHAR(100)), '(unknown)') + ' and it has been set to ' + CAST(cr.value_in_use AS VARCHAR(100)) + '.' ) AS Details FROM sys.configurations cr INNER JOIN #ConfigurationDefaults cd ON cd.name = cr.name LEFT OUTER JOIN #ConfigurationDefaults cdUsed ON cdUsed.name = cr.name AND cdUsed.DefaultValue = cr.value_in_use WHERE cdUsed.name IS NULL; END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 24 ) BEGIN INSERT INTO #BlitzResults ( CheckID , DatabaseName , Priority , FindingsGroup , Finding , URL , Details ) SELECT DISTINCT 24 AS CheckID , DB_NAME(database_id) AS DatabaseName , 170 AS Priority , 'File Configuration' AS FindingsGroup , 'System Database on C Drive' AS Finding , 'http://BrentOzar.com/go/cdrive' AS URL , ( 'The ' + DB_NAME(database_id) + ' database has a file on the C drive. Putting system databases on the C drive runs the risk of crashing the server when it runs out of space.' ) AS Details FROM sys.master_files WHERE UPPER(LEFT(physical_name, 1)) = 'C' AND DB_NAME(database_id) IN ( 'master', 'model', 'msdb' ); END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 25 ) BEGIN INSERT INTO #BlitzResults ( CheckID , DatabaseName , Priority , FindingsGroup , Finding , URL , Details ) SELECT TOP 1 25 AS CheckID , 'tempdb' , 170 AS Priority , 'File Configuration' AS FindingsGroup , 'TempDB on C Drive' AS Finding , 'http://BrentOzar.com/go/cdrive' AS URL , CASE WHEN growth > 0 THEN ( 'The tempdb database has files on the C drive. TempDB frequently grows unpredictably, putting your server at risk of running out of C drive space and crashing hard. C is also often much slower than other drives, so performance may be suffering.' ) ELSE ( 'The tempdb database has files on the C drive. TempDB is not set to Autogrow, hopefully it is big enough. C is also often much slower than other drives, so performance may be suffering.' ) END AS Details FROM sys.master_files WHERE UPPER(LEFT(physical_name, 1)) = 'C' AND DB_NAME(database_id) = 'tempdb'; END
