IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 34 ) BEGIN IF EXISTS ( SELECT * FROM sys.all_objects WHERE name = 'dm_db_mirroring_auto_page_repair' ) BEGIN SET @StringToExecute = 'INSERT INTO #BlitzResults (CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details) SELECT DISTINCT 34 AS CheckID , db.name , 1 AS Priority , ''Corruption'' AS FindingsGroup , ''Database Corruption Detected'' AS Finding , ''http://BrentOzar.com/go/repair'' AS URL , ( ''Database mirroring has automatically repaired at least one corrupt page in the last 30 days. For more information, query the DMV sys.dm_db_mirroring_auto_page_repair.'' ) AS Details FROM (SELECT rp2.database_id, rp2.modification_time FROM sys.dm_db_mirroring_auto_page_repair rp2 WHERE rp2.[database_id] not in ( SELECT db2.[database_id] FROM sys.databases as db2 WHERE db2.[state] = 1 ) ) as rp INNER JOIN master.sys.databases db ON rp.database_id = db.database_id WHERE rp.modification_time >= DATEADD(dd, -30, GETDATE()) ;' EXECUTE(@StringToExecute) END; END
