IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 36 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT DISTINCT 36 AS CheckID , 150 AS Priority , 'Performance' AS FindingsGroup , 'Slow Storage Reads on Drive ' + UPPER(LEFT(mf.physical_name, 1)) AS Finding , 'http://BrentOzar.com/go/slow' AS URL , 'Reads are averaging longer than 200ms for at least one database on this drive. For specific database file speeds, run the query from the information link.' AS Details FROM sys.dm_io_virtual_file_stats(NULL, NULL) AS fs INNER JOIN sys.master_files AS mf ON fs.database_id = mf.database_id AND fs.[file_id] = mf.[file_id] WHERE ( io_stall_read_ms / ( 1.0 + num_of_reads ) ) > 200 AND num_of_reads > 100000; END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 37 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT DISTINCT 37 AS CheckID , 150 AS Priority , 'Performance' AS FindingsGroup , 'Slow Storage Writes on Drive ' + UPPER(LEFT(mf.physical_name, 1)) AS Finding , 'http://BrentOzar.com/go/slow' AS URL , 'Writes are averaging longer than 100ms for at least one database on this drive. For specific database file speeds, run the query from the information link.' AS Details FROM sys.dm_io_virtual_file_stats(NULL, NULL) AS fs INNER JOIN sys.master_files AS mf ON fs.database_id = mf.database_id AND fs.[file_id] = mf.[file_id] WHERE ( io_stall_write_ms / ( 1.0 + num_of_writes ) ) > 100 AND num_of_writes > 100000; END
