IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 57 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 57 AS CheckID , 230 AS Priority , 'Security' AS FindingsGroup , 'SQL Agent Job Runs at Startup' AS Finding , 'http://BrentOzar.com/go/startup' AS URL , ( 'Job [' + j.name + '] runs automatically when SQL Server Agent starts up. Make sure you know exactly what this job is doing, because it could pose a security risk.' ) AS Details FROM msdb.dbo.sysschedules sched JOIN msdb.dbo.sysjobschedules jsched ON sched.schedule_id = jsched.schedule_id JOIN msdb.dbo.sysjobs j ON jsched.job_id = j.job_id WHERE sched.freq_type = 64 AND sched.enabled = 1; END
