IF @ProductVersionMajor >= 11 AND NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 162 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 162 AS CheckID , 50 AS Priority , 'Performance' AS FindingGroup , 'Poison Wait Detected: CMEMTHREAD & NUMA' AS Finding , 'http://BrentOzar.com/go/poison' AS URL , CAST(SUM([wait_time_ms]) / 10000 / 1000 / 60 / 60 / 24 AS VARCHAR) + CAST(CONVERT(TIME, DATEADD(ms, SUM([wait_time_ms] / 10000 % 1000), DATEADD(ss, SUM([wait_time_ms] / 10000000), 0))) AS VARCHAR) + ' of this wait have been recorded. In servers with over 8 cores per NUMA node, when CMEMTHREAD waits are a bottleneck, trace flag 8048 may be needed.' FROM sys.dm_os_nodes n INNER JOIN sys.[dm_os_wait_stats] w ON w.wait_type = 'CMEMTHREAD' WHERE n.node_id = 0 AND n.online_scheduler_count >= 8 GROUP BY w.wait_type HAVING SUM([wait_time_ms]) > (SELECT 5000 * datediff(HH,create_date,CURRENT_TIMESTAMP) AS hours_since_startup FROM sys.databases WHERE name='tempdb') AND SUM([wait_time_ms]) > 60000; END
