/*Checks to see if Buffer Pool Extensions are in use*/ IF @ProductVersionMajor >= 12 AND NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 174 ) BEGIN INSERT INTO [#BlitzResults] ( [CheckID] , [Priority] , [FindingsGroup] , [Finding] , [URL] , [Details] )
