/* Reliability - Dangerous Third Party Modules - 179 */ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 179 ) BEGIN INSERT INTO [#BlitzResults] ( [CheckID] , [Priority] , [FindingsGroup] , [Finding] , [URL] , [Details] )
SELECT 179 AS [CheckID] , 5 AS [Priority] , 'Reliability' AS [FindingsGroup] , 'Dangerous Third Party Modules' AS [Finding] , 'https://support.microsoft.com/en-us/kb/2033238' AS [URL] , ( COALESCE(company, '') + ' - ' + COALESCE(description, '') + ' - ' + COALESCE(name, '') + ' - suspected dangerous third party module is installed.') AS [Details] FROM sys.dm_os_loaded_modules WHERE UPPER(name) LIKE UPPER('%\ENTAPI.DLL') /* McAfee VirusScan Enterprise */ OR UPPER(name) LIKE UPPER('%\HIPI.DLL') OR UPPER(name) LIKE UPPER('%\HcSQL.dll') OR UPPER(name) LIKE UPPER('%\HcApi.dll') OR UPPER(name) LIKE UPPER('%\HcThe.dll') /* McAfee Host Intrusion */ OR UPPER(name) LIKE UPPER('%\SOPHOS_DETOURED.DLL') OR UPPER(name) LIKE UPPER('%\SOPHOS_DETOURED_x64.DLL') OR UPPER(name) LIKE UPPER('%\SWI_IFSLSP_64.dll') /* Sophos AV */ OR UPPER(name) LIKE UPPER('%\PIOLEDB.DLL') OR UPPER(name) LIKE UPPER('%\PISDK.DLL') /* OSISoft PI data access */
END
/*Find shrink database tasks*/
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 180 ) BEGIN ; WITH XMLNAMESPACES ('www.microsoft.com/SqlServer/Dts' AS [dts]) ,[maintenance_plan_steps] AS ( SELECT [name] , CAST(CAST([packagedata] AS VARBINARY(MAX)) AS XML) AS [maintenance_plan_xml] FROM [msdb].[dbo].[sysssispackages] WHERE [packagetype] = 6 ) INSERT INTO [#BlitzResults] ( [CheckID] , [Priority] , [FindingsGroup] , [Finding] , [URL] , [Details] ) SELECT 180 AS [CheckID] , 100 AS [Priority] , 'Performance' AS [FindingsGroup] , 'Shrink Database Step In Maintenance Plan' AS [Finding] , 'http://BrentOzar.com/go/autoshrink' AS [URL] , 'The maintenance plan ' + [mps].[name] + ' has a step to shrink databases in it. Shrinking databases is as outdated as maintenance plans.' AS [Details] FROM [maintenance_plan_steps] [mps] CROSS APPLY [maintenance_plan_xml].[nodes]('//dts:Executables/dts:Executable') [t]([c]) WHERE [c].[value]('(@dts:ObjectName)', 'VARCHAR(128)') = 'Shrink Database Task'
END
/*Find repetitive maintenance tasks*/ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 181 ) BEGIN ; WITH XMLNAMESPACES ('www.microsoft.com/SqlServer/Dts' AS [dts]) ,[maintenance_plan_steps] AS ( SELECT [name] , CAST(CAST([packagedata] AS VARBINARY(MAX)) AS XML) AS [maintenance_plan_xml] FROM [msdb].[dbo].[sysssispackages] WHERE [packagetype] = 6 ), [maintenance_plan_table] AS ( SELECT [mps].[name] ,[c].[value]('(@dts:ObjectName)', 'NVARCHAR(128)') AS [step_name] FROM [maintenance_plan_steps] [mps] CROSS APPLY [maintenance_plan_xml].[nodes]('//dts:Executables/dts:Executable') [t]([c]) ), [mp_steps_pretty] AS (SELECT DISTINCT [m1].[name] , STUFF((SELECT N', ' + [m2].[step_name] FROM [maintenance_plan_table] AS [m2] WHERE [m1].[name] = [m2].[name] FOR XML PATH(N'')), 1, 2, N'') AS [maintenance_plan_steps] FROM [maintenance_plan_table] AS [m1])
INSERT INTO [#BlitzResults] ( [CheckID] , [Priority] , [FindingsGroup] , [Finding] , [URL] , [Details] )
SELECT 181 AS [CheckID] , 100 AS [Priority] , 'Performance' AS [FindingsGroup] , 'Repetitive Steps In Maintenance Plans' AS [Finding] , 'https://ola.hallengren.com/' AS [URL] , 'The maintenance plan ' + [m].[name] + ' is doing repetitive work on indexes and statistics. Perhaps it''s time to try something more modern?' AS [Details] FROM [mp_steps_pretty] m WHERE m.[maintenance_plan_steps] LIKE '%Rebuild%Reorganize%' OR m.[maintenance_plan_steps] LIKE '%Rebuild%Update%'
END
IF @CheckUserDatabaseObjects = 1 BEGIN
/* But what if you need to run a query in every individual database? Check out CheckID 99 below. Yes, it uses sp_MSforeachdb, and no, we're not happy about that. sp_MSforeachdb is known to have a lot of issues, like skipping databases sometimes. However, this is the only built-in option that we have. If you're writing your own code for database maintenance, consider Aaron Bertrand's alternative: http://www.mssqltips.com/sqlservertip/2201/making-a-more-reliable-and-flexible-spmsforeachdb/ We don't include that as part of sp_Blitz, of course, because copying and distributing copyrighted code from others without their written permission isn't a good idea. */ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 99 ) BEGIN EXEC dbo.sp_MSforeachdb 'USE [?]; IF EXISTS (SELECT * FROM sys.tables WITH (NOLOCK) WHERE name = ''sysmergepublications'' ) IF EXISTS ( SELECT * FROM sysmergepublications WITH (NOLOCK) WHERE retention = 0) INSERT INTO #BlitzResults (CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details) SELECT DISTINCT 99, DB_NAME(), 110, ''Performance'', ''Infinite merge replication metadata retention period'', ''http://BrentOzar.com/go/merge'', (''The ['' + DB_NAME() + ''] database has merge replication metadata retention period set to infinite - this can be the case of significant performance issues.'')'; END /* Note that by using sp_MSforeachdb, we're running the query in all databases. We're not checking #SkipChecks here for each database to see if we should run the check in this database. That means we may still run a skipped check if it involves sp_MSforeachdb. We just don't output those results in the last step. */
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 163 ) AND EXISTS(SELECT * FROM sys.all_objects WHERE name = 'database_query_store_options') BEGIN EXEC dbo.sp_MSforeachdb 'USE [?]; INSERT INTO #BlitzResults (CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details) SELECT TOP 1 163, ''?'', 10, ''Performance'', ''Query Store Disabled'', ''http://BrentOzar.com/go/querystore'', (''The new SQL Server 2016 Query Store feature has not been enabled on this database.'') FROM [?].sys.database_query_store_options WHERE desired_state = 0 AND ''?'' NOT IN (''master'', ''model'', ''msdb'', ''tempdb'', ''DWConfiguration'', ''DWDiagnostics'', ''DWQueue'', ''ReportServer'', ''ReportServerTempDB'')'; END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 41 ) BEGIN EXEC dbo.sp_MSforeachdb 'use [?]; INSERT INTO #BlitzResults (CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details) SELECT 41, ''?'', 170, ''File Configuration'', ''Multiple Log Files on One Drive'', ''http://BrentOzar.com/go/manylogs'', (''The ['' + DB_NAME() + ''] database has multiple log files on the '' + LEFT(physical_name, 1) + '' drive. This is not a performance booster because log file access is sequential, not parallel.'') FROM [?].sys.database_files WHERE type_desc = ''LOG'' AND ''?'' <> ''[tempdb]'' GROUP BY LEFT(physical_name, 1) HAVING COUNT(*) > 1'; END
