/* Populate the query_plan_filtered field. Only works in 2005SP2+, but we're just doing it in 2008 to be safe. */ UPDATE #dm_exec_query_stats SET query_plan_filtered = qp.query_plan FROM #dm_exec_query_stats qs CROSS APPLY sys.dm_exec_text_query_plan(qs.plan_handle, qs.statement_start_offset, qs.statement_end_offset) AS qp
END;
/* Populate the additional query_plan, text, and text_filtered fields */ UPDATE #dm_exec_query_stats SET query_plan = qp.query_plan , [text] = st.[text] , text_filtered = SUBSTRING(st.text, ( qs.statement_start_offset / 2 ) + 1, ( ( CASE qs.statement_end_offset WHEN -1 THEN DATALENGTH(st.text) ELSE qs.statement_end_offset END - qs.statement_start_offset ) / 2 ) + 1) FROM #dm_exec_query_stats qs CROSS APPLY sys.dm_exec_sql_text(qs.sql_handle) AS st CROSS APPLY sys.dm_exec_query_plan(qs.plan_handle) AS qp
/* Dump instances of our own script. We're not trying to tune ourselves. */ DELETE #dm_exec_query_stats WHERE text LIKE '%sp_Blitz%' OR text LIKE '%#BlitzResults%'
