/*Identify globally enabled trace flags*/ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 74 ) BEGIN INSERT INTO #TraceStatus EXEC ( ' DBCC TRACESTATUS(-1) WITH NO_INFOMSGS' ) INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 74 AS CheckID , 200 AS Priority , 'Informational' AS FindingsGroup , 'TraceFlag On' AS Finding , 'http://www.BrentOzar.com/go/traceflags/' AS URL , 'Trace flag ' + CASE WHEN [T].[TraceFlag] = '2330' THEN ' 2330 enabled globally. Using this trace Flag disables missing index requests' WHEN [T].[TraceFlag] = '1211' THEN ' 1211 enabled globally. Using this Trace Flag disables lock escalation when you least expect it. No Bueno!' WHEN [T].[TraceFlag] = '1224' THEN ' 1224 enabled globally. Using this Trace Flag disables lock escalation based on the number of locks being taken. You shouldn''t have done that, Dave.' WHEN [T].[TraceFlag] = '652' THEN ' 652 enabled globally. Using this Trace Flag disables pre-fetching during index scans. If you hate slow queries, you should turn that off.' WHEN [T].[TraceFlag] = '661' THEN ' 661 enabled globally. Using this Trace Flag disables ghost record removal. Who you gonna call? No one, turn that thing off.' WHEN [T].[TraceFlag] = '1806' THEN ' 1806 enabled globally. Using this Trace Flag disables instant file initialization. I question your sanity.' WHEN [T].[TraceFlag] = '3505' THEN ' 3505 enabled globally. Using this Trace Flag disables Checkpoints. Probably not the wisest idea.' WHEN [T].[TraceFlag] = '8649' THEN ' 8649 enabled globally. Using this Trace Flag drops cost thresholf for parallelism down to 0. I hope this is a dev server.' ELSE [T].[TraceFlag] + ' is enabled globally.' END AS Details FROM #TraceStatus T END
/*Check for transaction log file larger than data file */ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 75 ) BEGIN INSERT INTO #BlitzResults ( CheckID , DatabaseName , Priority , FindingsGroup , Finding , URL , Details ) SELECT 75 AS CheckID , DB_NAME(a.database_id) , 50 AS Priority , 'Reliability' AS FindingsGroup , 'Transaction Log Larger than Data File' AS Finding , 'http://BrentOzar.com/go/biglog' AS URL , 'The database [' + DB_NAME(a.database_id) + '] has a ' + CAST((CAST(a.size AS BIGINT) * 8 / 1000000) AS NVARCHAR(20)) + ' GB transaction log file, larger than the total data file sizes. This may indicate that transaction log backups are not being performed or not performed often enough.' AS Details FROM sys.master_files a WHERE a.type = 1 AND DB_NAME(a.database_id) NOT IN ( SELECT DISTINCT DatabaseName FROM #SkipChecks ) AND a.size > 125000 /* Size is measured in pages here, so this gets us log files over 1GB. */ AND a.size > ( SELECT SUM(CAST(b.size AS BIGINT)) FROM sys.master_files b WHERE a.database_id = b.database_id AND b.type = 0 ) AND a.database_id IN ( SELECT database_id FROM sys.databases WHERE source_database_id IS NULL ) END
/*Check for collation conflicts between user databases and tempdb */ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 76 ) BEGIN INSERT INTO #BlitzResults ( CheckID , DatabaseName , Priority , FindingsGroup , Finding , URL , Details ) SELECT 76 AS CheckID , name AS DatabaseName , 200 AS Priority , 'Informational' AS FindingsGroup , 'Collation is ' + collation_name AS Finding , 'http://BrentOzar.com/go/collate' AS URL , 'Collation differences between user databases and tempdb can cause conflicts especially when comparing string values' AS Details FROM sys.databases WHERE name NOT IN ( 'master', 'model', 'msdb') AND name NOT LIKE 'ReportServer%' AND name NOT IN ( SELECT DISTINCT DatabaseName FROM #SkipChecks WHERE CheckID IS NULL) AND collation_name <> ( SELECT collation_name FROM sys.databases WHERE name = 'tempdb' ) END
