/* Check 84 - SQL Server 2012 */ IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 84 ) BEGIN IF EXISTS ( SELECT * FROM sys.all_objects o INNER JOIN sys.all_columns c ON o.object_id = c.object_id WHERE o.name = 'dm_os_sys_info' AND c.name = 'physical_memory_kb' ) BEGIN SET @StringToExecute = 'INSERT INTO #BlitzResults (CheckID, Priority, FindingsGroup, Finding, URL, Details) SELECT 84 AS CheckID , 250 AS Priority , ''Server Info'' AS FindingsGroup , ''Hardware'' AS Finding , '''' AS URL , ''Logical processors: '' + CAST(cpu_count AS VARCHAR(50)) + ''. Physical memory: '' + CAST( CAST(ROUND((physical_memory_kb / 1024.0 / 1024), 1) AS INT) AS VARCHAR(50)) + ''GB.'' FROM sys.dm_os_sys_info'; EXECUTE(@StringToExecute); END
/* Check 84 - SQL Server 2008 */ IF EXISTS ( SELECT * FROM sys.all_objects o INNER JOIN sys.all_columns c ON o.object_id = c.object_id WHERE o.name = 'dm_os_sys_info' AND c.name = 'physical_memory_in_bytes' ) BEGIN SET @StringToExecute = 'INSERT INTO #BlitzResults (CheckID, Priority, FindingsGroup, Finding, URL, Details) SELECT 84 AS CheckID , 250 AS Priority , ''Server Info'' AS FindingsGroup , ''Hardware'' AS Finding , '''' AS URL , ''Logical processors: '' + CAST(cpu_count AS VARCHAR(50)) + ''. Physical memory: '' + CAST( CAST(ROUND((physical_memory_in_bytes / 1024.0 / 1024 / 1024), 1) AS INT) AS VARCHAR(50)) + ''GB.'' FROM sys.dm_os_sys_info'; EXECUTE(@StringToExecute); END END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 85 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 85 AS CheckID , 250 AS Priority , 'Server Info' AS FindingsGroup , 'SQL Server Service' AS Finding , '' AS URL , N'Version: ' + CAST(SERVERPROPERTY('productversion') AS NVARCHAR(100)) + N'. Patch Level: ' + CAST(SERVERPROPERTY('productlevel') AS NVARCHAR(100)) + N'. Edition: ' + CAST(SERVERPROPERTY('edition') AS VARCHAR(100)) + N'. AlwaysOn Enabled: ' + CAST(COALESCE(SERVERPROPERTY('IsHadrEnabled'), 0) AS VARCHAR(100)) + N'. AlwaysOn Mgr Status: ' + CAST(COALESCE(SERVERPROPERTY('HadrManagerStatus'), 0) AS VARCHAR(100)) END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 88 ) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 88 AS CheckID , 250 AS Priority , 'Server Info' AS FindingsGroup , 'SQL Server Last Restart' AS Finding , '' AS URL , CAST(create_date AS VARCHAR(100)) FROM sys.databases WHERE database_id = 2 END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 92 ) BEGIN INSERT INTO #driveInfo ( drive, SIZE ) EXEC master..xp_fixeddrives
INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 92 AS CheckID , 250 AS Priority , 'Server Info' AS FindingsGroup , 'Drive ' + i.drive + ' Space' AS Finding , '' AS URL , CAST(i.SIZE AS VARCHAR) + 'MB free on ' + i.drive + ' drive' AS Details FROM #driveInfo AS i DROP TABLE #driveInfo END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 103 ) AND EXISTS ( SELECT * FROM sys.all_objects o INNER JOIN sys.all_columns c ON o.object_id = c.object_id WHERE o.name = 'dm_os_sys_info' AND c.name = 'virtual_machine_type_desc' ) BEGIN SET @StringToExecute = 'INSERT INTO #BlitzResults (CheckID, Priority, FindingsGroup, Finding, URL, Details) SELECT 103 AS CheckID, 250 AS Priority, ''Server Info'' AS FindingsGroup, ''Virtual Server'' AS Finding, ''http://BrentOzar.com/go/virtual'' AS URL, ''Type: ('' + virtual_machine_type_desc + '')'' AS Details FROM sys.dm_os_sys_info WHERE virtual_machine_type <> 0'; EXECUTE(@StringToExecute); END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 114 ) AND EXISTS ( SELECT * FROM sys.all_objects o WHERE o.name = 'dm_os_memory_nodes' ) AND EXISTS ( SELECT * FROM sys.all_objects o INNER JOIN sys.all_columns c ON o.object_id = c.object_id WHERE o.name = 'dm_os_nodes' AND c.name = 'processor_group' ) BEGIN SET @StringToExecute = 'INSERT INTO #BlitzResults (CheckID, Priority, FindingsGroup, Finding, URL, Details) SELECT 114 AS CheckID , 250 AS Priority , ''Server Info'' AS FindingsGroup , ''Hardware - NUMA Config'' AS Finding , '''' AS URL , ''Node: '' + CAST(n.node_id AS NVARCHAR(10)) + '' State: '' + node_state_desc + '' Online schedulers: '' + CAST(n.online_scheduler_count AS NVARCHAR(10)) + '' Processor Group: '' + CAST(n.processor_group AS NVARCHAR(10)) + '' Memory node: '' + CAST(n.memory_node_id AS NVARCHAR(10)) + '' Memory VAS Reserved GB: '' + CAST(CAST((m.virtual_address_space_reserved_kb / 1024.0 / 1024) AS INT) AS NVARCHAR(100)) FROM sys.dm_os_nodes n INNER JOIN sys.dm_os_memory_nodes m ON n.memory_node_id = m.memory_node_id WHERE n.node_state_desc NOT LIKE ''%DAC%'' ORDER BY n.node_id' EXECUTE(@StringToExecute); END
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 106 ) AND (select convert(int,value_in_use) from sys.configurations where name = 'default trace enabled' ) = 1 AND DATALENGTH( COALESCE( @base_tracefilename, '' ) ) > DATALENGTH('.TRC') BEGIN
INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT 106 AS CheckID ,250 AS Priority ,'Server Info' AS FindingsGroup ,'Default Trace Contents' AS Finding ,'http://BrentOzar.com/go/trace' AS URL ,'The default trace holds '+cast(DATEDIFF(hour,MIN(StartTime),GETDATE())as varchar)+' hours of data' +' between '+cast(Min(StartTime) as varchar)+' and '+cast(GETDATE()as varchar) +('. The default trace files are located in: '+left( @curr_tracefilename,len(@curr_tracefilename) - @indx) ) as Details FROM ::fn_trace_gettable( @base_tracefilename, default ) WHERE EventClass BETWEEN 65500 and 65600 END /* CheckID 106 */
IF NOT EXISTS ( SELECT 1 FROM #SkipChecks WHERE DatabaseName IS NULL AND CheckID = 152 ) BEGIN IF EXISTS (SELECT * FROM sys.dm_os_wait_stats WHERE wait_time_ms > .1 * @CPUMSsinceStartup AND waiting_tasks_count > 0 AND wait_type NOT IN ('REQUEST_FOR_DEADLOCK_SEARCH', 'SQLTRACE_INCREMENTAL_FLUSH_SLEEP', 'SQLTRACE_BUFFER_FLUSH', 'LAZYWRITER_SLEEP', 'XE_TIMER_EVENT', 'XE_DISPATCHER_WAIT', 'FT_IFTS_SCHEDULER_IDLE_WAIT', 'LOGMGR_QUEUE', 'CHECKPOINT_QUEUE', 'BROKER_TO_FLUSH', 'BROKER_TASK_STOP', 'BROKER_EVENTHANDLER', 'SLEEP_TASK', 'WAITFOR', 'DBMIRROR_DBM_MUTEX', 'DBMIRROR_EVENTS_QUEUE', 'DBMIRRORING_CMD', 'DISPATCHER_QUEUE_SEMAPHORE', 'BROKER_RECEIVE_WAITFOR', 'CLR_AUTO_EVENT', 'DIRTY_PAGE_POLL', 'HADR_FILESTREAM_IOMGR_IOCOMPLETION', 'ONDEMAND_TASK_QUEUE', 'FT_IFTSHC_MUTEX', 'CLR_MANUAL_EVENT', 'CLR_SEMAPHORE', 'DBMIRROR_WORKER_QUEUE', 'DBMIRROR_DBM_EVENT', 'SP_SERVER_DIAGNOSTICS_SLEEP', 'HADR_CLUSAPI_CALL', 'HADR_LOGCAPTURE_WAIT', 'HADR_NOTIFICATION_DEQUEUE', 'HADR_TIMER_TASK', 'HADR_WORK_QUEUE', 'QDS_PERSIST_TASK_MAIN_LOOP_SLEEP', 'QDS_CLEANUP_STALE_QUERIES_TASK_MAIN_LOOP_SLEEP', 'REDO_THREAD_PENDING_WORK', 'UCS_SESSION_REGISTRATION', 'BROKER_TRANSMITTER', 'QDS_ASYNC_QUEUE')) BEGIN /* Check for waits that have had more than 10% of the server's wait time */ WITH os(wait_type, waiting_tasks_count, wait_time_ms, max_wait_time_ms, signal_wait_time_ms) AS (SELECT wait_type, waiting_tasks_count, wait_time_ms, max_wait_time_ms, signal_wait_time_ms FROM sys.dm_os_wait_stats WHERE wait_type NOT IN ('REQUEST_FOR_DEADLOCK_SEARCH', 'SQLTRACE_INCREMENTAL_FLUSH_SLEEP', 'SQLTRACE_BUFFER_FLUSH', 'LAZYWRITER_SLEEP', 'XE_TIMER_EVENT', 'XE_DISPATCHER_WAIT', 'FT_IFTS_SCHEDULER_IDLE_WAIT', 'LOGMGR_QUEUE', 'CHECKPOINT_QUEUE', 'BROKER_TO_FLUSH', 'BROKER_TASK_STOP', 'BROKER_EVENTHANDLER', 'SLEEP_TASK', 'WAITFOR', 'DBMIRROR_DBM_MUTEX', 'DBMIRROR_EVENTS_QUEUE', 'DBMIRRORING_CMD', 'DISPATCHER_QUEUE_SEMAPHORE', 'BROKER_RECEIVE_WAITFOR', 'CLR_AUTO_EVENT', 'DIRTY_PAGE_POLL', 'HADR_FILESTREAM_IOMGR_IOCOMPLETION', 'ONDEMAND_TASK_QUEUE', 'FT_IFTSHC_MUTEX', 'CLR_MANUAL_EVENT', 'CLR_SEMAPHORE', 'DBMIRROR_WORKER_QUEUE', 'DBMIRROR_DBM_EVENT', 'SP_SERVER_DIAGNOSTICS_SLEEP', 'HADR_CLUSAPI_CALL', 'HADR_LOGCAPTURE_WAIT', 'HADR_NOTIFICATION_DEQUEUE', 'HADR_TIMER_TASK', 'HADR_WORK_QUEUE', 'QDS_PERSIST_TASK_MAIN_LOOP_SLEEP', 'QDS_CLEANUP_STALE_QUERIES_TASK_MAIN_LOOP_SLEEP', 'REDO_THREAD_PENDING_WORK', 'UCS_SESSION_REGISTRATION', 'BROKER_TRANSMITTER', 'PREEMPTIVE_SP_SERVER_DIAGNOSTICS', 'PREEMPTIVE_HADR_LEASE_MECHANISM', 'SLEEP_SYSTEMTASK', 'QDS_SHUTDOWN_QUEUE', 'XE_LIVE_TARGET_TVF') AND wait_time_ms > .1 * @CPUMSsinceStartup AND waiting_tasks_count > 0) INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) SELECT TOP 9 152 AS CheckID ,240 AS Priority ,'Wait Stats' AS FindingsGroup , CAST(ROW_NUMBER() OVER(ORDER BY os.wait_time_ms DESC) AS NVARCHAR(10)) + N' - ' + os.wait_type AS Finding ,'http://BrentOzar.com/go/waits' AS URL , Details = CAST(CAST(SUM(os.wait_time_ms / 1000.0 / 60 / 60) OVER (PARTITION BY os.wait_type) AS NUMERIC(18,1)) AS NVARCHAR(20)) + N' hours of waits, ' + CAST(CAST((SUM(60.0 * os.wait_time_ms) OVER (PARTITION BY os.wait_type) ) / @MSSinceStartup AS NUMERIC(18,1)) AS NVARCHAR(20)) + N' minutes average wait time per hour, ' + CAST(CAST( 100.* SUM(os.wait_time_ms) OVER (PARTITION BY os.wait_type) / (1. * SUM(os.wait_time_ms) OVER () ) AS NUMERIC(18,1)) AS NVARCHAR(40)) + N'% of waits, ' + CAST(CAST( 100. * SUM(os.signal_wait_time_ms) OVER (PARTITION BY os.wait_type) / (1. * SUM(os.wait_time_ms) OVER ()) AS NUMERIC(18,1)) AS NVARCHAR(40)) + N'% signal wait, ' + CAST(SUM(os.waiting_tasks_count) OVER (PARTITION BY os.wait_type) AS NVARCHAR(40)) + N' waiting tasks, ' + CAST(CASE WHEN SUM(os.waiting_tasks_count) OVER (PARTITION BY os.wait_type) > 0 THEN CAST( SUM(os.wait_time_ms) OVER (PARTITION BY os.wait_type) / (1. * SUM(os.waiting_tasks_count) OVER (PARTITION BY os.wait_type)) AS NUMERIC(18,1)) ELSE 0 END AS NVARCHAR(40)) + N' ms average wait time.' FROM os ORDER BY SUM(os.wait_time_ms / 1000.0 / 60 / 60) OVER (PARTITION BY os.wait_type) DESC; END /* IF EXISTS (SELECT * FROM sys.dm_os_wait_stats WHERE wait_time_ms > 0 AND waiting_tasks_count > 0) */
/* If no waits were found, add a note about that */ IF NOT EXISTS (SELECT * FROM #BlitzResults WHERE CheckID IN (107, 108, 109, 121, 152, 162)) BEGIN INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) VALUES (153, 240, 'Wait Stats', 'No Significant Waits Detected', 'http://BrentOzar.com/go/waits', 'This server might be just sitting around idle, or someone may have cleared wait stats recently.'); END END /* CheckID 152 */
END /* IF @CheckServerInfo = 1 */ END /* IF ( ( SERVERPROPERTY('ServerName') NOT IN ( SELECT ServerName */
/* Delete priorites they wanted to skip. */ IF @IgnorePrioritiesAbove IS NOT NULL DELETE #BlitzResults WHERE [Priority] > @IgnorePrioritiesAbove AND CheckID <> -1;
IF @IgnorePrioritiesBelow IS NOT NULL DELETE #BlitzResults WHERE [Priority] < @IgnorePrioritiesBelow AND CheckID <> -1;
/* Delete checks they wanted to skip. */ IF @SkipChecksTable IS NOT NULL BEGIN DELETE FROM #BlitzResults WHERE DatabaseName IN ( SELECT DatabaseName FROM #SkipChecks WHERE CheckID IS NULL AND (ServerName IS NULL OR ServerName = SERVERPROPERTY('ServerName'))); DELETE FROM #BlitzResults WHERE CheckID IN ( SELECT CheckID FROM #SkipChecks WHERE DatabaseName IS NULL AND (ServerName IS NULL OR ServerName = SERVERPROPERTY('ServerName'))); DELETE r FROM #BlitzResults r INNER JOIN #SkipChecks c ON r.DatabaseName = c.DatabaseName and r.CheckID = c.CheckID AND (ServerName IS NULL OR ServerName = SERVERPROPERTY('ServerName')); END
/* Add summary mode */ IF @SummaryMode > 0 BEGIN UPDATE #BlitzResults SET Finding = br.Finding + ' (' + CAST(brTotals.recs AS NVARCHAR(20)) + ')' FROM #BlitzResults br INNER JOIN (SELECT FindingsGroup, Finding, Priority, COUNT(*) AS recs FROM #BlitzResults GROUP BY FindingsGroup, Finding, Priority) brTotals ON br.FindingsGroup = brTotals.FindingsGroup AND br.Finding = brTotals.Finding AND br.Priority = brTotals.Priority WHERE brTotals.recs > 1;
DELETE br FROM #BlitzResults br WHERE EXISTS (SELECT * FROM #BlitzResults brLower WHERE br.FindingsGroup = brLower.FindingsGroup AND br.Finding = brLower.Finding AND br.Priority = brLower.Priority AND br.ID > brLower.ID);
END
/* Add credits for the nice folks who put so much time into building and maintaining this for free: */ INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details ) VALUES ( -1 , 255 , 'Thanks!' , 'From Your Community Volunteers' , 'http://FirstResponderKit.org' , 'We hope you found this tool useful.' );
INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details
) VALUES ( -1 , 0 , 'sp_Blitz ' + CAST(CONVERT(DATETIME, @VersionDate, 102) AS VARCHAR(100)), 'SQL Server First Responder Kit' , 'http://FirstResponderKit.org/' , 'To get help or add your own contributions, join us at http://FirstResponderKit.org.'
);
INSERT INTO #BlitzResults ( CheckID , Priority , FindingsGroup , Finding , URL , Details
) SELECT 156 , 254 , 'Rundate' , GETDATE() , 'http://FirstResponderKit.org/' , 'Captain''s log: stardate something and something...';
IF @EmailRecipients IS NOT NULL BEGIN /* Database mail won't work off a local temp table. I'm not happy about this hacky workaround either. */ IF (OBJECT_ID('tempdb..##BlitzResults', 'U') IS NOT NULL) DROP TABLE ##BlitzResults; SELECT * INTO ##BlitzResults FROM #BlitzResults; SET @query_result_separator = char(9); SET @StringToExecute = 'SET NOCOUNT ON;SELECT [Priority] , [FindingsGroup] , [Finding] , [DatabaseName] , [URL] , [Details] , CheckID FROM ##BlitzResults ORDER BY Priority , FindingsGroup, Finding, Details; SET NOCOUNT OFF;'; SET @EmailSubject = 'sp_Blitz Results for ' + @@SERVERNAME; SET @EmailBody = 'sp_Blitz ' + CAST(CONVERT(DATETIME, @VersionDate, 102) AS VARCHAR(100)) + '. http://FirstResponderKit.org'; IF @EmailProfile IS NULL EXEC msdb.dbo.sp_send_dbmail @recipients = @EmailRecipients, @subject = @EmailSubject, @body = @EmailBody, @query_attachment_filename = 'sp_Blitz-Results.csv', @attach_query_result_as_file = 1, @query_result_header = 1, @query_result_width = 32767, @append_query_error = 1, @query_result_no_padding = 1, @query_result_separator = @query_result_separator, @query = @StringToExecute; ELSE EXEC msdb.dbo.sp_send_dbmail @profile_name = @EmailProfile, @recipients = @EmailRecipients, @subject = @EmailSubject, @body = @EmailBody, @query_attachment_filename = 'sp_Blitz-Results.csv', @attach_query_result_as_file = 1, @query_result_header = 1, @query_result_width = 32767, @append_query_error = 1, @query_result_no_padding = 1, @query_result_separator = @query_result_separator, @query = @StringToExecute; IF (OBJECT_ID('tempdb..##BlitzResults', 'U') IS NOT NULL) DROP TABLE ##BlitzResults; END
/* @OutputTableName lets us export the results to a permanent table */ IF @OutputDatabaseName IS NOT NULL AND @OutputSchemaName IS NOT NULL AND @OutputTableName IS NOT NULL AND EXISTS ( SELECT * FROM sys.databases WHERE QUOTENAME([name]) = @OutputDatabaseName) BEGIN SET @StringToExecute = 'USE ' + @OutputDatabaseName + '; IF EXISTS(SELECT * FROM ' + @OutputDatabaseName + '.INFORMATION_SCHEMA.SCHEMATA WHERE QUOTENAME(SCHEMA_NAME) = ''' + @OutputSchemaName + ''') AND NOT EXISTS (SELECT * FROM ' + @OutputDatabaseName + '.INFORMATION_SCHEMA.TABLES WHERE QUOTENAME(TABLE_SCHEMA) = ''' + @OutputSchemaName + ''' AND QUOTENAME(TABLE_NAME) = ''' + @OutputTableName + ''') CREATE TABLE ' + @OutputSchemaName + '.' + @OutputTableName + ' (ID INT IDENTITY(1,1) NOT NULL, ServerName NVARCHAR(128), CheckDate DATETIMEOFFSET, Priority TINYINT , FindingsGroup VARCHAR(50) , Finding VARCHAR(200) , DatabaseName NVARCHAR(128), URL VARCHAR(200) , Details NVARCHAR(4000) , QueryPlan [XML] NULL , QueryPlanFiltered [NVARCHAR](MAX) NULL, CheckID INT , CONSTRAINT [PK_' + CAST(NEWID() AS CHAR(36)) + '] PRIMARY KEY CLUSTERED (ID ASC));' EXEC(@StringToExecute); SET @StringToExecute = N' IF EXISTS(SELECT * FROM ' + @OutputDatabaseName + '.INFORMATION_SCHEMA.SCHEMATA WHERE QUOTENAME(SCHEMA_NAME) = ''' + @OutputSchemaName + ''') INSERT ' + @OutputDatabaseName + '.' + @OutputSchemaName + '.' + @OutputTableName + ' (ServerName, CheckDate, CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details, QueryPlan, QueryPlanFiltered) SELECT ''' + CAST(SERVERPROPERTY('ServerName') AS NVARCHAR(128)) + ''', SYSDATETIMEOFFSET(), CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details, QueryPlan, QueryPlanFiltered FROM #BlitzResults ORDER BY Priority , FindingsGroup , Finding , Details'; EXEC(@StringToExecute); END ELSE IF (SUBSTRING(@OutputTableName, 2, 2) = '##') BEGIN SET @StringToExecute = N' IF (OBJECT_ID(''tempdb..' + @OutputTableName + ''') IS NOT NULL) DROP TABLE ' + @OutputTableName + ';' + 'CREATE TABLE ' + @OutputTableName + ' (ID INT IDENTITY(1,1) NOT NULL, ServerName NVARCHAR(128), CheckDate DATETIMEOFFSET, Priority TINYINT , FindingsGroup VARCHAR(50) , Finding VARCHAR(200) , DatabaseName NVARCHAR(128), URL VARCHAR(200) , Details NVARCHAR(4000) , QueryPlan [XML] NULL , QueryPlanFiltered [NVARCHAR](MAX) NULL, CheckID INT , CONSTRAINT [PK_' + CAST(NEWID() AS CHAR(36)) + '] PRIMARY KEY CLUSTERED (ID ASC));' + ' INSERT ' + @OutputTableName + ' (ServerName, CheckDate, CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details, QueryPlan, QueryPlanFiltered) SELECT ''' + CAST(SERVERPROPERTY('ServerName') AS NVARCHAR(128)) + ''', SYSDATETIMEOFFSET(), CheckID, DatabaseName, Priority, FindingsGroup, Finding, URL, Details, QueryPlan, QueryPlanFiltered FROM #BlitzResults ORDER BY Priority , FindingsGroup , Finding , Details'; EXEC(@StringToExecute); END ELSE IF (SUBSTRING(@OutputTableName, 2, 1) = '#') BEGIN RAISERROR('Due to the nature of Dymamic SQL, only global (i.e. double pound (##)) temp tables are supported for @OutputTableName', 16, 0) END
DECLARE @separator AS VARCHAR(1); IF @OutputType = 'RSV' SET @separator = CHAR(31); ELSE SET @separator = ',';
IF @OutputType = 'COUNT' BEGIN SELECT COUNT(*) AS Warnings FROM #BlitzResults END ELSE IF @OutputType IN ( 'CSV', 'RSV' ) BEGIN
SELECT Result = CAST([Priority] AS NVARCHAR(100)) + @separator + CAST(CheckID AS NVARCHAR(100)) + @separator + COALESCE([FindingsGroup], '(N/A)') + @separator + COALESCE([Finding], '(N/A)') + @separator + COALESCE(DatabaseName, '(N/A)') + @separator + COALESCE([URL], '(N/A)') + @separator + COALESCE([Details], '(N/A)') FROM #BlitzResults ORDER BY Priority , FindingsGroup , Finding , Details; END ELSE IF @OutputXMLasNVARCHAR = 1 AND @OutputType <> 'NONE' BEGIN SELECT [Priority] , [FindingsGroup] , [Finding] , [DatabaseName] , [URL] , [Details] , CAST([QueryPlan] AS NVARCHAR(MAX)) AS QueryPlan, [QueryPlanFiltered] , CheckID FROM #BlitzResults ORDER BY Priority , FindingsGroup , Finding , Details; END ELSE IF @OutputType <> 'NONE' BEGIN SELECT [Priority] , [FindingsGroup] , [Finding] , [DatabaseName] , [URL] , [Details] , [QueryPlan] , [QueryPlanFiltered] , CheckID FROM #BlitzResults ORDER BY Priority , FindingsGroup , Finding , Details; END
DROP TABLE #BlitzResults;
IF @OutputProcedureCache = 1 AND @CheckProcedureCache = 1 SELECT TOP 20 total_worker_time / execution_count AS AvgCPU , total_worker_time AS TotalCPU , CAST(ROUND(100.00 * total_worker_time / ( SELECT SUM(total_worker_time) FROM sys.dm_exec_query_stats ), 2) AS MONEY) AS PercentCPU , total_elapsed_time / execution_count AS AvgDuration , total_elapsed_time AS TotalDuration , CAST(ROUND(100.00 * total_elapsed_time / ( SELECT SUM(total_elapsed_time) FROM sys.dm_exec_query_stats ), 2) AS MONEY) AS PercentDuration , total_logical_reads / execution_count AS AvgReads , total_logical_reads AS TotalReads , CAST(ROUND(100.00 * total_logical_reads / ( SELECT SUM(total_logical_reads) FROM sys.dm_exec_query_stats ), 2) AS MONEY) AS PercentReads , execution_count , CAST(ROUND(100.00 * execution_count / ( SELECT SUM(execution_count) FROM sys.dm_exec_query_stats ), 2) AS MONEY) AS PercentExecutions , CASE WHEN DATEDIFF(mi, creation_time, qs.last_execution_time) = 0 THEN 0 ELSE CAST(( 1.00 * execution_count / DATEDIFF(mi, creation_time, qs.last_execution_time) ) AS MONEY) END AS executions_per_minute , qs.creation_time AS plan_creation_time , qs.last_execution_time , text , text_filtered , query_plan , query_plan_filtered , sql_handle , query_hash , plan_handle , query_plan_hash FROM #dm_exec_query_stats qs ORDER BY CASE UPPER(@CheckProcedureCacheFilter) WHEN 'CPU' THEN total_worker_time WHEN 'READS' THEN total_logical_reads WHEN 'EXECCOUNT' THEN execution_count WHEN 'DURATION' THEN total_elapsed_time ELSE total_worker_time END DESC
END /* ELSE -- IF @OutputType = 'SCHEMA' */
SET NOCOUNT OFF;

CREATE PROCEDURE [dbo].[sp_Blitz]
    @Help TINYINT = 0 ,
    @CheckUserDatabaseObjects TINYINT = 1 ,
    @CheckProcedureCache TINYINT = 0 ,
    @OutputType VARCHAR(20) = 'TABLE' ,
    @OutputProcedureCache TINYINT = 0 ,
    @CheckProcedureCacheFilter VARCHAR(10) = NULL ,
    @CheckServerInfo TINYINT = 1 ,
    @SkipChecksServer NVARCHAR(256) = NULL ,
    @SkipChecksDatabase NVARCHAR(256) = NULL ,
    @SkipChecksSchema NVARCHAR(256) = NULL ,
    @SkipChecksTable NVARCHAR(256) = NULL ,
    @IgnorePrioritiesBelow INT = NULL ,
    @IgnorePrioritiesAbove INT = NULL ,
    @OutputServerName NVARCHAR(256) = NULL ,
    @OutputDatabaseName NVARCHAR(256) = NULL ,
    @OutputSchemaName NVARCHAR(256) = NULL ,
    @OutputTableName NVARCHAR(256) = NULL ,
    @OutputXMLasNVARCHAR TINYINT = 0 ,
    @EmailRecipients VARCHAR(MAX) = NULL ,
    @EmailProfile sysname = NULL ,
    @SummaryMode TINYINT = 0 ,
    @BringThePain TINYINT = 0 ,
    @VersionDate DATETIME = NULL OUTPUT
AS

BEGIN; SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED; SET QUOTED_IDENTIFIER ON; SET ANSI_PADDING ON; SET CONCAT_NULL_YIELDS_NULL ON; SET ANSI_WARNINGS ON; SET NUMERIC_ROUNDABORT OFF; SET ARITHABORT ON;
IF @filter IS NULL OR @filter_type IS NULL OR @not_filter IS NULL OR @not_filter_type IS NULL OR @show_own_spid IS NULL OR @show_system_spids IS NULL OR @show_sleeping_spids IS NULL OR @get_full_inner_text IS NULL OR @get_plans IS NULL OR @get_outer_command IS NULL OR @get_transaction_info IS NULL OR @get_task_info IS NULL OR @get_locks IS NULL OR @get_avg_time IS NULL OR @get_additional_info IS NULL OR @find_block_leaders IS NULL OR @delta_interval IS NULL OR @format_output IS NULL OR @output_column_list IS NULL OR @sort_order IS NULL OR @return_schema IS NULL OR @destination_table IS NULL OR @help IS NULL BEGIN; RAISERROR('Input parameters cannot be NULL', 16, 1); RETURN; END;
IF @filter_type NOT IN ('session', 'program', 'database', 'login', 'host') BEGIN; RAISERROR('Valid filter types are: session, program, database, login, host', 16, 1); RETURN; END;
IF @filter_type = 'session' AND @filter LIKE '%[^0123456789]%' BEGIN; RAISERROR('Session filters must be valid integers', 16, 1); RETURN; END;
IF @not_filter_type NOT IN ('session', 'program', 'database', 'login', 'host') BEGIN; RAISERROR('Valid filter types are: session, program, database, login, host', 16, 1); RETURN; END;
IF @not_filter_type = 'session' AND @not_filter LIKE '%[^0123456789]%' BEGIN; RAISERROR('Session filters must be valid integers', 16, 1); RETURN; END;
IF @show_sleeping_spids NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @show_sleeping_spids are: 0, 1, or 2', 16, 1); RETURN; END;
IF @get_plans NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @get_plans are: 0, 1, or 2', 16, 1); RETURN; END;
IF @get_task_info NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @get_task_info are: 0, 1, or 2', 16, 1); RETURN; END;
IF @format_output NOT IN (0, 1, 2) BEGIN; RAISERROR('Valid values for @format_output are: 0, 1, or 2', 16, 1); RETURN; END;
IF @help = 1 BEGIN; DECLARE @header VARCHAR(MAX), @params VARCHAR(MAX), @outputs VARCHAR(MAX);
SELECT @header = REPLACE ( REPLACE ( CONVERT ( VARCHAR(MAX), SUBSTRING ( t.text, CHARINDEX('/' + REPLICATE('*', 93), t.text) + 94, CHARINDEX(REPLICATE('*', 93) + '/', t.text) - (CHARINDEX('/' + REPLICATE('*', 93), t.text) + 94) ) ), CHAR(13)+CHAR(10), CHAR(13) ), ' ', '' ), @params = CHAR(13) + REPLACE ( REPLACE ( CONVERT ( VARCHAR(MAX), SUBSTRING ( t.text, CHARINDEX('--~', t.text) + 5, CHARINDEX('--~', t.text, CHARINDEX('--~', t.text) + 5) - (CHARINDEX('--~', t.text) + 5) ) ), CHAR(13)+CHAR(10), CHAR(13) ), ' ', '' ), @outputs = CHAR(13) + REPLACE ( REPLACE ( REPLACE ( CONVERT ( VARCHAR(MAX), SUBSTRING ( t.text, CHARINDEX('OUTPUT COLUMNS'+CHAR(13)+CHAR(10)+'--------------', t.text) + 32, CHARINDEX('*/', t.text, CHARINDEX('OUTPUT COLUMNS'+CHAR(13)+CHAR(10)+'--------------', t.text) + 32) - (CHARINDEX('OUTPUT COLUMNS'+CHAR(13)+CHAR(10)+'--------------', t.text) + 32) ) ), CHAR(9), CHAR(255) ), CHAR(13)+CHAR(10), CHAR(13) ), ' ', '' ) + CHAR(13) FROM sys.dm_exec_requests AS r CROSS APPLY sys.dm_exec_sql_text(r.sql_handle) AS t WHERE r.session_id = @@SPID;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@header) - 1) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ) SELECT RTRIM(LTRIM( SUBSTRING ( @header, number + 1, CHARINDEX(CHAR(13), @header, number + 1) - number - 1 ) )) AS [------header---------------------------------------------------------------------------------------------------------------] FROM numbers WHERE SUBSTRING(@header, number, 1) = CHAR(13);
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@params) - 1) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT RTRIM(LTRIM( SUBSTRING ( @params, number + 1, CHARINDEX(CHAR(13), @params, number + 1) - number - 1 ) )) AS token, number, CASE WHEN SUBSTRING(@params, number + 1, 1) = CHAR(13) THEN number ELSE COALESCE(NULLIF(CHARINDEX(',' + CHAR(13) + CHAR(13), @params, number), 0), LEN(@params)) END AS param_group, ROW_NUMBER() OVER ( PARTITION BY CHARINDEX(',' + CHAR(13) + CHAR(13), @params, number), SUBSTRING(@params, number+1, 1) ORDER BY number ) AS group_order FROM numbers WHERE SUBSTRING(@params, number, 1) = CHAR(13) ), parsed_tokens AS ( SELECT MIN ( CASE WHEN token LIKE '@%' THEN token ELSE NULL END ) AS parameter, MIN ( CASE WHEN token LIKE '--%' THEN RIGHT(token, LEN(token) - 2) ELSE NULL END ) AS description, param_group, group_order FROM tokens WHERE NOT ( token = '' AND group_order > 1 ) GROUP BY param_group, group_order ) SELECT CASE WHEN description IS NULL AND parameter IS NULL THEN '-------------------------------------------------------------------------' WHEN param_group = MAX(param_group) OVER() THEN parameter ELSE COALESCE(LEFT(parameter, LEN(parameter) - 1), '') END AS [------parameter----------------------------------------------------------], CASE WHEN description IS NULL AND parameter IS NULL THEN '----------------------------------------------------------------------------------------------------------------------' ELSE COALESCE(description, '') END AS [------description-----------------------------------------------------------------------------------------------------] FROM parsed_tokens ORDER BY param_group, group_order;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@outputs) - 1) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT RTRIM(LTRIM( SUBSTRING ( @outputs, number + 1, CASE WHEN COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) < COALESCE(NULLIF(CHARINDEX(CHAR(13) + CHAR(255) COLLATE Latin1_General_Bin2, @outputs, number + 1), 0), LEN(@outputs)) THEN COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) - number - 1 ELSE COALESCE(NULLIF(CHARINDEX(CHAR(13) + CHAR(255) COLLATE Latin1_General_Bin2, @outputs, number + 1), 0), LEN(@outputs)) - number - 1 END ) )) AS token, number, COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) AS output_group, ROW_NUMBER() OVER ( PARTITION BY COALESCE(NULLIF(CHARINDEX(CHAR(13) + 'Formatted', @outputs, number + 1), 0), LEN(@outputs)) ORDER BY number ) AS output_group_order FROM numbers WHERE SUBSTRING(@outputs, number, 10) = CHAR(13) + 'Formatted' OR SUBSTRING(@outputs, number, 2) = CHAR(13) + CHAR(255) COLLATE Latin1_General_Bin2 ), output_tokens AS ( SELECT *, CASE output_group_order WHEN 2 THEN MAX(CASE output_group_order WHEN 1 THEN token ELSE NULL END) OVER (PARTITION BY output_group) ELSE '' END COLLATE Latin1_General_Bin2 AS column_info FROM tokens ) SELECT CASE output_group_order WHEN 1 THEN '-----------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+1, CHARINDEX(']', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+2) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)) ELSE SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+2, CHARINDEX(']', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)+2) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info)-1) END ELSE '' END AS formatted_column_name, CASE output_group_order WHEN 1 THEN '-----------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN SUBSTRING(column_info, CHARINDEX(']', column_info)+2, LEN(column_info)) ELSE SUBSTRING(column_info, CHARINDEX(']', column_info)+2, CHARINDEX('Non-Formatted:', column_info, CHARINDEX(']', column_info)+2) - CHARINDEX(']', column_info)-3) END ELSE '' END AS formatted_column_type, CASE output_group_order WHEN 1 THEN '---------------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN '' ELSE CASE WHEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, 1) = '<' THEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, CHARINDEX('>', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))) ELSE SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, CHARINDEX(']', column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1) - CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))) END END ELSE '' END AS unformatted_column_name, CASE output_group_order WHEN 1 THEN '---------------------------------------' WHEN 2 THEN CASE WHEN CHARINDEX('Formatted/Non:', column_info) = 1 THEN '' ELSE CASE WHEN SUBSTRING(column_info, CHARINDEX(CHAR(255) COLLATE Latin1_General_Bin2, column_info, CHARINDEX('Non-Formatted:', column_info))+1, 1) = '<' THEN '' ELSE SUBSTRING(column_info, CHARINDEX(']', column_info, CHARINDEX('Non-Formatted:', column_info))+2, CHARINDEX('Non-Formatted:', column_info, CHARINDEX(']', column_info)+2) - CHARINDEX(']', column_info)-3) END END ELSE '' END AS unformatted_column_type, CASE output_group_order WHEN 1 THEN '----------------------------------------------------------------------------------------------------------------------' ELSE REPLACE(token, CHAR(255) COLLATE Latin1_General_Bin2, '') END AS [------description-----------------------------------------------------------------------------------------------------] FROM output_tokens WHERE NOT ( output_group_order = 1 AND output_group = LEN(@outputs) ) ORDER BY output_group, CASE output_group_order WHEN 1 THEN 99 ELSE output_group_order END;
RETURN; END;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@output_column_list)) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT '|[' + SUBSTRING ( @output_column_list, number + 1, CHARINDEX(']', @output_column_list, number) - number - 1 ) + '|]' AS token, number FROM numbers WHERE SUBSTRING(@output_column_list, number, 1) = '[' ), ordered_columns AS ( SELECT x.column_name, ROW_NUMBER() OVER ( PARTITION BY x.column_name ORDER BY tokens.number, x.default_order ) AS r, ROW_NUMBER() OVER ( ORDER BY tokens.number, x.default_order ) AS s FROM tokens JOIN ( SELECT '[session_id]' AS column_name, 1 AS default_order UNION ALL SELECT '[dd hh:mm:ss.mss]', 2 WHERE @format_output = 1 UNION ALL SELECT '[dd hh:mm:ss.mss (avg)]', 3 WHERE @format_output = 1 AND @get_avg_time = 1 UNION ALL SELECT '[avg_elapsed_time]', 4 WHERE @format_output = 0 AND @get_avg_time = 1 UNION ALL SELECT '[physical_io]', 5 WHERE @get_task_info = 2 UNION ALL SELECT '[reads]', 6 UNION ALL SELECT '[physical_reads]', 7 UNION ALL SELECT '[writes]', 8 UNION ALL SELECT '[tempdb_allocations]', 9 UNION ALL SELECT '[tempdb_current]', 10 UNION ALL SELECT '[CPU]', 11 UNION ALL SELECT '[context_switches]', 12 WHERE @get_task_info = 2 UNION ALL SELECT '[used_memory]', 13 UNION ALL SELECT '[physical_io_delta]', 14 WHERE @delta_interval > 0 AND @get_task_info = 2 UNION ALL SELECT '[reads_delta]', 15 WHERE @delta_interval > 0 UNION ALL SELECT '[physical_reads_delta]', 16 WHERE @delta_interval > 0 UNION ALL SELECT '[writes_delta]', 17 WHERE @delta_interval > 0 UNION ALL SELECT '[tempdb_allocations_delta]', 18 WHERE @delta_interval > 0 UNION ALL SELECT '[tempdb_current_delta]', 19 WHERE @delta_interval > 0 UNION ALL SELECT '[CPU_delta]', 20 WHERE @delta_interval > 0 UNION ALL SELECT '[context_switches_delta]', 21 WHERE @delta_interval > 0 AND @get_task_info = 2 UNION ALL SELECT '[used_memory_delta]', 22 WHERE @delta_interval > 0 UNION ALL SELECT '[tasks]', 23 WHERE @get_task_info = 2 UNION ALL SELECT '[status]', 24 UNION ALL SELECT '[wait_info]', 25 WHERE @get_task_info > 0 OR @find_block_leaders = 1 UNION ALL SELECT '[locks]', 26 WHERE @get_locks = 1 UNION ALL SELECT '[tran_start_time]', 27 WHERE @get_transaction_info = 1 UNION ALL SELECT '[tran_log_writes]', 28 WHERE @get_transaction_info = 1 UNION ALL SELECT '[open_tran_count]', 29 UNION ALL SELECT '[sql_command]', 30 WHERE @get_outer_command = 1 UNION ALL SELECT '[sql_text]', 31 UNION ALL SELECT '[query_plan]', 32 WHERE @get_plans >= 1 UNION ALL SELECT '[blocking_session_id]', 33 WHERE @get_task_info > 0 OR @find_block_leaders = 1 UNION ALL SELECT '[blocked_session_count]', 34 WHERE @find_block_leaders = 1 UNION ALL SELECT '[percent_complete]', 35 UNION ALL SELECT '[host_name]', 36 UNION ALL SELECT '[login_name]', 37 UNION ALL SELECT '[database_name]', 38 UNION ALL SELECT '[program_name]', 39 UNION ALL SELECT '[additional_info]', 40 WHERE @get_additional_info = 1 UNION ALL SELECT '[start_time]', 41 UNION ALL SELECT '[login_time]', 42 UNION ALL SELECT '[request_id]', 43 UNION ALL SELECT '[collection_time]', 44 ) AS x ON x.column_name LIKE token ESCAPE '|' ) SELECT @output_column_list = STUFF ( ( SELECT ',' + column_name as [text()] FROM ordered_columns WHERE r = 1 ORDER BY s FOR XML PATH('') ), 1, 1, '' );
IF COALESCE(RTRIM(@output_column_list), '') = '' BEGIN; RAISERROR('No valid column matches found in @output_column_list or no columns remain due to selected options.', 16, 1); RETURN; END;
IF @destination_table <> '' BEGIN; SET @destination_table = --database COALESCE(QUOTENAME(PARSENAME(@destination_table, 3)) + '.', '') + --schema COALESCE(QUOTENAME(PARSENAME(@destination_table, 2)) + '.', '') + --table COALESCE(QUOTENAME(PARSENAME(@destination_table, 1)), '');
IF COALESCE(RTRIM(@destination_table), '') = '' BEGIN; RAISERROR('Destination table not properly formatted.', 16, 1); RETURN; END; END;
WITH a0 AS (SELECT 1 AS n UNION ALL SELECT 1), a1 AS (SELECT 1 AS n FROM a0 AS a, a0 AS b), a2 AS (SELECT 1 AS n FROM a1 AS a, a1 AS b), a3 AS (SELECT 1 AS n FROM a2 AS a, a2 AS b), a4 AS (SELECT 1 AS n FROM a3 AS a, a3 AS b), numbers AS ( SELECT TOP(LEN(@sort_order)) ROW_NUMBER() OVER ( ORDER BY (SELECT NULL) ) AS number FROM a4 ORDER BY number ), tokens AS ( SELECT '|[' + SUBSTRING ( @sort_order, number + 1, CHARINDEX(']', @sort_order, number) - number - 1 ) + '|]' AS token, SUBSTRING ( @sort_order, CHARINDEX(']', @sort_order, number) + 1, COALESCE(NULLIF(CHARINDEX('[', @sort_order, CHARINDEX(']', @sort_order, number)), 0), LEN(@sort_order)) - CHARINDEX(']', @sort_order, number) ) AS next_chunk, number FROM numbers WHERE SUBSTRING(@sort_order, number, 1) = '[' ), ordered_columns AS ( SELECT x.column_name + CASE WHEN tokens.next_chunk LIKE '%asc%' THEN ' ASC' WHEN tokens.next_chunk LIKE '%desc%' THEN ' DESC' ELSE '' END AS column_name, ROW_NUMBER() OVER ( PARTITION BY x.column_name ORDER BY tokens.number ) AS r, tokens.number FROM tokens JOIN ( SELECT '[session_id]' AS column_name UNION ALL SELECT '[physical_io]' UNION ALL SELECT '[reads]' UNION ALL SELECT '[physical_reads]' UNION ALL SELECT '[writes]' UNION ALL SELECT '[tempdb_allocations]' UNION ALL SELECT '[tempdb_current]' UNION ALL SELECT '[CPU]' UNION ALL SELECT '[context_switches]' UNION ALL SELECT '[used_memory]' UNION ALL SELECT '[physical_io_delta]' UNION ALL SELECT '[reads_delta]' UNION ALL SELECT '[physical_reads_delta]' UNION ALL SELECT '[writes_delta]' UNION ALL SELECT '[tempdb_allocations_delta]' UNION ALL SELECT '[tempdb_current_delta]' UNION ALL SELECT '[CPU_delta]' UNION ALL SELECT '[context_switches_delta]' UNION ALL SELECT '[used_memory_delta]' UNION ALL SELECT '[tasks]' UNION ALL SELECT '[tran_start_time]' UNION ALL SELECT '[open_tran_count]' UNION ALL SELECT '[blocking_session_id]' UNION ALL SELECT '[blocked_session_count]' UNION ALL SELECT '[percent_complete]' UNION ALL SELECT '[host_name]' UNION ALL SELECT '[login_name]' UNION ALL SELECT '[database_name]' UNION ALL SELECT '[start_time]' UNION ALL SELECT '[login_time]' ) AS x ON x.column_name LIKE token ESCAPE '|' ) SELECT @sort_order = COALESCE(z.sort_order, '') FROM ( SELECT STUFF ( ( SELECT ',' + column_name as [text()] FROM ordered_columns WHERE r = 1 ORDER BY number FOR XML PATH('') ), 1, 1, '' ) AS sort_order ) AS z;
CREATE TABLE #sessions ( recursion SMALLINT NOT NULL, session_id SMALLINT NOT NULL, request_id INT NOT NULL, session_number INT NOT NULL, elapsed_time INT NOT NULL, avg_elapsed_time INT NULL, physical_io BIGINT NULL, reads BIGINT NULL, physical_reads BIGINT NULL, writes BIGINT NULL, tempdb_allocations BIGINT NULL, tempdb_current BIGINT NULL, CPU INT NULL, thread_CPU_snapshot BIGINT NULL, context_switches BIGINT NULL, used_memory BIGINT NOT NULL, tasks SMALLINT NULL, status VARCHAR(30) NOT NULL, wait_info NVARCHAR(4000) NULL, locks XML NULL, transaction_id BIGINT NULL, tran_start_time DATETIME NULL, tran_log_writes NVARCHAR(4000) NULL, open_tran_count SMALLINT NULL, sql_command XML NULL, sql_handle VARBINARY(64) NULL, statement_start_offset INT NULL, statement_end_offset INT NULL, sql_text XML NULL, plan_handle VARBINARY(64) NULL, query_plan XML NULL, blocking_session_id SMALLINT NULL, blocked_session_count SMALLINT NULL, percent_complete REAL NULL, host_name sysname NULL, login_name sysname NOT NULL, database_name sysname NULL, program_name sysname NULL, additional_info XML NULL, start_time DATETIME NOT NULL, login_time DATETIME NULL, last_request_start_time DATETIME NULL, PRIMARY KEY CLUSTERED (session_id, request_id, recursion) WITH (IGNORE_DUP_KEY = ON), UNIQUE NONCLUSTERED (transaction_id, session_id, request_id, recursion) WITH (IGNORE_DUP_KEY = ON) );
IF @return_schema = 0 BEGIN; --Disable unnecessary autostats on the table CREATE STATISTICS s_session_id ON #sessions (session_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_id ON #sessions (request_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_transaction_id ON #sessions (transaction_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_session_number ON #sessions (session_number) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_status ON #sessions (status) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_start_time ON #sessions (start_time) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_last_request_start_time ON #sessions (last_request_start_time) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_recursion ON #sessions (recursion) WITH SAMPLE 0 ROWS, NORECOMPUTE;
DECLARE @recursion SMALLINT; SET @recursion = CASE @delta_interval WHEN 0 THEN 1 ELSE -1 END;
DECLARE @first_collection_ms_ticks BIGINT; DECLARE @last_collection_start DATETIME;
--Used for the delta pull REDO:;
IF @get_locks = 1 AND @recursion = 1 AND @output_column_list LIKE '%|[locks|]%' ESCAPE '|' BEGIN; SELECT y.resource_type, y.database_name, y.object_id, y.file_id, y.page_type, y.hobt_id, y.allocation_unit_id, y.index_id, y.schema_id, y.principal_id, y.request_mode, y.request_status, y.session_id, y.resource_description, y.request_count, s.request_id, s.start_time, CONVERT(sysname, NULL) AS object_name, CONVERT(sysname, NULL) AS index_name, CONVERT(sysname, NULL) AS schema_name, CONVERT(sysname, NULL) AS principal_name, CONVERT(NVARCHAR(2048), NULL) AS query_error INTO #locks FROM ( SELECT sp.spid AS session_id, CASE sp.status WHEN 'sleeping' THEN CONVERT(INT, 0) ELSE sp.request_id END AS request_id, CASE sp.status WHEN 'sleeping' THEN sp.last_batch ELSE COALESCE(req.start_time, sp.last_batch) END AS start_time, sp.dbid FROM sys.sysprocesses AS sp OUTER APPLY ( SELECT TOP(1) CASE WHEN ( sp.hostprocess > '' OR r.total_elapsed_time < 0 ) THEN r.start_time ELSE DATEADD ( ms, 1000 * (DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())) / 500) - DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())), DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE()) ) END AS start_time FROM sys.dm_exec_requests AS r WHERE r.session_id = sp.spid AND r.request_id = sp.request_id ) AS req WHERE --Process inclusive filter 1 = CASE WHEN @filter <> '' THEN CASE @filter_type WHEN 'session' THEN CASE WHEN CONVERT(SMALLINT, @filter) = 0 OR sp.spid = CONVERT(SMALLINT, @filter) THEN 1 ELSE 0 END WHEN 'program' THEN CASE WHEN sp.program_name LIKE @filter THEN 1 ELSE 0 END WHEN 'login' THEN CASE WHEN sp.loginame LIKE @filter THEN 1 ELSE 0 END WHEN 'host' THEN CASE WHEN sp.hostname LIKE @filter THEN 1 ELSE 0 END WHEN 'database' THEN CASE WHEN DB_NAME(sp.dbid) LIKE @filter THEN 1 ELSE 0 END ELSE 0 END ELSE 1 END --Process exclusive filter AND 0 = CASE WHEN @not_filter <> '' THEN CASE @not_filter_type WHEN 'session' THEN CASE WHEN sp.spid = CONVERT(SMALLINT, @not_filter) THEN 1 ELSE 0 END WHEN 'program' THEN CASE WHEN sp.program_name LIKE @not_filter THEN 1 ELSE 0 END WHEN 'login' THEN CASE WHEN sp.loginame LIKE @not_filter THEN 1 ELSE 0 END WHEN 'host' THEN CASE WHEN sp.hostname LIKE @not_filter THEN 1 ELSE 0 END WHEN 'database' THEN CASE WHEN DB_NAME(sp.dbid) LIKE @not_filter THEN 1 ELSE 0 END ELSE 0 END ELSE 0 END AND ( @show_own_spid = 1 OR sp.spid <> @@SPID ) AND ( @show_system_spids = 1 OR sp.hostprocess > '' ) AND sp.ecid = 0 ) AS s INNER HASH JOIN ( SELECT x.resource_type, x.database_name, x.object_id, x.file_id, CASE WHEN x.page_no = 1 OR x.page_no % 8088 = 0 THEN 'PFS' WHEN x.page_no = 2 OR x.page_no % 511232 = 0 THEN 'GAM' WHEN x.page_no = 3 OR x.page_no % 511233 = 0 THEN 'SGAM' WHEN x.page_no = 6 OR x.page_no % 511238 = 0 THEN 'DCM' WHEN x.page_no = 7 OR x.page_no % 511239 = 0 THEN 'BCM' WHEN x.page_no IS NOT NULL THEN '*' ELSE NULL END AS page_type, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id, x.request_mode, x.request_status, x.session_id, x.request_id, CASE WHEN COALESCE(x.object_id, x.file_id, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id) IS NULL THEN NULLIF(resource_description, '') ELSE NULL END AS resource_description, COUNT(*) AS request_count FROM ( SELECT tl.resource_type + CASE WHEN tl.resource_subtype = '' THEN '' ELSE '.' + tl.resource_subtype END AS resource_type, COALESCE(DB_NAME(tl.resource_database_id), N'(null)') AS database_name, CONVERT ( INT, CASE WHEN tl.resource_type = 'OBJECT' THEN tl.resource_associated_entity_id WHEN tl.resource_description LIKE '%object_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('object_id = ', tl.resource_description) + 12), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('object_id = ', tl.resource_description) + 12), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('object_id = ', tl.resource_description) + 12) ) ) ELSE NULL END ) AS object_id, CONVERT ( INT, CASE WHEN tl.resource_type = 'FILE' THEN CONVERT(INT, tl.resource_description) WHEN tl.resource_type IN ('PAGE', 'EXTENT', 'RID') THEN LEFT(tl.resource_description, CHARINDEX(':', tl.resource_description)-1) ELSE NULL END ) AS file_id, CONVERT ( INT, CASE WHEN tl.resource_type IN ('PAGE', 'EXTENT', 'RID') THEN SUBSTRING ( tl.resource_description, CHARINDEX(':', tl.resource_description) + 1, COALESCE ( NULLIF ( CHARINDEX(':', tl.resource_description, CHARINDEX(':', tl.resource_description) + 1), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX(':', tl.resource_description) + 1) ) ELSE NULL END ) AS page_no, CASE WHEN tl.resource_type IN ('PAGE', 'KEY', 'RID', 'HOBT') THEN tl.resource_associated_entity_id ELSE NULL END AS hobt_id, CASE WHEN tl.resource_type = 'ALLOCATION_UNIT' THEN tl.resource_associated_entity_id ELSE NULL END AS allocation_unit_id, CONVERT ( INT, CASE WHEN /*TODO: Deal with server principals*/ tl.resource_subtype <> 'SERVER_PRINCIPAL' AND tl.resource_description LIKE '%index_id or stats_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('index_id or stats_id = ', tl.resource_description) + 23), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('index_id or stats_id = ', tl.resource_description) + 23), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('index_id or stats_id = ', tl.resource_description) + 23) ) ) ELSE NULL END ) AS index_id, CONVERT ( INT, CASE WHEN tl.resource_description LIKE '%schema_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('schema_id = ', tl.resource_description) + 12), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('schema_id = ', tl.resource_description) + 12), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('schema_id = ', tl.resource_description) + 12) ) ) ELSE NULL END ) AS schema_id, CONVERT ( INT, CASE WHEN tl.resource_description LIKE '%principal_id = %' THEN ( SUBSTRING ( tl.resource_description, (CHARINDEX('principal_id = ', tl.resource_description) + 15), COALESCE ( NULLIF ( CHARINDEX(',', tl.resource_description, CHARINDEX('principal_id = ', tl.resource_description) + 15), 0 ), DATALENGTH(tl.resource_description)+1 ) - (CHARINDEX('principal_id = ', tl.resource_description) + 15) ) ) ELSE NULL END ) AS principal_id, tl.request_mode, tl.request_status, tl.request_session_id AS session_id, tl.request_request_id AS request_id,
/*TODO: Applocks, other resource_descriptions*/ RTRIM(tl.resource_description) AS resource_description, tl.resource_associated_entity_id /*********************************************/ FROM ( SELECT request_session_id, CONVERT(VARCHAR(120), resource_type) COLLATE Latin1_General_Bin2 AS resource_type, CONVERT(VARCHAR(120), resource_subtype) COLLATE Latin1_General_Bin2 AS resource_subtype, resource_database_id, CONVERT(VARCHAR(512), resource_description) COLLATE Latin1_General_Bin2 AS resource_description, resource_associated_entity_id, CONVERT(VARCHAR(120), request_mode) COLLATE Latin1_General_Bin2 AS request_mode, CONVERT(VARCHAR(120), request_status) COLLATE Latin1_General_Bin2 AS request_status, request_request_id FROM sys.dm_tran_locks ) AS tl ) AS x GROUP BY x.resource_type, x.database_name, x.object_id, x.file_id, CASE WHEN x.page_no = 1 OR x.page_no % 8088 = 0 THEN 'PFS' WHEN x.page_no = 2 OR x.page_no % 511232 = 0 THEN 'GAM' WHEN x.page_no = 3 OR x.page_no % 511233 = 0 THEN 'SGAM' WHEN x.page_no = 6 OR x.page_no % 511238 = 0 THEN 'DCM' WHEN x.page_no = 7 OR x.page_no % 511239 = 0 THEN 'BCM' WHEN x.page_no IS NOT NULL THEN '*' ELSE NULL END, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id, x.request_mode, x.request_status, x.session_id, x.request_id, CASE WHEN COALESCE(x.object_id, x.file_id, x.hobt_id, x.allocation_unit_id, x.index_id, x.schema_id, x.principal_id) IS NULL THEN NULLIF(resource_description, '') ELSE NULL END ) AS y ON y.session_id = s.session_id AND y.request_id = s.request_id OPTION (HASH GROUP);
--Disable unnecessary autostats on the table CREATE STATISTICS s_database_name ON #locks (database_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_object_id ON #locks (object_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_hobt_id ON #locks (hobt_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_allocation_unit_id ON #locks (allocation_unit_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_index_id ON #locks (index_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_schema_id ON #locks (schema_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_principal_id ON #locks (principal_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_id ON #locks (request_id) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_start_time ON #locks (start_time) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_resource_type ON #locks (resource_type) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_object_name ON #locks (object_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_schema_name ON #locks (schema_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_page_type ON #locks (page_type) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_mode ON #locks (request_mode) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_request_status ON #locks (request_status) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_resource_description ON #locks (resource_description) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_index_name ON #locks (index_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_principal_name ON #locks (principal_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; END;
DECLARE @sql VARCHAR(MAX), @sql_n NVARCHAR(MAX);
SET @sql = CONVERT(VARCHAR(MAX), '') + 'DECLARE @blocker BIT; ' + 'SET @blocker = 0; ' + 'DECLARE @i INT; ' + 'SET @i = 2147483647; ' + '' + 'DECLARE @sessions TABLE ' + '( ' + 'session_id SMALLINT NOT NULL, ' + 'request_id INT NOT NULL, ' + 'login_time DATETIME, ' + 'last_request_end_time DATETIME, ' + 'status VARCHAR(30), ' + 'statement_start_offset INT, ' + 'statement_end_offset INT, ' + 'sql_handle BINARY(20), ' + 'host_name NVARCHAR(128), ' + 'login_name NVARCHAR(128), ' + 'program_name NVARCHAR(128), ' + 'database_id SMALLINT, ' + 'memory_usage INT, ' + 'open_tran_count SMALLINT, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'wait_type NVARCHAR(32), ' + 'wait_resource NVARCHAR(256), ' + 'wait_time BIGINT, ' ELSE '' END + 'blocked SMALLINT, ' + 'is_user_process BIT, ' + 'cmd VARCHAR(32), ' + 'PRIMARY KEY CLUSTERED (session_id, request_id) WITH (IGNORE_DUP_KEY = ON) ' + '); ' + '' + 'DECLARE @blockers TABLE ' + '( ' + 'session_id INT NOT NULL PRIMARY KEY ' + '); ' + '' + 'BLOCKERS:; ' + '' + 'INSERT @sessions ' + '( ' + 'session_id, ' + 'request_id, ' + 'login_time, ' + 'last_request_end_time, ' + 'status, ' + 'statement_start_offset, ' + 'statement_end_offset, ' + 'sql_handle, ' + 'host_name, ' + 'login_name, ' + 'program_name, ' + 'database_id, ' + 'memory_usage, ' + 'open_tran_count, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'wait_type, ' + 'wait_resource, ' + 'wait_time, ' ELSE '' END + 'blocked, ' + 'is_user_process, ' + 'cmd ' + ') ' + 'SELECT TOP(@i) ' + 'spy.session_id, ' + 'spy.request_id, ' + 'spy.login_time, ' + 'spy.last_request_end_time, ' + 'spy.status, ' + 'spy.statement_start_offset, ' + 'spy.statement_end_offset, ' + 'spy.sql_handle, ' + 'spy.host_name, ' + 'spy.login_name, ' + 'spy.program_name, ' + 'spy.database_id, ' + 'spy.memory_usage, ' + 'spy.open_tran_count, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'spy.wait_type, ' + 'CASE ' + 'WHEN ' + 'spy.wait_type LIKE N''PAGE%LATCH_%'' ' + 'OR spy.wait_type = N''CXPACKET'' ' + 'OR spy.wait_type LIKE N''LATCH[_]%'' ' + 'OR spy.wait_type = N''OLEDB'' THEN ' + 'spy.wait_resource ' + 'ELSE ' + 'NULL ' + 'END AS wait_resource, ' + 'spy.wait_time, ' ELSE '' END + 'spy.blocked, ' + 'spy.is_user_process, ' + 'spy.cmd ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'spx.*, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'ROW_NUMBER() OVER ' + '( ' + 'PARTITION BY ' + 'spx.session_id, ' + 'spx.request_id ' + 'ORDER BY ' + 'CASE ' + 'WHEN spx.wait_type LIKE N''LCK[_]%'' THEN 1 ' + 'ELSE 99 ' + 'END, ' + 'spx.wait_time DESC, ' + 'spx.blocked DESC ' + ') AS r ' ELSE '1 AS r ' END + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'sp0.session_id, ' + 'sp0.request_id, ' + 'sp0.login_time, ' + 'sp0.last_request_end_time, ' + 'LOWER(sp0.status) AS status, ' + 'CASE ' + 'WHEN sp0.cmd = ''CREATE INDEX'' THEN 0 ' + 'ELSE sp0.stmt_start ' + 'END AS statement_start_offset, ' + 'CASE ' + 'WHEN sp0.cmd = N''CREATE INDEX'' THEN -1 ' + 'ELSE COALESCE(NULLIF(sp0.stmt_end, 0), -1) ' + 'END AS statement_end_offset, ' + 'sp0.sql_handle, ' + 'sp0.host_name, ' + 'sp0.login_name, ' + 'sp0.program_name, ' + 'sp0.database_id, ' + 'sp0.memory_usage, ' + 'sp0.open_tran_count, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'CASE ' + 'WHEN sp0.wait_time > 0 AND sp0.wait_type <> N''CXPACKET'' THEN sp0.wait_type ' + 'ELSE NULL ' + 'END AS wait_type, ' + 'CASE ' + 'WHEN sp0.wait_time > 0 AND sp0.wait_type <> N''CXPACKET'' THEN sp0.wait_resource ' + 'ELSE NULL ' + 'END AS wait_resource, ' + 'CASE ' + 'WHEN sp0.wait_type <> N''CXPACKET'' THEN sp0.wait_time ' + 'ELSE 0 ' + 'END AS wait_time, ' ELSE '' END + 'sp0.blocked, ' + 'sp0.is_user_process, ' + 'sp0.cmd ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'sp1.session_id, ' + 'sp1.request_id, ' + 'sp1.login_time, ' + 'sp1.last_request_end_time, ' + 'sp1.status, ' + 'sp1.cmd, ' + 'sp1.stmt_start, ' + 'sp1.stmt_end, ' + 'MAX(NULLIF(sp1.sql_handle, 0x00)) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS sql_handle, ' + 'sp1.host_name, ' + 'MAX(sp1.login_name) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS login_name, ' + 'sp1.program_name, ' + 'sp1.database_id, ' + 'MAX(sp1.memory_usage) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS memory_usage, ' + 'MAX(sp1.open_tran_count) OVER (PARTITION BY sp1.session_id, sp1.request_id) AS open_tran_count, ' + 'sp1.wait_type, ' + 'sp1.wait_resource, ' + 'sp1.wait_time, ' + 'sp1.blocked, ' + 'sp1.hostprocess, ' + 'sp1.is_user_process ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'sp2.spid AS session_id, ' + 'CASE sp2.status ' + 'WHEN ''sleeping'' THEN CONVERT(INT, 0) ' + 'ELSE sp2.request_id ' + 'END AS request_id, ' + 'MAX(sp2.login_time) AS login_time, ' + 'MAX(sp2.last_batch) AS last_request_end_time, ' + 'MAX(CONVERT(VARCHAR(30), RTRIM(sp2.status)) COLLATE Latin1_General_Bin2) AS status, ' + 'MAX(CONVERT(VARCHAR(32), RTRIM(sp2.cmd)) COLLATE Latin1_General_Bin2) AS cmd, ' + 'MAX(sp2.stmt_start) AS stmt_start, ' + 'MAX(sp2.stmt_end) AS stmt_end, ' + 'MAX(sp2.sql_handle) AS sql_handle, ' + 'MAX(CONVERT(sysname, RTRIM(sp2.hostname)) COLLATE SQL_Latin1_General_CP1_CI_AS) AS host_name, ' + 'MAX(CONVERT(sysname, RTRIM(sp2.loginame)) COLLATE SQL_Latin1_General_CP1_CI_AS) AS login_name, ' + 'MAX ' + '( ' + 'CASE ' + 'WHEN blk.queue_id IS NOT NULL THEN ' + 'N''Service Broker ' + 'database_id: '' + CONVERT(NVARCHAR, blk.database_id) + ' + 'N'' queue_id: '' + CONVERT(NVARCHAR, blk.queue_id)' + 'ELSE ' + 'CONVERT ' + '( ' + 'sysname, ' + 'RTRIM(sp2.program_name) ' + ') ' + 'END COLLATE SQL_Latin1_General_CP1_CI_AS ' + ') AS program_name, ' + 'MAX(sp2.dbid) AS database_id, ' + 'MAX(sp2.memusage) AS memory_usage, ' + 'MAX(sp2.open_tran) AS open_tran_count, ' + 'RTRIM(sp2.lastwaittype) AS wait_type, ' + 'RTRIM(sp2.waitresource) AS wait_resource, ' + 'MAX(sp2.waittime) AS wait_time, ' + 'COALESCE(NULLIF(sp2.blocked, sp2.spid), 0) AS blocked, ' + 'MAX ' + '( ' + 'CASE ' + 'WHEN blk.session_id = sp2.spid THEN ' + '''blocker'' ' + 'ELSE ' + 'RTRIM(sp2.hostprocess) ' + 'END ' + ') AS hostprocess, ' + 'CONVERT ' + '( ' + 'BIT, ' + 'MAX ' + '( ' + 'CASE ' + 'WHEN sp2.hostprocess > '''' THEN ' + '1 ' + 'ELSE ' + '0 ' + 'END ' + ') ' + ') AS is_user_process ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'session_id, ' + 'CONVERT(INT, NULL) AS queue_id, ' + 'CONVERT(INT, NULL) AS database_id ' + 'FROM @blockers ' + '' + 'UNION ALL ' + '' + 'SELECT TOP(@i) ' + 'CONVERT(SMALLINT, 0), ' + 'CONVERT(INT, NULL) AS queue_id, ' + 'CONVERT(INT, NULL) AS database_id ' + 'WHERE ' + '@blocker = 0 ' + '' + 'UNION ALL ' + '' + 'SELECT TOP(@i) ' + 'CONVERT(SMALLINT, spid), ' + 'queue_id, ' + 'database_id ' + 'FROM sys.dm_broker_activated_tasks ' + 'WHERE ' + '@blocker = 0 ' + ') AS blk ' + 'INNER JOIN sys.sysprocesses AS sp2 ON ' + 'sp2.spid = blk.session_id ' + 'OR ' + '( ' + 'blk.session_id = 0 ' + 'AND @blocker = 0 ' + ') ' + CASE WHEN ( @get_task_info = 0 AND @find_block_leaders = 0 ) THEN 'WHERE ' + 'sp2.ecid = 0 ' ELSE '' END + 'GROUP BY ' + 'sp2.spid, ' + 'CASE sp2.status ' + 'WHEN ''sleeping'' THEN CONVERT(INT, 0) ' + 'ELSE sp2.request_id ' + 'END, ' + 'RTRIM(sp2.lastwaittype), ' + 'RTRIM(sp2.waitresource), ' + 'COALESCE(NULLIF(sp2.blocked, sp2.spid), 0) ' + ') AS sp1 ' + ') AS sp0 ' + 'WHERE ' + '@blocker = 1 ' + 'OR ' + '(1=1 ' + --inclusive filter CASE WHEN @filter <> '' THEN CASE @filter_type WHEN 'session' THEN CASE WHEN CONVERT(SMALLINT, @filter) <> 0 THEN 'AND sp0.session_id = CONVERT(SMALLINT, @filter) ' ELSE '' END WHEN 'program' THEN 'AND sp0.program_name LIKE @filter ' WHEN 'login' THEN 'AND sp0.login_name LIKE @filter ' WHEN 'host' THEN 'AND sp0.host_name LIKE @filter ' WHEN 'database' THEN 'AND DB_NAME(sp0.database_id) LIKE @filter ' ELSE '' END ELSE '' END + --exclusive filter CASE WHEN @not_filter <> '' THEN CASE @not_filter_type WHEN 'session' THEN CASE WHEN CONVERT(SMALLINT, @not_filter) <> 0 THEN 'AND sp0.session_id <> CONVERT(SMALLINT, @not_filter) ' ELSE '' END WHEN 'program' THEN 'AND sp0.program_name NOT LIKE @not_filter ' WHEN 'login' THEN 'AND sp0.login_name NOT LIKE @not_filter ' WHEN 'host' THEN 'AND sp0.host_name NOT LIKE @not_filter ' WHEN 'database' THEN 'AND DB_NAME(sp0.database_id) NOT LIKE @not_filter ' ELSE '' END ELSE '' END + CASE @show_own_spid WHEN 1 THEN '' ELSE 'AND sp0.session_id <> @@spid ' END + CASE WHEN @show_system_spids = 0 THEN 'AND sp0.hostprocess > '''' ' ELSE '' END + CASE @show_sleeping_spids WHEN 0 THEN 'AND sp0.status <> ''sleeping'' ' WHEN 1 THEN 'AND ' + '( ' + 'sp0.status <> ''sleeping'' ' + 'OR sp0.open_tran_count > 0 ' + ') ' ELSE '' END + ') ' + ') AS spx ' + ') AS spy ' + 'WHERE ' + 'spy.r = 1; ' + CASE @recursion WHEN 1 THEN 'IF @@ROWCOUNT > 0 ' + 'BEGIN; ' + 'INSERT @blockers ' + '( ' + 'session_id ' + ') ' + 'SELECT TOP(@i) ' + 'blocked ' + 'FROM @sessions ' + 'WHERE ' + 'NULLIF(blocked, 0) IS NOT NULL ' + '' + 'EXCEPT ' + '' + 'SELECT TOP(@i) ' + 'session_id ' + 'FROM @sessions; ' + '' + CASE WHEN ( @get_task_info > 0 OR @find_block_leaders = 1 ) THEN 'IF @@ROWCOUNT > 0 ' + 'BEGIN; ' + 'SET @blocker = 1; ' + 'GOTO BLOCKERS; ' + 'END; ' ELSE '' END + 'END; ' ELSE '' END + 'SELECT TOP(@i) ' + '@recursion AS recursion, ' + 'x.session_id, ' + 'x.request_id, ' + 'DENSE_RANK() OVER ' + '( ' + 'ORDER BY ' + 'x.session_id ' + ') AS session_number, ' + CASE WHEN @output_column_list LIKE '%|[dd hh:mm:ss.mss|]%' ESCAPE '|' THEN 'x.elapsed_time ' ELSE '0 ' END + 'AS elapsed_time, ' + CASE WHEN ( @output_column_list LIKE '%|[dd hh:mm:ss.mss (avg)|]%' ESCAPE '|' OR @output_column_list LIKE '%|[avg_elapsed_time|]%' ESCAPE '|' ) AND @recursion = 1 THEN 'x.avg_elapsed_time / 1000 ' ELSE 'NULL ' END + 'AS avg_elapsed_time, ' + CASE WHEN @output_column_list LIKE '%|[physical_io|]%' ESCAPE '|' OR @output_column_list LIKE '%|[physical_io_delta|]%' ESCAPE '|' THEN 'x.physical_io ' ELSE 'NULL ' END + 'AS physical_io, ' + CASE WHEN @output_column_list LIKE '%|[reads|]%' ESCAPE '|' OR @output_column_list LIKE '%|[reads_delta|]%' ESCAPE '|' THEN 'x.reads ' ELSE '0 ' END + 'AS reads, ' + CASE WHEN @output_column_list LIKE '%|[physical_reads|]%' ESCAPE '|' OR @output_column_list LIKE '%|[physical_reads_delta|]%' ESCAPE '|' THEN 'x.physical_reads ' ELSE '0 ' END + 'AS physical_reads, ' + CASE WHEN @output_column_list LIKE '%|[writes|]%' ESCAPE '|' OR @output_column_list LIKE '%|[writes_delta|]%' ESCAPE '|' THEN 'x.writes ' ELSE '0 ' END + 'AS writes, ' + CASE WHEN @output_column_list LIKE '%|[tempdb_allocations|]%' ESCAPE '|' OR @output_column_list LIKE '%|[tempdb_allocations_delta|]%' ESCAPE '|' THEN 'x.tempdb_allocations ' ELSE '0 ' END + 'AS tempdb_allocations, ' + CASE WHEN @output_column_list LIKE '%|[tempdb_current|]%' ESCAPE '|' OR @output_column_list LIKE '%|[tempdb_current_delta|]%' ESCAPE '|' THEN 'x.tempdb_current ' ELSE '0 ' END + 'AS tempdb_current, ' + CASE WHEN @output_column_list LIKE '%|[CPU|]%' ESCAPE '|' OR @output_column_list LIKE '%|[CPU_delta|]%' ESCAPE '|' THEN 'x.CPU ' ELSE '0 ' END + 'AS CPU, ' + CASE WHEN @output_column_list LIKE '%|[CPU_delta|]%' ESCAPE '|' AND @get_task_info = 2 THEN 'x.thread_CPU_snapshot ' ELSE '0 ' END + 'AS thread_CPU_snapshot, ' + CASE WHEN @output_column_list LIKE '%|[context_switches|]%' ESCAPE '|' OR @output_column_list LIKE '%|[context_switches_delta|]%' ESCAPE '|' THEN 'x.context_switches ' ELSE 'NULL ' END + 'AS context_switches, ' + CASE WHEN @output_column_list LIKE '%|[used_memory|]%' ESCAPE '|' OR @output_column_list LIKE '%|[used_memory_delta|]%' ESCAPE '|' THEN 'x.used_memory ' ELSE '0 ' END + 'AS used_memory, ' + CASE WHEN @output_column_list LIKE '%|[tasks|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.tasks ' ELSE 'NULL ' END + 'AS tasks, ' + CASE WHEN ( @output_column_list LIKE '%|[status|]%' ESCAPE '|' OR @output_column_list LIKE '%|[sql_command|]%' ESCAPE '|' ) AND @recursion = 1 THEN 'x.status ' ELSE ''''' ' END + 'AS status, ' + CASE WHEN @output_column_list LIKE '%|[wait_info|]%' ESCAPE '|' AND @recursion = 1 THEN CASE @get_task_info WHEN 2 THEN 'COALESCE(x.task_wait_info, x.sys_wait_info) ' ELSE 'x.sys_wait_info ' END ELSE 'NULL ' END + 'AS wait_info, ' + CASE WHEN ( @output_column_list LIKE '%|[tran_start_time|]%' ESCAPE '|' OR @output_column_list LIKE '%|[tran_log_writes|]%' ESCAPE '|' ) AND @recursion = 1 THEN 'x.transaction_id ' ELSE 'NULL ' END + 'AS transaction_id, ' + CASE WHEN @output_column_list LIKE '%|[open_tran_count|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.open_tran_count ' ELSE 'NULL ' END + 'AS open_tran_count, ' + CASE WHEN @output_column_list LIKE '%|[sql_text|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.sql_handle ' ELSE 'NULL ' END + 'AS sql_handle, ' + CASE WHEN ( @output_column_list LIKE '%|[sql_text|]%' ESCAPE '|' OR @output_column_list LIKE '%|[query_plan|]%' ESCAPE '|' ) AND @recursion = 1 THEN 'x.statement_start_offset ' ELSE 'NULL ' END + 'AS statement_start_offset, ' + CASE WHEN ( @output_column_list LIKE '%|[sql_text|]%' ESCAPE '|' OR @output_column_list LIKE '%|[query_plan|]%' ESCAPE '|' ) AND @recursion = 1 THEN 'x.statement_end_offset ' ELSE 'NULL ' END + 'AS statement_end_offset, ' + 'NULL AS sql_text, ' + CASE WHEN @output_column_list LIKE '%|[query_plan|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.plan_handle ' ELSE 'NULL ' END + 'AS plan_handle, ' + CASE WHEN @output_column_list LIKE '%|[blocking_session_id|]%' ESCAPE '|' AND @recursion = 1 THEN 'NULLIF(x.blocking_session_id, 0) ' ELSE 'NULL ' END + 'AS blocking_session_id, ' + CASE WHEN @output_column_list LIKE '%|[percent_complete|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.percent_complete ' ELSE 'NULL ' END + 'AS percent_complete, ' + CASE WHEN @output_column_list LIKE '%|[host_name|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.host_name ' ELSE ''''' ' END + 'AS host_name, ' + CASE WHEN @output_column_list LIKE '%|[login_name|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.login_name ' ELSE ''''' ' END + 'AS login_name, ' + CASE WHEN @output_column_list LIKE '%|[database_name|]%' ESCAPE '|' AND @recursion = 1 THEN 'DB_NAME(x.database_id) ' ELSE 'NULL ' END + 'AS database_name, ' + CASE WHEN @output_column_list LIKE '%|[program_name|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.program_name ' ELSE ''''' ' END + 'AS program_name, ' + CASE WHEN @output_column_list LIKE '%|[additional_info|]%' ESCAPE '|' AND @recursion = 1 THEN '( ' + 'SELECT TOP(@i) ' + 'text_size, ' + 'language, ' + 'date_format, ' + 'date_first, ' + 'CASE quoted_identifier ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS quoted_identifier, ' + 'CASE arithabort ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS arithabort, ' + 'CASE ansi_null_dflt_on ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS ansi_null_dflt_on, ' + 'CASE ansi_defaults ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS ansi_defaults, ' + 'CASE ansi_warnings ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS ansi_warnings, ' + 'CASE ansi_padding ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS ansi_padding, ' + 'CASE ansi_nulls ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS ansi_nulls, ' + 'CASE concat_null_yields_null ' + 'WHEN 0 THEN ''OFF'' ' + 'WHEN 1 THEN ''ON'' ' + 'END AS concat_null_yields_null, ' + 'CASE transaction_isolation_level ' + 'WHEN 0 THEN ''Unspecified'' ' + 'WHEN 1 THEN ''ReadUncomitted'' ' + 'WHEN 2 THEN ''ReadCommitted'' ' + 'WHEN 3 THEN ''Repeatable'' ' + 'WHEN 4 THEN ''Serializable'' ' + 'WHEN 5 THEN ''Snapshot'' ' + 'END AS transaction_isolation_level, ' + 'lock_timeout, ' + 'deadlock_priority, ' + 'row_count, ' + 'command_type, ' + CASE WHEN @output_column_list LIKE '%|[program_name|]%' ESCAPE '|' THEN '( ' + 'SELECT TOP(1) ' + 'CONVERT(uniqueidentifier, CONVERT(XML, '''').value(''xs:hexBinary( substring(sql:column("agent_info.job_id_string"), 0) )'', ''binary(16)'')) AS job_id, ' + 'agent_info.step_id, ' + '( ' + 'SELECT TOP(1) ' + 'NULL ' + 'FOR XML ' + 'PATH(''job_name''), ' + 'TYPE ' + '), ' + '( ' + 'SELECT TOP(1) ' + 'NULL ' + 'FOR XML ' + 'PATH(''step_name''), ' + 'TYPE ' + ') ' + 'FROM ' + '( ' + 'SELECT TOP(1) ' + 'SUBSTRING(x.program_name, CHARINDEX(''0x'', x.program_name) + 2, 32) AS job_id_string, ' + 'SUBSTRING(x.program_name, CHARINDEX('': Step '', x.program_name) + 7, CHARINDEX('')'', x.program_name, CHARINDEX('': Step '', x.program_name)) - (CHARINDEX('': Step '', x.program_name) + 7)) AS step_id ' + 'WHERE '+ 'x.program_name LIKE N''SQLAgent - TSQL JobStep (Job 0x%'' ' + ') AS agent_info ' + 'FOR XML ' + 'PATH(''agent_job_info''), ' + 'TYPE ' + ') ' ELSE '' END + CASE WHEN @get_task_info = 2 THEN ', CONVERT(XML, x.block_info) AS block_info ' ELSE '' END + 'FOR XML ' + 'PATH(''additional_info''), ' + 'TYPE ' + ') ' ELSE 'NULL ' END + 'AS additional_info, ' + 'x.start_time, ' + CASE WHEN @output_column_list LIKE '%|[login_time|]%' ESCAPE '|' AND @recursion = 1 THEN 'x.login_time ' ELSE 'NULL ' END + 'AS login_time, ' + 'x.last_request_start_time ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'y.*, ' + 'CASE ' + --if there are more than 24 days, return a negative number of seconds rather than --positive milliseconds, in order to avoid overflow errors 'WHEN DATEDIFF(day, y.start_time, GETDATE()) > 24 THEN ' + 'DATEDIFF(second, GETDATE(), y.start_time) ' + 'ELSE DATEDIFF(ms, y.start_time, GETDATE()) ' + 'END AS elapsed_time, ' + 'COALESCE(tempdb_info.tempdb_allocations, 0) AS tempdb_allocations, ' + 'COALESCE ' + '( ' + 'CASE ' + 'WHEN tempdb_info.tempdb_current < 0 THEN 0 ' + 'ELSE tempdb_info.tempdb_current ' + 'END, ' + '0 ' + ') AS tempdb_current, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'N''('' + CONVERT(NVARCHAR, y.wait_duration_ms) + N''ms)'' + ' + 'y.wait_type + ' + --TODO: What else can be pulled from the resource_description? 'CASE ' + 'WHEN y.wait_type LIKE N''PAGE%LATCH_%'' THEN ' + 'N'':'' + ' + --database name 'COALESCE(DB_NAME(CONVERT(INT, LEFT(y.resource_description, CHARINDEX(N'':'', y.resource_descriptio