''OLEDB'' ' + 'AND y.resource_description LIKE N''%(SPID=%)'' THEN ' + 'N''['' + LEFT(y.resource_description, CHARINDEX(N''(SPID='', y.resource_description) - 2) + ' + 'N'':'' + SUBSTRING(y.resource_description, CHARINDEX(N''(SPID='', y.resource_description) + 6, CHARINDEX(N'')'', y.resource_description, (CHARINDEX(N''(SPID='', y.resource_description) + 6)) - (CHARINDEX(N''(SPID='', y.resource_description) + 6)) + '']'' ' + 'ELSE N'''' ' + 'END COLLATE Latin1_General_Bin2 AS sys_wait_info, ' ELSE '' END + CASE WHEN @get_task_info = 2 THEN 'tasks.physical_io, ' + 'tasks.context_switches, ' + 'tasks.tasks, ' + 'tasks.block_info, ' + 'tasks.wait_info AS task_wait_info, ' + 'tasks.thread_CPU_snapshot, ' ELSE '' END + CASE WHEN NOT (@get_avg_time = 1 AND @recursion = 1) THEN 'CONVERT(INT, NULL) ' ELSE 'qs.total_elapsed_time / qs.execution_count ' END + 'AS avg_elapsed_time ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'sp.session_id, ' + 'sp.request_id, ' + 'COALESCE(r.logical_reads, s.logical_reads) AS reads, ' + 'COALESCE(r.reads, s.reads) AS physical_reads, ' + 'COALESCE(r.writes, s.writes) AS writes, ' + 'COALESCE(r.CPU_time, s.CPU_time) AS CPU, ' + 'sp.memory_usage + COALESCE(r.granted_query_memory, 0) AS used_memory, ' + 'LOWER(sp.status) AS status, ' + 'COALESCE(r.sql_handle, sp.sql_handle) AS sql_handle, ' + 'COALESCE(r.statement_start_offset, sp.statement_start_offset) AS statement_start_offset, ' + 'COALESCE(r.statement_end_offset, sp.statement_end_offset) AS statement_end_offset, ' + CASE WHEN ( @get_task_info <> 0 OR @find_block_leaders = 1 ) THEN 'sp.wait_type COLLATE Latin1_General_Bin2 AS wait_type, ' + 'sp.wait_resource COLLATE Latin1_General_Bin2 AS resource_description, ' + 'sp.wait_time AS wait_duration_ms, ' ELSE '' END + 'NULLIF(sp.blocked, 0) AS blocking_session_id, ' + 'r.plan_handle, ' + 'NULLIF(r.percent_complete, 0) AS percent_complete, ' + 'sp.host_name, ' + 'sp.login_name, ' + 'sp.program_name, ' + 'COALESCE(r.text_size, s.text_size) AS text_size, ' + 'COALESCE(r.language, s.language) AS language, ' + 'COALESCE(r.date_format, s.date_format) AS date_format, ' + 'COALESCE(r.date_first, s.date_first) AS date_first, ' + 'COALESCE(r.quoted_identifier, s.quoted_identifier) AS quoted_identifier, ' + 'COALESCE(r.arithabort, s.arithabort) AS arithabort, ' + 'COALESCE(r.ansi_null_dflt_on, s.ansi_null_dflt_on) AS ansi_null_dflt_on, ' + 'COALESCE(r.ansi_defaults, s.ansi_defaults) AS ansi_defaults, ' + 'COALESCE(r.ansi_warnings, s.ansi_warnings) AS ansi_warnings, ' + 'COALESCE(r.ansi_padding, s.ansi_padding) AS ansi_padding, ' + 'COALESCE(r.ansi_nulls, s.ansi_nulls) AS ansi_nulls, ' + 'COALESCE(r.concat_null_yields_null, s.concat_null_yields_null) AS concat_null_yields_null, ' + 'COALESCE(r.transaction_isolation_level, s.transaction_isolation_level) AS transaction_isolation_level, ' + 'COALESCE(r.lock_timeout, s.lock_timeout) AS lock_timeout, ' + 'COALESCE(r.deadlock_priority, s.deadlock_priority) AS deadlock_priority, ' + 'COALESCE(r.row_count, s.row_count) AS row_count, ' + 'COALESCE(r.command, sp.cmd) AS command_type, ' + 'COALESCE ' + '( ' + 'CASE ' + 'WHEN ' + '( ' + 's.is_user_process = 0 ' + 'AND r.total_elapsed_time >= 0 ' + ') THEN ' + 'DATEADD ' + '( ' + 'ms, ' + '1000 * (DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())) / 500) - DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())), ' + 'DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE()) ' + ') ' + 'END, ' + 'NULLIF(COALESCE(r.start_time, sp.last_request_end_time), CONVERT(DATETIME, ''19000101'', 112)), ' + '( ' + 'SELECT TOP(1) ' + 'DATEADD(second, -(ms_ticks / 1000), GETDATE()) ' + 'FROM sys.dm_os_sys_info ' + ') ' + ') AS start_time, ' + 'sp.login_time, ' + 'CASE ' + 'WHEN s.is_user_process = 1 THEN ' + 's.last_request_start_time ' + 'ELSE ' + 'COALESCE ' + '( ' + 'DATEADD ' + '( ' + 'ms, ' + '1000 * (DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())) / 500) - DATEPART(ms, DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE())), ' + 'DATEADD(second, -(r.total_elapsed_time / 1000), GETDATE()) ' + '), ' + 's.last_request_start_time ' + ') ' + 'END AS last_request_start_time, ' + 'r.transaction_id, ' + 'sp.database_id, ' + 'sp.open_tran_count ' + 'FROM @sessions AS sp ' + 'LEFT OUTER LOOP JOIN sys.dm_exec_sessions AS s ON ' + 's.session_id = sp.session_id ' + 'AND s.login_time = sp.login_time ' + 'LEFT OUTER LOOP JOIN sys.dm_exec_requests AS r ON ' + 'sp.status <> ''sleeping'' ' + 'AND r.session_id = sp.session_id ' + 'AND r.request_id = sp.request_id ' + 'AND ' + '( ' + '( ' + 's.is_user_process = 0 ' + 'AND sp.is_user_process = 0 ' + ') ' + 'OR ' + '( ' + 'r.start_time = s.last_request_start_time ' + 'AND s.last_request_end_time = sp.last_request_end_time ' + ') ' + ') ' + ') AS y ' + CASE WHEN @get_task_info = 2 THEN CONVERT(VARCHAR(MAX), '') + 'LEFT OUTER HASH JOIN ' + '( ' + 'SELECT TOP(@i) ' + 'task_nodes.task_node.value(''(session_id/text())[1]'', ''SMALLINT'') AS session_id, ' + 'task_nodes.task_node.value(''(request_id/text())[1]'', ''INT'') AS request_id, ' + 'task_nodes.task_node.value(''(physical_io/text())[1]'', ''BIGINT'') AS physical_io, ' + 'task_nodes.task_node.value(''(context_switches/text())[1]'', ''BIGINT'') AS context_switches, ' + 'task_nodes.task_node.value(''(tasks/text())[1]'', ''INT'') AS tasks, ' + 'task_nodes.task_node.value(''(block_info/text())[1]'', ''NVARCHAR(4000)'') AS block_info, ' + 'task_nodes.task_node.value(''(waits/text())[1]'', ''NVARCHAR(4000)'') AS wait_info, ' + 'task_nodes.task_node.value(''(thread_CPU_snapshot/text())[1]'', ''BIGINT'') AS thread_CPU_snapshot ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'CONVERT ' + '( ' + 'XML, ' + 'REPLACE ' + '( ' + 'CONVERT(NVARCHAR(MAX), tasks_raw.task_xml_raw) COLLATE Latin1_General_Bin2, ' + 'N''</waits></tasks><tasks><waits>'', ' + 'N'', '' ' + ') ' + ') AS task_xml ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'CASE waits.r ' + 'WHEN 1 THEN waits.session_id ' + 'ELSE N