CHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + 'N'''' ' + ') AS [waits] ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'w1.*, ' + 'ROW_NUMBER() OVER ' + '( ' + 'PARTITION BY ' + 'w1.session_id, ' + 'w1.request_id ' + 'ORDER BY ' + 'w1.block_info DESC, ' + 'w1.num_waits DESC, ' + 'w1.wait_type ' + ') AS r ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'task_info.session_id, ' + 'task_info.request_id, ' + 'task_info.physical_io, ' + 'task_info.context_switches, ' + 'task_info.thread_CPU_snapshot, ' + 'task_info.num_tasks AS tasks, ' + 'CASE ' + 'WHEN task_info.runnable_time IS NOT NULL THEN ' + '''RUNNABLE'' ' + 'ELSE ' + 'wt2.wait_type ' + 'END AS wait_type, ' + 'NULLIF(COUNT(COALESCE(task_info.runnable_time, wt2.waiting_task_address)), 0) AS num_waits, ' + 'MIN(COALESCE(task_info.runnable_time, wt2.wait_duration_ms)) AS min_wait_time, ' + 'AVG(COALESCE(task_info.runnable_time, wt2.wait_duration_ms)) AS avg_wait_time, ' + 'MAX(COALESCE(task_info.runnable_time, wt2.wait_duration_ms)) AS max_wait_time, ' + 'MAX(wt2.block_info) AS block_info ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 't.session_id, ' + 't.request_id, ' + 'SUM(CONVERT(BIGINT, t.pending_io_count)) OVER (PARTITION BY t.session_id, t.request_id) AS physical_io, ' + 'SUM(CONVERT(BIGINT, t.context_switches_count)) OVER (PARTITION BY t.session_id, t.request_id) AS context_switches, ' + CASE WHEN @output_column_list LIKE '%|[CPU_delta|]%' ESCAPE '|' THEN 'SUM(tr.usermode_time + tr.kernel_time) OVER (PARTITION BY t.session_id, t.request_id) ' ELSE 'CONVERT(BIGINT, NULL) ' END + ' AS thread_CPU_snapshot, ' + 'COUNT(*) OVER (PARTITION BY t.session_id, t.request_id) AS num_tasks, ' + 't.task_address, ' + 't.task_state, ' + 'CASE ' + 'WHEN ' + 't.task_state = ''RUNNABLE'' ' + 'AND w.runnable_time > 0 THEN ' + 'w.runnable_time ' + 'ELSE ' + 'NULL ' + 'END AS runnable_time ' + 'FROM sys.dm_os_tasks AS t ' + 'CROSS APPLY ' + '( ' + 'SELECT TOP(1) ' + 'sp2.session_id ' + 'FROM @sessions AS sp2 ' + 'WHERE ' + 'sp2.session_id = t.session_id ' + 'AND sp2.request_id = t.request_id ' + 'AND sp2.status <> ''sleeping'' ' + ') AS sp20 ' + 'LEFT OUTER HASH JOIN ' + '( ' + 'SELECT TOP(@i) ' + '( ' + 'SELECT TOP(@i) ' + 'ms_ticks ' + 'FROM sys.dm_os_sys_info ' + ') - ' + 'w0.wait_resumed_ms_ticks AS runnable_time, ' + 'w0.worker_address, ' + 'w0.thread_address, ' + 'w0.task_bound_ms_ticks ' + 'FROM sys.dm_os_workers AS w0 ' + 'WHERE ' + 'w0.state = ''RUNNABLE'' ' + 'OR @first_collection_ms_ticks >= w0.task_bound_ms_ticks ' + ') AS w ON ' + 'w.worker_address = t.worker_address ' + CASE WHEN @output_column_list LIKE '%|[CPU_delta|]%' ESCAPE '|' THEN 'LEFT OUTER HASH JOIN sys.dm_os_threads AS tr ON ' + 'tr.thread_address = w.thread_address ' + 'AND @first_collection_ms_ticks >= w.task_bound_ms_ticks ' ELSE '' END + ') AS task_info ' + 'LEFT OUTER HASH JOIN ' + '( ' + 'SELECT TOP(@i) ' + 'wt1.wait_type, ' + 'wt1.waiting_task_address, ' + 'MAX(wt1.wait_duration_ms) AS wait_duration_ms, ' + 'MAX(wt1.block_info) AS block_info ' + 'FROM ' + '( ' + 'SELECT DISTINCT TOP(@i) ' + 'wt.wait_type + ' + --TODO: What else can be pulled from the resource_description? 'CASE ' + 'WHEN wt.wait_type LIKE N''PAGE%LATCH_%'' THEN ' + ''':'' + ' + --database name 'COALESCE(DB_NAME(CONVERT(INT, LEFT(wt.resource_description, CHARINDEX(N'':'', wt.resource_description) - 1))), N''(null)'') + ' + 'N'':'' + ' + --file id 'SUBSTRING(wt.resource_description, CHARINDEX(N'':'', wt.resource_description) + 1, LEN(wt.resource_description) - CHARINDEX(N'':'', REVERSE(wt.resource_description)) - CHARINDEX(N'':'', wt.resource_description)) + ' + --page # for special pages 'N''('' + ' + 'CASE ' + 'WHEN ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) = 1 OR ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) % 8088 = 0 THEN N''PFS'' ' + 'WHEN ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) = 2 OR ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) % 511232 = 0 THEN N''GAM'' ' + 'WHEN ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) = 3 OR ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) % 511233 = 0 THEN N''SGAM'' ' + 'WHEN ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) = 6 OR ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) % 511238 = 0 THEN N''DCM'' ' + 'WHEN ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) = 7 OR ' + 'CONVERT(INT, RIGHT(wt.resource_description, CHARINDEX(N'':'', REVERSE(wt.resource_description)) - 1)) % 511239 = 0 THEN N''BCM'' ' + 'ELSE N''*'' ' + 'END + ' + 'N'')'' ' + 'WHEN wt.wait_type = N''CXPACKET'' THEN ' + 'N'':'' + SUBSTRING(wt.resource_description, CHARINDEX(N''nodeId'', wt.resource_description) + 7, 4) ' + 'WHEN wt.wait_type LIKE N''LATCH[_]%'' THEN ' + 'N'' ['' + LEFT(wt.resource_description, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description), 0), LEN(wt.resource_description) + 1) - 1) + N'']'' ' + 'ELSE N'''' ' + 'END COLLATE Latin1_General_Bin2 AS wait_type, ' + 'CASE ' + 'WHEN ' + '( ' + 'wt.blocking_session_id IS NOT NULL ' + 'AND wt.wait_type LIKE N''LCK[_]%'' ' + ') THEN ' + '( ' + 'SELECT TOP(@i) ' + 'x.lock_type, ' + 'REPLACE ' + '( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'DB_NAME ' + '( ' + 'CONVERT ' + '( ' + 'INT, ' + 'SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''dbid='', wt.resource_description), 0) + 5, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description, CHARINDEX(N''dbid='', wt.resource_description) + 5), 0), LEN(wt.resource_description) + 1) - CHARINDEX(N''dbid='', wt.resource_description) - 5) ' + ') ' + '), ' + 'NCHAR(31),N''?''),NCHAR(30),N''?''),NCHAR(29),N''?''),NCHAR(28),N''?''),NCHAR(27),N''?''),NCHAR(26),N''?''),NCHAR(25),N''?''),NCHAR(24),N''?''),NCHAR(23),N''?''),NCHAR(22),N''?''), ' + 'NCHAR(21),N''?''),NCHAR(20),N''?''),NCHAR(19),N''?''),NCHAR(18),N''?''),NCHAR(17),N''?''),NCHAR(16),N''?''),NCHAR(15),N''?''),NCHAR(14),N''?''),NCHAR(12),N''?''), ' + 'NCHAR(11),N''?''),NCHAR(8),N''?''),NCHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + 'N'''' ' + ') AS database_name, ' + 'CASE x.lock_type ' + 'WHEN N''objectlock'' THEN SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''objid='', wt.resource_description), 0) + 6, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description, CHARINDEX(N''objid='', wt.resource_description) + 6), 0), LEN(wt.resource_description) + 1) - CHARINDEX(N''objid='', wt.resource_description) - 6) ' + 'ELSE NULL ' + 'END AS object_id, ' + 'CASE x.lock_type ' + 'WHEN N''filelock'' THEN ' + 'SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''fileid='', wt.resource_description), 0) + 7, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description, CHARINDEX(N''fileid='', wt.resource_description) + 7), 0), LEN(wt.resource_description) + 1) - CHARINDEX(N''fileid='', wt.resource_description) - 7) ' + 'ELSE NULL ' + 'END AS file_id, ' + 'CASE ' + 'WHEN x.lock_type in (N''pagelock'', N''extentlock'', N''ridlock'') THEN ' + 'SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''associatedObjectId='', wt.resource_description), 0) + 19, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description, CHARINDEX(N''associatedObjectId='', wt.resource_description) + 19), 0), LEN(wt.resource_description) + 1) - CHARINDEX(N''associatedObjectId='', wt.resource_description) - 19) ' + 'WHEN x.lock_type in (N''keylock'', N''hobtlock'', N''allocunitlock'') THEN ' + 'SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''hobtid='', wt.resource_description), 0) + 7, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description, CHARINDEX(N''hobtid='', wt.resource_description) + 7), 0), LEN(wt.resource_description) + 1) - CHARINDEX(N''hobtid='', wt.resource_description) - 7) ' + 'ELSE NULL ' + 'END AS hobt_id, ' + 'CASE x.lock_type ' + 'WHEN N''applicationlock'' THEN ' + 'SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''hash='', wt.resource_description), 0) + 5, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description, CHARINDEX(N''hash='', wt.resource_description) + 5), 0), LEN(wt.resource_description) + 1) - CHARINDEX(N''hash='', wt.resource_description) - 5) ' + 'ELSE NULL ' + 'END AS applock_hash, ' + 'CASE x.lock_type ' + 'WHEN N''metadatalock'' THEN ' + 'SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''subresource='', wt.resource_description), 0) + 12, COALESCE(NULLIF(CHARINDEX(N'' '', wt.resource_description, CHARINDEX(N''subresource='', wt.resource_description) + 12), 0), LEN(wt.resource_description) + 1) - CHARINDEX(N''subresource='', wt.resource_description) - 12) ' + 'ELSE NULL ' + 'END AS metadata_resource, ' + 'CASE x.lock_type ' + 'WHEN N''metadatalock'' THEN ' + 'SUBSTRING(wt.resource_description, NULLIF(CHARINDEX(N''classid='', wt.resource_description), 0) + 8, COALESCE(NULLIF(CHARINDEX(N'' dbid='', wt.resource_description) - CHARINDEX(N''classid='', wt.resource_description), 0), LEN(wt.resource_description) + 1) - 8) ' + 'ELSE NULL ' + 'END AS metadata_class_id ' + 'FROM ' + '( ' + 'SELECT TOP(1) ' + 'LEFT(wt.resource_description, CHARINDEX(N'' '', wt.resource_description) - 1) COLLATE Latin1_General_Bin2 AS lock_type ' + ') AS x ' + 'FOR XML ' + 'PATH('''') ' + ') ' + 'ELSE NULL ' + 'END AS block_info, ' + 'wt.wait_duration_ms, ' + 'wt.waiting_task_address ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'wt0.wait_type COLLATE Latin1_General_Bin2 AS wait_type, ' + 'wt0.resource_description COLLATE Latin1_General_Bin2 AS resource_description, ' + 'wt0.wait_duration_ms, ' + 'wt0.waiting_task_address, ' + 'CASE ' + 'WHEN wt0.blocking_session_id = p.blocked THEN wt0.blocking_session_id ' + 'ELSE NULL ' + 'END AS blocking_session_id ' + 'FROM sys.dm_os_waiting_tasks AS wt0 ' + 'CROSS APPLY ' + '( ' + 'SELECT TOP(1)' + 's0.blocked ' + 'FROM @sessions AS s0 ' + 'WHERE ' + 's0.session_id = wt0.session_id ' + 'AND s0.wait_type <> N''OLEDB'' ' + 'AND wt0.wait_type <> N''OLEDB'' ' + ') AS p ' + ') AS wt ' + ') AS wt1 ' + 'GROUP BY ' + 'wt1.wait_type, ' + 'wt1.waiting_task_address ' + ') AS wt2 ON ' + 'wt2.waiting_task_address = task_info.task_address ' + 'AND wt2.wait_duration_ms > 0 ' + 'AND task_info.runnable_time IS NULL ' + 'GROUP BY ' + 'task_info.session_id, ' + 'task_info.request_id, ' + 'task_info.physical_io, ' + 'task_info.context_switches, ' + 'task_info.thread_CPU_snapshot, ' + 'task_info.num_tasks, ' + 'CASE ' + 'WHEN task_info.runnable_time IS NOT NULL THEN ' + '''RUNNABLE'' ' + 'ELSE ' + 'wt2.wait_type ' + 'END ' + ') AS w1 ' + ') AS waits ' + 'ORDER BY ' + 'waits.session_id, ' + 'waits.request_id, ' + 'waits.r ' + 'FOR XML ' + 'PATH(N''tasks''), ' + 'TYPE ' + ') AS tasks_raw (task_xml_raw) ' + ') AS tasks_final ' + 'CROSS APPLY tasks_final.task_xml.nodes(N''/tasks'') AS task_nodes (task_node) ' + 'WHERE ' + 'task_nodes.task_node.exist(N''session_id'') = 1 ' + ') AS tasks ON ' + 'tasks.session_id = y.session_id ' + 'AND tasks.request_id = y.request_id ' ELSE '' END + 'LEFT OUTER HASH JOIN ' + '( ' + 'SELECT TOP(@i) ' + 't_info.session_id, ' + 'COALESCE(t_info.request_id, -1) AS request_id, ' + 'SUM(t_info.tempdb_allocations) AS tempdb_allocations, ' + 'SUM(t_info.tempdb_current) AS tempdb_current ' + 'FROM ' + '( ' + 'SELECT TOP(@i) ' + 'tsu.session_id, ' + 'tsu.request_id, ' + 'tsu.user_objects_alloc_page_count + ' + 'tsu.internal_objects_alloc_page_count AS tempdb_allocations,' + 'tsu.user_objects_alloc_page_count + ' + 'tsu.internal_objects_alloc_page_count - ' + 'tsu.user_objects_dealloc_page_count - ' + 'tsu.internal_objects_dealloc_page_count AS tempdb_current ' + 'FROM sys.dm_db_task_space_usage AS tsu ' + 'CROSS APPLY ' + '( ' + 'SELECT TOP(1) ' + 's0.session_id ' + 'FROM @sessions AS s0 ' + 'WHERE ' + 's0.session_id = tsu.session_id ' + ') AS p ' + '' + 'UNION ALL ' + '' + 'SELECT TOP(@i) ' + 'ssu.session_id, ' + 'NULL AS request_id, ' + 'ssu.user_objects_alloc_page_count + ' + 'ssu.internal_objects_alloc_page_count AS tempdb_allocations, ' + 'ssu.user_objects_alloc_page_count + ' + 'ssu.internal_objects_alloc_page_count - ' + 'ssu.user_objects_dealloc_page_count - ' + 'ssu.internal_objects_dealloc_page_count AS tempdb_current ' + 'FROM sys.dm_db_session_space_usage AS ssu ' + 'CROSS APPLY ' + '( ' + 'SELECT TOP(1) ' + 's0.session_id ' + 'FROM @sessions AS s0 ' + 'WHERE ' + 's0.session_id = ssu.session_id ' + ') AS p ' + ') AS t_info ' + 'GROUP BY ' + 't_info.session_id, ' + 'COALESCE(t_info.request_id, -1) ' + ') AS tempdb_info ON ' + 'tempdb_info.session_id = y.session_id ' + 'AND tempdb_info.request_id = ' + 'CASE ' + 'WHEN y.status = N''sleeping'' THEN ' + '-1 ' + 'ELSE ' + 'y.request_id ' + 'END ' + CASE WHEN NOT ( @get_avg_time = 1 AND @recursion = 1 ) THEN '' ELSE 'LEFT OUTER HASH JOIN ' + '( ' + 'SELECT TOP(@i) ' + '* ' + 'FROM sys.dm_exec_query_stats ' + ') AS qs ON ' + 'qs.sql_handle = y.sql_handle ' + 'AND qs.plan_handle = y.plan_handle ' + 'AND qs.statement_start_offset = y.statement_start_offset ' + 'AND qs.statement_end_offset = y.statement_end_offset ' END + ') AS x ' + 'OPTION (KEEPFIXED PLAN, OPTIMIZE FOR (@i = 1)); ';
SET @sql_n = CONVERT(NVARCHAR(MAX), @sql);
SET @last_collection_start = GETDATE();
IF @recursion = -1 BEGIN; SELECT @first_collection_ms_ticks = ms_ticks FROM sys.dm_os_sys_info; END;
INSERT #sessions ( recursion, session_id, request_id, session_number, elapsed_time, avg_elapsed_time, physical_io, reads, physical_reads, writes, tempdb_allocations, tempdb_current, CPU, thread_CPU_snapshot, context_switches, used_memory, tasks, status, wait_info, transaction_id, open_tran_count, sql_handle, statement_start_offset, statement_end_offset, sql_text, plan_handle, blocking_session_id, percent_complete, host_name, login_name, database_name, program_name, additional_info, start_time, login_time, last_request_start_time ) EXEC sp_executesql @sql_n, N'@recursion SMALLINT, @filter sysname, @not_filter sysname, @first_collection_ms_ticks BIGINT', @recursion, @filter, @not_filter, @first_collection_ms_ticks;
--Collect transaction information? IF @recursion = 1 AND ( @output_column_list LIKE '%|[tran_start_time|]%' ESCAPE '|' OR @output_column_list LIKE '%|[tran_log_writes|]%' ESCAPE '|' ) BEGIN; DECLARE @i INT; SET @i = 2147483647;
UPDATE s SET tran_start_time = CONVERT ( DATETIME, LEFT ( x.trans_info, NULLIF(CHARINDEX(NCHAR(254), x.trans_info) - 1, -1) ), 121 ), tran_log_writes = RIGHT ( x.trans_info, LEN(x.trans_info) - CHARINDEX(NCHAR(254), x.trans_info) ) FROM ( SELECT TOP(@i) trans_nodes.trans_node.value('(session_id/text())[1]', 'SMALLINT') AS session_id, COALESCE(trans_nodes.trans_node.value('(request_id/text())[1]', 'INT'), 0) AS request_id, trans_nodes.trans_node.value('(trans_info/text())[1]', 'NVARCHAR(4000)') AS trans_info FROM ( SELECT TOP(@i) CONVERT ( XML, REPLACE ( CONVERT(NVARCHAR(MAX), trans_raw.trans_xml_raw) COLLATE Latin1_General_Bin2, N'</trans_info></trans><trans><trans_info>', N'' ) ) FROM ( SELECT TOP(@i) CASE u_trans.r WHEN 1 THEN u_trans.session_id ELSE NULL END AS [session_id], CASE u_trans.r WHEN 1 THEN u_trans.request_id ELSE NULL END AS [request_id], CONVERT ( NVARCHAR(MAX), CASE WHEN u_trans.database_id IS NOT NULL THEN CASE u_trans.r WHEN 1 THEN COALESCE(CONVERT(NVARCHAR, u_trans.transaction_start_time, 121) + NCHAR(254), N'') ELSE N'' END + REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( CONVERT(VARCHAR(128), COALESCE(DB_NAME(u_trans.database_id), N'(null)')), NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'?' ) + N': ' + CONVERT(NVARCHAR, u_trans.log_record_count) + N' (' + CONVERT(NVARCHAR, u_trans.log_kb_used) + N' kB)' + N',' ELSE N'N/A,' END COLLATE Latin1_General_Bin2 ) AS [trans_info] FROM ( SELECT TOP(@i) trans.*, ROW_NUMBER() OVER ( PARTITION BY trans.session_id, trans.request_id ORDER BY trans.transaction_start_time DESC ) AS r FROM ( SELECT TOP(@i) session_tran_map.session_id, session_tran_map.request_id, s_tran.database_id, COALESCE(SUM(s_tran.database_transaction_log_record_count), 0) AS log_record_count, COALESCE(SUM(s_tran.database_transaction_log_bytes_used), 0) / 1024 AS log_kb_used, MIN(s_tran.database_transaction_begin_time) AS transaction_start_time FROM ( SELECT TOP(@i) * FROM sys.dm_tran_active_transactions WHERE transaction_begin_time <= @last_collection_start ) AS a_tran INNER HASH JOIN ( SELECT TOP(@i) * FROM sys.dm_tran_database_transactions WHERE database_id < 32767 ) AS s_tran ON s_tran.transaction_id = a_tran.transaction_id LEFT OUTER HASH JOIN ( SELECT TOP(@i) * FROM sys.dm_tran_session_transactions ) AS tst ON s_tran.transaction_id = tst.transaction_id CROSS APPLY ( SELECT TOP(1) s3.session_id, s3.request_id FROM ( SELECT TOP(1) s1.session_id, s1.request_id FROM #sessions AS s1 WHERE s1.transaction_id = s_tran.transaction_id AND s1.recursion = 1
UNION ALL
SELECT TOP(1) s2.session_id, s2.request_id FROM #sessions AS s2 WHERE s2.session_id = tst.session_id AND s2.recursion = 1 ) AS s3 ORDER BY s3.request_id ) AS session_tran_map GROUP BY session_tran_map.session_id, session_tran_map.request_id, s_tran.database_id ) AS trans ) AS u_trans FOR XML PATH('trans'), TYPE ) AS trans_raw (trans_xml_raw) ) AS trans_final (trans_xml) CROSS APPLY trans_final.trans_xml.nodes('/trans') AS trans_nodes (trans_node) ) AS x INNER HASH JOIN #sessions AS s ON s.session_id = x.session_id AND s.request_id = x.request_id OPTION (OPTIMIZE FOR (@i = 1)); END;
--Variables for text and plan collection DECLARE @session_id SMALLINT, @request_id INT, @sql_handle VARBINARY(64), @plan_handle VARBINARY(64), @statement_start_offset INT, @statement_end_offset INT, @start_time DATETIME, @database_name sysname;
IF @recursion = 1 AND @output_column_list LIKE '%|[sql_text|]%' ESCAPE '|' BEGIN; DECLARE sql_cursor CURSOR LOCAL FAST_FORWARD FOR SELECT session_id, request_id, sql_handle, statement_start_offset, statement_end_offset FROM #sessions WHERE recursion = 1 AND sql_handle IS NOT NULL OPTION (KEEPFIXED PLAN);
OPEN sql_cursor;
FETCH NEXT FROM sql_cursor INTO @session_id, @request_id, @sql_handle, @statement_start_offset, @statement_end_offset;
--Wait up to 5 ms for the SQL text, then give up SET LOCK_TIMEOUT 5;
WHILE @@FETCH_STATUS = 0 BEGIN; BEGIN TRY; UPDATE s SET s.sql_text = ( SELECT REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( N'--' + NCHAR(13) + NCHAR(10) + CASE WHEN @get_full_inner_text = 1 THEN est.text WHEN LEN(est.text) < (@statement_end_offset / 2) + 1 THEN est.text WHEN SUBSTRING(est.text, (@statement_start_offset/2), 2) LIKE N'[a-zA-Z0-9][a-zA-Z0-9]' THEN est.text ELSE CASE WHEN @statement_start_offset > 0 THEN SUBSTRING ( est.text, ((@statement_start_offset/2) + 1), ( CASE WHEN @statement_end_offset = -1 THEN 2147483647 ELSE ((@statement_end_offset - @statement_start_offset)/2) + 1 END ) ) ELSE RTRIM(LTRIM(est.text)) END END + NCHAR(13) + NCHAR(10) + N'--' COLLATE Latin1_General_Bin2, NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'' ) AS [processing-instruction(query)] FOR XML PATH(''), TYPE ), s.statement_start_offset = CASE WHEN LEN(est.text) < (@statement_end_offset / 2) + 1 THEN 0 WHEN SUBSTRING(CONVERT(VARCHAR(MAX), est.text), (@statement_start_offset/2), 2) LIKE '[a-zA-Z0-9][a-zA-Z0-9]' THEN 0 ELSE @statement_start_offset END, s.statement_end_offset = CASE WHEN LEN(est.text) < (@statement_end_offset / 2) + 1 THEN -1 WHEN SUBSTRING(CONVERT(VARCHAR(MAX), est.text), (@statement_start_offset/2), 2) LIKE '[a-zA-Z0-9][a-zA-Z0-9]' THEN -1 ELSE @statement_end_offset END FROM #sessions AS s, ( SELECT TOP(1) text FROM ( SELECT text, 0 AS row_num FROM sys.dm_exec_sql_text(@sql_handle)
UNION ALL
SELECT NULL, 1 AS row_num ) AS est0 ORDER BY row_num ) AS est WHERE s.session_id = @session_id AND s.request_id = @request_id AND s.recursion = 1 OPTION (KEEPFIXED PLAN); END TRY BEGIN CATCH; UPDATE s SET s.sql_text = CASE ERROR_NUMBER() WHEN 1222 THEN '<timeout_exceeded />' ELSE '<error message="' + ERROR_MESSAGE() + '" />' END FROM #sessions AS s WHERE s.session_id = @session_id AND s.request_id = @request_id AND s.recursion = 1 OPTION (KEEPFIXED PLAN); END CATCH;
FETCH NEXT FROM sql_cursor INTO @session_id, @request_id, @sql_handle, @statement_start_offset, @statement_end_offset; END;
--Return this to the default SET LOCK_TIMEOUT -1;
CLOSE sql_cursor; DEALLOCATE sql_cursor; END;
IF @get_outer_command = 1 AND @recursion = 1 AND @output_column_list LIKE '%|[sql_command|]%' ESCAPE '|' BEGIN; DECLARE @buffer_results TABLE ( EventType VARCHAR(30), Parameters INT, EventInfo NVARCHAR(4000), start_time DATETIME, session_number INT IDENTITY(1,1) NOT NULL PRIMARY KEY );
DECLARE buffer_cursor CURSOR LOCAL FAST_FORWARD FOR SELECT session_id, MAX(start_time) AS start_time FROM #sessions WHERE recursion = 1 GROUP BY session_id ORDER BY session_id OPTION (KEEPFIXED PLAN);
OPEN buffer_cursor;
FETCH NEXT FROM buffer_cursor INTO @session_id, @start_time;
WHILE @@FETCH_STATUS = 0 BEGIN; BEGIN TRY; --In SQL Server 2008, DBCC INPUTBUFFER will throw --an exception if the session no longer exists INSERT @buffer_results ( EventType, Parameters, EventInfo ) EXEC sp_executesql N'DBCC INPUTBUFFER(@session_id) WITH NO_INFOMSGS;', N'@session_id SMALLINT', @session_id;
UPDATE br SET br.start_time = @start_time FROM @buffer_results AS br WHERE br.session_number = ( SELECT MAX(br2.session_number) FROM @buffer_results br2 ); END TRY BEGIN CATCH END CATCH;
FETCH NEXT FROM buffer_cursor INTO @session_id, @start_time; END;
UPDATE s SET sql_command = ( SELECT REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( CONVERT ( NVARCHAR(MAX), N'--' + NCHAR(13) + NCHAR(10) + br.EventInfo + NCHAR(13) + NCHAR(10) + N'--' COLLATE Latin1_General_Bin2 ), NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'' ) AS [processing-instruction(query)] FROM @buffer_results AS br WHERE br.session_number = s.session_number AND br.start_time = s.start_time AND ( ( s.start_time = s.last_request_start_time AND EXISTS ( SELECT * FROM sys.dm_exec_requests r2 WHERE r2.session_id = s.session_id AND r2.request_id = s.request_id AND r2.start_time = s.start_time ) ) OR ( s.request_id = 0 AND EXISTS ( SELECT * FROM sys.dm_exec_sessions s2 WHERE s2.session_id = s.session_id AND s2.last_request_start_time = s.last_request_start_time ) ) ) FOR XML PATH(''), TYPE ) FROM #sessions AS s WHERE recursion = 1 OPTION (KEEPFIXED PLAN);
CLOSE buffer_cursor; DEALLOCATE buffer_cursor; END;
IF @get_plans >= 1 AND @recursion = 1 AND @output_column_list LIKE '%|[query_plan|]%' ESCAPE '|' BEGIN; DECLARE plan_cursor CURSOR LOCAL FAST_FORWARD FOR SELECT session_id, request_id, plan_handle, statement_start_offset, statement_end_offset FROM #sessions WHERE recursion = 1 AND plan_handle IS NOT NULL OPTION (KEEPFIXED PLAN);
OPEN plan_cursor;
FETCH NEXT FROM plan_cursor INTO @session_id, @request_id, @plan_handle, @statement_start_offset, @statement_end_offset;
--Wait up to 5 ms for a query plan, then give up SET LOCK_TIMEOUT 5;
WHILE @@FETCH_STATUS = 0 BEGIN; BEGIN TRY; UPDATE s SET s.query_plan = ( SELECT CONVERT(xml, query_plan) FROM sys.dm_exec_text_query_plan ( @plan_handle, CASE @get_plans WHEN 1 THEN @statement_start_offset ELSE 0 END, CASE @get_plans WHEN 1 THEN @statement_end_offset ELSE -1 END ) ) FROM #sessions AS s WHERE s.session_id = @session_id AND s.request_id = @request_id AND s.recursion = 1 OPTION (KEEPFIXED PLAN); END TRY BEGIN CATCH; IF ERROR_NUMBER() = 6335 BEGIN; UPDATE s SET s.query_plan = ( SELECT N'--' + NCHAR(13) + NCHAR(10) + N'-- Could not render showplan due to XML data type limitations. ' + NCHAR(13) + NCHAR(10) + N'-- To see the graphical plan save the XML below as a .SQLPLAN file and re-open in SSMS.' + NCHAR(13) + NCHAR(10) + N'--' + NCHAR(13) + NCHAR(10) + REPLACE(qp.query_plan, N'<RelOp', NCHAR(13)+NCHAR(10)+N'<RelOp') + NCHAR(13) + NCHAR(10) + N'--' COLLATE Latin1_General_Bin2 AS [processing-instruction(query_plan)] FROM sys.dm_exec_text_query_plan ( @plan_handle, CASE @get_plans WHEN 1 THEN @statement_start_offset ELSE 0 END, CASE @get_plans WHEN 1 THEN @statement_end_offset ELSE -1 END ) AS qp FOR XML PATH(''), TYPE ) FROM #sessions AS s WHERE s.session_id = @session_id AND s.request_id = @request_id AND s.recursion = 1 OPTION (KEEPFIXED PLAN); END; ELSE BEGIN; UPDATE s SET s.query_plan = CASE ERROR_NUMBER() WHEN 1222 THEN '<timeout_exceeded />' ELSE '<error message="' + ERROR_MESSAGE() + '" />' END FROM #sessions AS s WHERE s.session_id = @session_id AND s.request_id = @request_id AND s.recursion = 1 OPTION (KEEPFIXED PLAN); END; END CATCH;
FETCH NEXT FROM plan_cursor INTO @session_id, @request_id, @plan_handle, @statement_start_offset, @statement_end_offset; END;
--Return this to the default SET LOCK_TIMEOUT -1;
CLOSE plan_cursor; DEALLOCATE plan_cursor; END;
IF @get_locks = 1 AND @recursion = 1 AND @output_column_list LIKE '%|[locks|]%' ESCAPE '|' BEGIN; DECLARE locks_cursor CURSOR LOCAL FAST_FORWARD FOR SELECT DISTINCT database_name FROM #locks WHERE EXISTS ( SELECT * FROM #sessions AS s WHERE s.session_id = #locks.session_id AND recursion = 1 ) AND database_name <> '(null)' OPTION (KEEPFIXED PLAN);
OPEN locks_cursor;
FETCH NEXT FROM locks_cursor INTO @database_name;
WHILE @@FETCH_STATUS = 0 BEGIN; BEGIN TRY; SET @sql_n = CONVERT(NVARCHAR(MAX), '') + 'UPDATE l ' + 'SET ' + 'object_name = ' + 'REPLACE ' + '( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'o.name COLLATE Latin1_General_Bin2, ' + 'NCHAR(31),N''?''),NCHAR(30),N''?''),NCHAR(29),N''?''),NCHAR(28),N''?''),NCHAR(27),N''?''),NCHAR(26),N''?''),NCHAR(25),N''?''),NCHAR(24),N''?''),NCHAR(23),N''?''),NCHAR(22),N''?''), ' + 'NCHAR(21),N''?''),NCHAR(20),N''?''),NCHAR(19),N''?''),NCHAR(18),N''?''),NCHAR(17),N''?''),NCHAR(16),N''?''),NCHAR(15),N''?''),NCHAR(14),N''?''),NCHAR(12),N''?''), ' + 'NCHAR(11),N''?''),NCHAR(8),N''?''),NCHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + N''''' ' + '), ' + 'index_name = ' + 'REPLACE ' + '( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'i.name COLLATE Latin1_General_Bin2, ' + 'NCHAR(31),N''?''),NCHAR(30),N''?''),NCHAR(29),N''?''),NCHAR(28),N''?''),NCHAR(27),N''?''),NCHAR(26),N''?''),NCHAR(25),N''?''),NCHAR(24),N''?''),NCHAR(23),N''?''),NCHAR(22),N''?''), ' + 'NCHAR(21),N''?''),NCHAR(20),N''?''),NCHAR(19),N''?''),NCHAR(18),N''?''),NCHAR(17),N''?''),NCHAR(16),N''?''),NCHAR(15),N''?''),NCHAR(14),N''?''),NCHAR(12),N''?''), ' + 'NCHAR(11),N''?''),NCHAR(8),N''?''),NCHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + N''''' ' + '), ' + 'schema_name = ' + 'REPLACE ' + '( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 's.name COLLATE Latin1_General_Bin2, ' + 'NCHAR(31),N''?''),NCHAR(30),N''?''),NCHAR(29),N''?''),NCHAR(28),N''?''),NCHAR(27),N''?''),NCHAR(26),N''?''),NCHAR(25),N''?''),NCHAR(24),N''?''),NCHAR(23),N''?''),NCHAR(22),N''?''), ' + 'NCHAR(21),N''?''),NCHAR(20),N''?''),NCHAR(19),N''?''),NCHAR(18),N''?''),NCHAR(17),N''?''),NCHAR(16),N''?''),NCHAR(15),N''?''),NCHAR(14),N''?''),NCHAR(12),N''?''), ' + 'NCHAR(11),N''?''),NCHAR(8),N''?''),NCHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + N''''' ' + '), ' + 'principal_name = ' + 'REPLACE ' + '( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'dp.name COLLATE Latin1_General_Bin2, ' + 'NCHAR(31),N''?''),NCHAR(30),N''?''),NCHAR(29),N''?''),NCHAR(28),N''?''),NCHAR(27),N''?''),NCHAR(26),N''?''),NCHAR(25),N''?''),NCHAR(24),N''?''),NCHAR(23),N''?''),NCHAR(22),N''?''), ' + 'NCHAR(21),N''?''),NCHAR(20),N''?''),NCHAR(19),N''?''),NCHAR(18),N''?''),NCHAR(17),N''?''),NCHAR(16),N''?''),NCHAR(15),N''?''),NCHAR(14),N''?''),NCHAR(12),N''?''), ' + 'NCHAR(11),N''?''),NCHAR(8),N''?''),NCHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + N''''' ' + ') ' + 'FROM #locks AS l ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.allocation_units AS au ON ' + 'au.allocation_unit_id = l.allocation_unit_id ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.partitions AS p ON ' + 'p.hobt_id = ' + 'COALESCE ' + '( ' + 'l.hobt_id, ' + 'CASE ' + 'WHEN au.type IN (1, 3) THEN au.container_id ' + 'ELSE NULL ' + 'END ' + ') ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.partitions AS p1 ON ' + 'l.hobt_id IS NULL ' + 'AND au.type = 2 ' + 'AND p1.partition_id = au.container_id ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.objects AS o ON ' + 'o.object_id = COALESCE(l.object_id, p.object_id, p1.object_id) ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.indexes AS i ON ' + 'i.object_id = COALESCE(l.object_id, p.object_id, p1.object_id) ' + 'AND i.index_id = COALESCE(l.index_id, p.index_id, p1.index_id) ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.schemas AS s ON ' + 's.schema_id = COALESCE(l.schema_id, o.schema_id) ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.database_principals AS dp ON ' + 'dp.principal_id = l.principal_id ' + 'WHERE ' + 'l.database_name = @database_name ' + 'OPTION (KEEPFIXED PLAN); ';
EXEC sp_executesql @sql_n, N'@database_name sysname', @database_name; END TRY BEGIN CATCH; UPDATE #locks SET query_error = REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( CONVERT ( NVARCHAR(MAX), ERROR_MESSAGE() COLLATE Latin1_General_Bin2 ), NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'' ) WHERE database_name = @database_name OPTION (KEEPFIXED PLAN); END CATCH;
FETCH NEXT FROM locks_cursor INTO @database_name; END;
CLOSE locks_cursor; DEALLOCATE locks_cursor;
CREATE CLUSTERED INDEX IX_SRD ON #locks (session_id, request_id, database_name);
UPDATE s SET s.locks = ( SELECT REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( CONVERT ( NVARCHAR(MAX), l1.database_name COLLATE Latin1_General_Bin2 ), NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'' ) AS [Database/@name], MIN(l1.query_error) AS [Database/@query_error], ( SELECT l2.request_mode AS [Lock/@request_mode], l2.request_status AS [Lock/@request_status], COUNT(*) AS [Lock/@request_count] FROM #locks AS l2 WHERE l1.session_id = l2.session_id AND l1.request_id = l2.request_id AND l2.database_name = l1.database_name AND l2.resource_type = 'DATABASE' GROUP BY l2.request_mode, l2.request_status FOR XML PATH(''), TYPE ) AS [Database/Locks], ( SELECT COALESCE(l3.object_name, '(null)') AS [Object/@name], l3.schema_name AS [Object/@schema_name], ( SELECT l4.resource_type AS [Lock/@resource_type], l4.page_type AS [Lock/@page_type], l4.index_name AS [Lock/@index_name], CASE WHEN l4.object_name IS NULL THEN l4.schema_name ELSE NULL END AS [Lock/@schema_name], l4.principal_name AS [Lock/@principal_name], l4.resource_description AS [Lock/@resource_description], l4.request_mode AS [Lock/@request_mode], l4.request_status AS [Lock/@request_status], SUM(l4.request_count) AS [Lock/@request_count] FROM #locks AS l4 WHERE l4.session_id = l3.session_id AND l4.request_id = l3.request_id AND l3.database_name = l4.database_name AND COALESCE(l3.object_name, '(null)') = COALESCE(l4.object_name, '(null)') AND COALESCE(l3.schema_name, '') = COALESCE(l4.schema_name, '') AND l4.resource_type <> 'DATABASE' GROUP BY l4.resource_type, l4.page_type, l4.index_name, CASE WHEN l4.object_name IS NULL THEN l4.schema_name ELSE NULL END, l4.principal_name, l4.resource_description, l4.request_mode, l4.request_status FOR XML PATH(''), TYPE ) AS [Object/Locks] FROM #locks AS l3 WHERE l3.session_id = l1.session_id AND l3.request_id = l1.request_id AND l3.database_name = l1.database_name AND l3.resource_type <> 'DATABASE' GROUP BY l3.session_id, l3.request_id, l3.database_name, COALESCE(l3.object_name, '(null)'), l3.schema_name FOR XML PATH(''), TYPE ) AS [Database/Objects] FROM #locks AS l1 WHERE l1.session_id = s.session_id AND l1.request_id = s.request_id AND l1.start_time IN (s.start_time, s.last_request_start_time) AND s.recursion = 1 GROUP BY l1.session_id, l1.request_id, l1.database_name FOR XML PATH(''), TYPE ) FROM #sessions s OPTION (KEEPFIXED PLAN); END;
IF @find_block_leaders = 1 AND @recursion = 1 AND @output_column_list LIKE '%|[blocked_session_count|]%' ESCAPE '|' BEGIN; WITH blockers AS ( SELECT session_id, session_id AS top_level_session_id FROM #sessions WHERE recursion = 1
UNION ALL
SELECT s.session_id, b.top_level_session_id FROM blockers AS b JOIN #sessions AS s ON s.blocking_session_id = b.session_id AND s.recursion = 1 ) UPDATE s SET s.blocked_session_count = x.blocked_session_count FROM #sessions AS s JOIN ( SELECT b.top_level_session_id AS session_id, COUNT(*) - 1 AS blocked_session_count FROM blockers AS b GROUP BY b.top_level_session_id ) x ON s.session_id = x.session_id WHERE s.recursion = 1; END;
IF @get_task_info = 2 AND @output_column_list LIKE '%|[additional_info|]%' ESCAPE '|' AND @recursion = 1 BEGIN; CREATE TABLE #blocked_requests ( session_id SMALLINT NOT NULL, request_id INT NOT NULL, database_name sysname NOT NULL, object_id INT, hobt_id BIGINT, schema_id INT, schema_name sysname NULL, object_name sysname NULL, query_error NVARCHAR(2048), PRIMARY KEY (database_name, session_id, request_id) );
CREATE STATISTICS s_database_name ON #blocked_requests (database_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_schema_name ON #blocked_requests (schema_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_object_name ON #blocked_requests (object_name) WITH SAMPLE 0 ROWS, NORECOMPUTE; CREATE STATISTICS s_query_error ON #blocked_requests (query_error) WITH SAMPLE 0 ROWS, NORECOMPUTE;
INSERT #blocked_requests ( session_id, request_id, database_name, object_id, hobt_id, schema_id ) SELECT session_id, request_id, database_name, object_id, hobt_id, CONVERT(INT, SUBSTRING(schema_node, CHARINDEX(' = ', schema_node) + 3, LEN(schema_node))) AS schema_id FROM ( SELECT session_id, request_id, agent_nodes.agent_node.value('(database_name/text())[1]', 'sysname') AS database_name, agent_nodes.agent_node.value('(object_id/text())[1]', 'int') AS object_id, agent_nodes.agent_node.value('(hobt_id/text())[1]', 'bigint') AS hobt_id, agent_nodes.agent_node.value('(metadata_resource/text()[.="SCHEMA"]/../../metadata_class_id/text())[1]', 'varchar(100)') AS schema_node FROM #sessions AS s CROSS APPLY s.additional_info.nodes('//block_info') AS agent_nodes (agent_node) WHERE s.recursion = 1 ) AS t WHERE t.object_id IS NOT NULL OR t.hobt_id IS NOT NULL OR t.schema_node IS NOT NULL;
DECLARE blocks_cursor CURSOR LOCAL FAST_FORWARD FOR SELECT DISTINCT database_name FROM #blocked_requests;
OPEN blocks_cursor;
FETCH NEXT FROM blocks_cursor INTO @database_name;
WHILE @@FETCH_STATUS = 0 BEGIN; BEGIN TRY; SET @sql_n = CONVERT(NVARCHAR(MAX), '') + 'UPDATE b ' + 'SET ' + 'b.schema_name = ' + 'REPLACE ' + '( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 's.name COLLATE Latin1_General_Bin2, ' + 'NCHAR(31),N''?''),NCHAR(30),N''?''),NCHAR(29),N''?''),NCHAR(28),N''?''),NCHAR(27),N''?''),NCHAR(26),N''?''),NCHAR(25),N''?''),NCHAR(24),N''?''),NCHAR(23),N''?''),NCHAR(22),N''?''), ' + 'NCHAR(21),N''?''),NCHAR(20),N''?''),NCHAR(19),N''?''),NCHAR(18),N''?''),NCHAR(17),N''?''),NCHAR(16),N''?''),NCHAR(15),N''?''),NCHAR(14),N''?''),NCHAR(12),N''?''), ' + 'NCHAR(11),N''?''),NCHAR(8),N''?''),NCHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + N''''' ' + '), ' + 'b.object_name = ' + 'REPLACE ' + '( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( ' + 'o.name COLLATE Latin1_General_Bin2, ' + 'NCHAR(31),N''?''),NCHAR(30),N''?''),NCHAR(29),N''?''),NCHAR(28),N''?''),NCHAR(27),N''?''),NCHAR(26),N''?''),NCHAR(25),N''?''),NCHAR(24),N''?''),NCHAR(23),N''?''),NCHAR(22),N''?''), ' + 'NCHAR(21),N''?''),NCHAR(20),N''?''),NCHAR(19),N''?''),NCHAR(18),N''?''),NCHAR(17),N''?''),NCHAR(16),N''?''),NCHAR(15),N''?''),NCHAR(14),N''?''),NCHAR(12),N''?''), ' + 'NCHAR(11),N''?''),NCHAR(8),N''?''),NCHAR(7),N''?''),NCHAR(6),N''?''),NCHAR(5),N''?''),NCHAR(4),N''?''),NCHAR(3),N''?''),NCHAR(2),N''?''),NCHAR(1),N''?''), ' + 'NCHAR(0), ' + N''''' ' + ') ' + 'FROM #blocked_requests AS b ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.partitions AS p ON ' + 'p.hobt_id = b.hobt_id ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.objects AS o ON ' + 'o.object_id = COALESCE(p.object_id, b.object_id) ' + 'LEFT OUTER JOIN ' + QUOTENAME(@database_name) + '.sys.schemas AS s ON ' + 's.schema_id = COALESCE(o.schema_id, b.schema_id) ' + 'WHERE ' + 'b.database_name = @database_name; ';
EXEC sp_executesql @sql_n, N'@database_name sysname', @database_name; END TRY BEGIN CATCH; UPDATE #blocked_requests SET query_error = REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( CONVERT ( NVARCHAR(MAX), ERROR_MESSAGE() COLLATE Latin1_General_Bin2 ), NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'' ) WHERE database_name = @database_name; END CATCH;
FETCH NEXT FROM blocks_cursor INTO @database_name; END;
CLOSE blocks_cursor; DEALLOCATE blocks_cursor;
UPDATE s SET additional_info.modify (' insert <schema_name>{sql:column("b.schema_name")}</schema_name> as last into (/additional_info/block_info)[1] ') FROM #sessions AS s INNER JOIN #blocked_requests AS b ON b.session_id = s.session_id AND b.request_id = s.request_id AND s.recursion = 1 WHERE b.schema_name IS NOT NULL;
UPDATE s SET additional_info.modify (' insert <object_name>{sql:column("b.object_name")}</object_name> as last into (/additional_info/block_info)[1] ') FROM #sessions AS s INNER JOIN #blocked_requests AS b ON b.session_id = s.session_id AND b.request_id = s.request_id AND s.recursion = 1 WHERE b.object_name IS NOT NULL;
UPDATE s SET additional_info.modify (' insert <query_error>{sql:column("b.query_error")}</query_error> as last into (/additional_info/block_info)[1] ') FROM #sessions AS s INNER JOIN #blocked_requests AS b ON b.session_id = s.session_id AND b.request_id = s.request_id AND s.recursion = 1 WHERE b.query_error IS NOT NULL; END;
IF @output_column_list LIKE '%|[program_name|]%' ESCAPE '|' AND @output_column_list LIKE '%|[additional_info|]%' ESCAPE '|' AND @recursion = 1 BEGIN; DECLARE @job_id UNIQUEIDENTIFIER; DECLARE @step_id INT;
DECLARE agent_cursor CURSOR LOCAL FAST_FORWARD FOR SELECT s.session_id, agent_nodes.agent_node.value('(job_id/text())[1]', 'uniqueidentifier') AS job_id, agent_nodes.agent_node.value('(step_id/text())[1]', 'int') AS step_id FROM #sessions AS s CROSS APPLY s.additional_info.nodes('//agent_job_info') AS agent_nodes (agent_node) WHERE s.recursion = 1 OPTION (KEEPFIXED PLAN);
OPEN agent_cursor;
FETCH NEXT FROM agent_cursor INTO @session_id, @job_id, @step_id;
WHILE @@FETCH_STATUS = 0 BEGIN; BEGIN TRY; DECLARE @job_name sysname; SET @job_name = NULL; DECLARE @step_name sysname; SET @step_name = NULL;
SELECT @job_name = REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( j.name, NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'?' ), @step_name = REPLACE ( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE( s.step_name, NCHAR(31),N'?'),NCHAR(30),N'?'),NCHAR(29),N'?'),NCHAR(28),N'?'),NCHAR(27),N'?'),NCHAR(26),N'?'),NCHAR(25),N'?'),NCHAR(24),N'?'),NCHAR(23),N'?'),NCHAR(22),N'?'), NCHAR(21),N'?'),NCHAR(20),N'?'),NCHAR(19),N'?'),NCHAR(18),N'?'),NCHAR(17),N'?'),NCHAR(16),N'?'),NCHAR(15),N'?'),NCHAR(14),N'?'),NCHAR(12),N'?'), NCHAR(11),N'?'),NCHAR(8),N'?'),NCHAR(7),N'?'),NCHAR(6),N'?'),NCHAR(5),N'?'),NCHAR(4),N'?'),NCHAR(3),N'?'),NCHAR(2),N'?'),NCHAR(1),N'?'), NCHAR(0), N'?' ) FROM msdb.dbo.sysjobs AS j INNER JOIN msdb..sysjobsteps AS s ON j.job_id = s.job_id WHERE j.job_id = @job_id AND s.step_id = @step_id;
IF @job_name IS NOT NULL BEGIN; UPDATE s SET additional_info.modify (' insert text{sql:variable("@job_name")} into (/additional_info/agent_job_info/job_name)[1] ') FROM #sessions AS s WHERE s.session_id = @session_id OPTION (KEEPFIXED PLAN);
UPDATE s SET additional_info.modify (' insert text{sql:variable("@step_name")} into (/additional_info/agent_job_info/step_name)[1] ') FROM #sessions AS s WHERE s.session_id = @session_id OPTION (KEEPFIXED PLAN); END; END TRY BEGIN CATCH; DECLARE @msdb_error_message NVARCHAR(256); SET @msdb_error_message = ERROR_MESSAGE();
UPDATE s SET additional_info.modify (' insert <msdb_query_error>{sql:variable("@msdb_error_message")}</msdb_query_error> as last into (/additional_info/agent_job_info)[1] ') FROM #sessions AS s WHERE s.session_id = @session_id AND s.recursion = 1 OPTION (KEEPFIXED PLAN); END CATCH;
FETCH NEXT FROM agent_cursor INTO @session_id, @job_id, @step_id; END;
CLOSE agent_cursor; DEALLOCATE agent_cursor; END;
IF @delta_interval > 0 AND @recursion <> 1 BEGIN; SET @recursion = 1;
DECLARE @delay_time CHAR(12); SET @delay_time = CONVERT(VARCHAR, DATEADD(second, @delta_interval, 0), 114); WAITFOR DELAY @delay_time;
