BEGIN
  SET NOCOUNT ON;
		DECLARE @MailRecipiants NVARCHAR (255);
		DECLARE @copy_recipients NVARCHAR (255);
		SELECT	@copy_recipients = 'Sharonri@openu.ac.il';
		DECLARE @MailSubject NVARCHAR (255) = N'';
		DECLARE @MailProfile sysname = (SELECT TOP 1 name FROM msdb.dbo.sysmail_profile);
		DECLARE @MailBodey NVARCHAR (max) = N'';

		DECLARE @MailTable NVARCHAR (max) = N'';

		SET @MailSubject = N'Faild Jobs over night: ' + @@SERVERNAME +' | ' + convert (nvarchar (50), GETDATE(), 100) ;
		SET @MailTable = '<table class="sample">
<tr>
	<td style="background-color:#E6E6E6; color:#2E64FE; font-weight:bolder;font-size:14px;">Server Name</td>
	<td style="background-color:#E6E6E6; color:#2E64FE; font-weight:bolder;font-size:14px;">Database</td>
	<td style="background-color:#E6E6E6; color:#2E64FE; font-weight:bolder;font-size:14px;">Code</td>
	<td style="background-color:#E6E6E6; color:#2E64FE; font-weight:bolder;font-size:14px;">Task Status</td>
	<td style="background-color:#E6E6E6; color:#2E64FE; font-weight:bolder;font-size:14px;">How many times</td>
	<td style="background-color:#E6E6E6; color:#2E64FE; font-weight:bolder;font-size:14px;">Execution Date</td>
	<td style="background-color:#E6E6E6; color:#2E64FE; font-weight:bolder;font-size:14px;">Error</td>td>
</tr>';


/* declare variables */
DECLARE @Database sysname;
DECLARE @Code NVARCHAR(MAX);
DECLARE @Status sysname;
DECLARE @NOTimes INT;
DECLARE @Date DATETIME;
DECLARE @Error NVARCHAR(MAX);

DECLARE cuJobMailStatus CURSOR LOCAL FAST_FORWARD READ_ONLY FOR 
SELECT	c.[Database],c.StoredProcedure,l.TaskStatus,COUNT_BIG(1) [HowManyTimesTheJobHaveRun],MAX(l.StartDate) [At],l.AdditionalInformation [Error],REPLACE(dbo.ufn_Util_clr_Conc(DISTINCT ML.Mail),',',';') + ';'
FROM	Schedule.Log_ScheduleTaskLog l
		INNER JOIN Schedule.Schedule_Configuration c ON c.ID = l.ScheduleTaskID
		INNER JOIN Schedule.Schedule_cl_ConfigurationMailListScheduleTask CL ON CL.ScheduleTaskID = c.ID
		INNER JOIN  Schedule.Schedule_ConfigurationMailList ML ON CL.MailID = ML.ID
WHERE	ISNULL(c.Server,@@SERVERNAME) = @@SERVERNAME
		AND l.StartDate BETWEEN DATEADD(DAY, DATEDIFF(DAY, '19000101', DATEADD(DAY,-1,GETDATE())), '19000101') AND DATEADD(DAY, DATEDIFF(DAY, '19000101', DATEADD(DAY,-1,GETDATE())), '23:59:59')
		AND TaskStatus = N'Failed'
GROUP BY c.[Database],c.StoredProcedure,l.TaskStatus,l.AdditionalInformation

OPEN cuJobMailStatus

FETCH NEXT FROM cuJobMailStatus INTO @Database,@Code,@Status,@NOTimes,@Date,@Error,@MailRecipiants;

WHILE @@FETCH_STATUS = 0
BEGIN


    SELECT  @MailTable += CONCAT('<tr><td>' , @@SERVERNAME
			, '</td><td>' , @Database 
			, '</td><td>' , @Code
            , '</td><td>' , @Status
			, '</td><td>' , @NOTimes
			, '</td><td>' , @Date
            , '</td><td>' , @Error
			, '</td></tr>');

    SET @MailTable += '
</table>'

	SET @MailBodey+=
'
<!DOCTYPE html>
<html>
<body>
<style type="text/css">
table.sample {
	font-family:Calibri;
	font-size:small;
	border-width: 1px;
	border-spacing: 0px;
	border-style: solid;
	border-color: gray;
	border-collapse: collapse;
	background-color: white;}
table.sample th {
	border-width: 1px;
	padding: 3px;
	border-style: solid;
	border-color: gray;
	background-color: white;
	}
table.sample td {
	font-family:Calibri;
	font-size:12px;
	border-width: 1px;
	padding: 3px;
	border-style: solid;
	border-color: gray;
	background-color: white;
	}
</style>
<font face="Calibri (Body)">
<H1><p style=''font-size:18.0pt;font-family:"Bradley Hand ITC"''>' + @MailSubject + N'</p></H1>
<br/>' + @MailTable  + '<br/>

</font>
</body>
</html>';

	EXEC msdb.dbo.sp_send_dbmail	
			@profile_name = @MailProfile,
			@recipients = @MailRecipiants,
			@copy_recipients = @copy_recipients,
			@subject = @MailSubject,
			@body = @MailBodey,
			@body_format = HTML,
			@exclude_query_output = 1;



    FETCH NEXT FROM cuJobMailStatus INTO @Database,@Code,@Status,@NOTimes,@Date,@Error,@MailRecipiants;
END

CLOSE cuJobMailStatus;
DEALLOCATE cuJobMailStatus;

END

BEGIN
  SET NOCOUNT ON;

  DECLARE @conversation_handle UNIQUEIDENTIFIER;
  DECLARE @message_body XML;
  DECLARE @message_type_name sysname;

  WHILE (1=1)
  BEGIN

    WAITFOR
    (
      RECEIVE TOP (1)
        @conversation_handle = conversation_handle,
        @message_body = CAST(message_body AS XML),
        @message_type_name = message_type_name
      FROM JobProcessingQueue
    ), TIMEOUT 5000;

    IF (@@ROWCOUNT = 0)
    BEGIN
      BREAK;
    END

    IF @message_type_name = N'JobRequest'
    BEGIN
      -- Handle complex long processing here
      -- For demonstration we'll pull the account number and send a reply back only
      DECLARE @JobNumber INT = @message_body.value('(JobRequest/JobNumber)[1]', 'INT');
      -- Build reply message and send back
      END CONVERSATION @conversation_handle;
	  EXEC [Schedule].[usp_ScheduleTask_RunJob] @JobNumber

    END

    -- If end dialog message, end the dialog
    ELSE IF @message_type_name = N'http://schemas.microsoft.com/SQL/ServiceBroker/EndDialog'
    BEGIN
      END CONVERSATION @conversation_handle;
    END

    -- If error message, log and end conversation
    ELSE IF @message_type_name = N'http://schemas.microsoft.com/SQL/ServiceBroker/Error'
    BEGIN
      -- Log the error code and perform any required handling here
      -- End the conversation for the error
      END CONVERSATION @conversation_handle;
    END

  END
END

BEGIN
  SET NOCOUNT ON;

  DECLARE @conversation_handle UNIQUEIDENTIFIER;
  DECLARE @message_body XML;
  DECLARE @message_type_name sysname;

  WHILE (1=1)
  BEGIN

    WAITFOR
    (
      RECEIVE TOP (1)
        @conversation_handle = conversation_handle,
        @message_body = CAST(message_body AS XML),
        @message_type_name = message_type_name
      FROM JobRequestQueue
    ), TIMEOUT 5000;

    IF (@@ROWCOUNT = 0)
    BEGIN
      BREAK;
    END

    IF @message_type_name = N'JobRequest'
    BEGIN
      -- If necessary handle the reply message here
      DECLARE @JobNumber INT = @message_body.value('(JobRequest/JobNumber)[1]', 'INT');
      -- Build reply message and send back
      END CONVERSATION @conversation_handle;
	  IF @JobNumber IS NOT NULL EXEC [Schedule].[usp_ScheduleTask_RunJob] @JobNumber;
      -- Since this is all the work being done, end the conversation to send the EndDialog message
      END CONVERSATION @conversation_handle;
    END

    -- If end dialog message, end the dialog
    ELSE IF @message_type_name = N'http://schemas.microsoft.com/SQL/ServiceBroker/EndDialog'
    BEGIN
       END CONVERSATION @conversation_handle;
    END

    -- If error message, log and end conversation
    ELSE IF @message_type_name = N'http://schemas.microsoft.com/SQL/ServiceBroker/Error'
    BEGIN
       END CONVERSATION @conversation_handle;
    END

  END
END


BEGIN  
    SET NOCOUNT ON;

    DECLARE @cmd NVARCHAR(MAX) = '';
    DECLARE @now TIME = CONVERT(TIME,GETDATE());

    SELECT  @cmd += N'

BEGIN TRY  
       SELECT @StartDate = GETDATE(); 

       EXEC ' + QUOTENAME(ES.[Database]) + N'.' + ES.[StoredProcedure] + N';
       SET @RC = @@ROWCOUNT;
       SELECT @EndDate = GETDATE();

       INSERT [Schedule].[Log_ScheduleTaskLog]
       (ScheduleTaskID, TaskStatus, StartDate, EndDate, EffectedRows, AdditionalInformation)
       VALUES (' + CONVERT(NVARCHAR(10),ES.ID) + N',''successfully'',@StartDate,@EndDate,@RC,NULL)

END TRY
BEGIN CATCH
       SELECT @EndDate = GETDATE();
       SELECT @err = ERROR_MESSAGE();
       INSERT [Schedule].[Log_ScheduleTaskLog] 
       (ScheduleTaskID, TaskStatus, StartDate, EndDate, EffectedRows, AdditionalInformation)
       VALUES (' + CONVERT(NVARCHAR(10),ES.ID) + N',''Failed'',@StartDate,GETDATE(),NULL,@err)

END CATCH
'
    FROM    [Schedule].[Schedule_Configuration]ES
            INNER JOIN sys.databases D WITH(NOLOCK) ON D.name = ES.[Database]
    WHERE   @JobID = ES.ID
            AND ES.IsActive = 1
            AND D.[state] = 0;


    SET @cmd = N'
DECLARE @RC INT = 0,
              @err NVARCHAR(2048) = '''',
              @StartDate DATETIME ,
              @EndDate DATETIME;
' + @cmd;
    EXEC sys.sp_executesql @cmd;
END

BEGIN
	SET NOCOUNT ON;
	DECLARE @FromService SYSNAME = 'JobRequestService',
			@ToService   SYSNAME = 'JobProcessingService',
			@Contract    SYSNAME = 'JobContract',
			@MessageType SYSNAME = 'JobRequest',
			@conversation_handle UNIQUEIDENTIFIER;

  BEGIN TRANSACTION;

  BEGIN DIALOG CONVERSATION @conversation_handle
    FROM SERVICE @FromService
    TO SERVICE @ToService
    ON CONTRACT @Contract
    WITH ENCRYPTION = OFF;

  SEND ON CONVERSATION @conversation_handle
    MESSAGE TYPE @MessageType(@MessageBody);

  COMMIT TRANSACTION;
END

BEGIN SET NOCOUNT ON; DECLARE @CPU_Core INT IF OBJECT_ID('tempdb..#Exeption') IS NOT NULL DROP TABLE #Exeption; -- Collecte all errors from checks. CREATE TABLE #Exeption ( ServerName sysname COLLATE SQL_Latin1_General_CP1_CI_AS, Type NVARCHAR(255) COLLATE SQL_Latin1_General_CP1_CI_AS, Message NVARCHAR(4000) COLLATE SQL_Latin1_General_CP1_CI_AS, URL VARCHAR(512) COLLATE SQL_Latin1_General_CP1_CI_AS, Severity sysname COLLATE SQL_Latin1_General_CP1_CI_AS, Action NVARCHAR(4000) COLLATE SQL_Latin1_General_CP1_CI_AS ); -- Get VLF Counts for all databases on the instance (Query 25) (VLF Counts) -- (adapted from Michelle Ufford) CREATE TABLE #VLFInfo (RecoveryUnitID int, FileID int, FileSize bigint, StartOffset bigint, FSeqNo bigint, [Status] bigint, Parity bigint, CreateLSN numeric(38));
CREATE TABLE #VLFCountResults(DatabaseName sysname, VLFCount int);
EXEC sp_MSforeachdb N'Use [?];
INSERT INTO #VLFInfo EXEC sp_executesql N''DBCC LOGINFO([?])WITH NO_INFOMSGS'';
INSERT INTO #VLFCountResults SELECT DB_NAME(), COUNT(*) FROM #VLFInfo;
TRUNCATE TABLE #VLFInfo;' INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Database' Type, 'High VLF counts can affect write performance and they can make database restores and recovery take much longer. ' + DatabaseName + '(VLF:' + CONVERT(VARCHAR(10),VLFCount ) + ')' Message, NULL URL, 'Minor' Severity, 'Try to keep your VLF counts under 200 in most cases' Action FROM #VLFCountResults WHERE VLFCount > 200 ORDER BY VLFCount DESC; -- High VLF counts can affect write performance -- and they can make database restores and recovery take much longer -- Try to keep your VLF counts under 200 in most cases DROP TABLE #VLFInfo; DROP TABLE #VLFCountResults;
---- SQL Server Error log. This query might take a few seconds ---- if you have not recycled your error log recently --CREATE TABLE #Manufacturer (LogDate DATETIME,ProcessInfo sysname,Text VARCHAR(4000)) --INSERT #Manufacturer --EXEC xp_readerrorlog 0, 1, "Manufacturer";
--SELECT TOP 1 1 FROM #Manufacturer WHERE Text LIKE '%VMware%' IF EXISTS(SELECT TOP 1 1 FROM sys.dm_os_sys_info WHERE virtual_machine_type = 1) /*HYPERVISOR*/ BEGIN SELECT @CPU_Core = cpu_count/hyperthread_ratio FROM sys.dm_os_sys_info WITH (NOLOCK);
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'CPU' Type, 'CPU resources ratio of the physical cores is 1:' + CONVERT(VARCHAR(10), cpu_count / hyperthread_ratio) Message , 'http://www.vmware.com/files/pdf/solutions/SQL_Server_on_VMware-Best_Practices_Guide.pdf' URL, 'Major' Severity, 'Provide CPU resources by maintaining a 1:1 ratio of the physical cores' Action FROM sys.dm_os_sys_info WITH (NOLOCK) WHERE hyperthread_ratio != 1 AND cpu_count / hyperthread_ratio > 1; --INSERT #Exeption --SELECT @@SERVERNAME ServerName, -- 'Memory' Type, -- 'CPU resources ratio of the physical cores is ' + CONVERT(VARCHAR(10),cpu_count/hyperthread_ratio ) + ':' + CONVERT(VARCHAR(10),hyperthread_ratio ) Message, -- 'http://www.vmware.com/files/pdf/solutions/SQL_Server_on_VMware-Best_Practices_Guide.pdf' URL, -- 'Major' Severity, -- 'Provide CPU resources by maintaining a 1:1 ratio of the physical cores' Action --FROM sys.dm_os_sys_info WITH (NOLOCK) --WHERE hyperthread_ratio != 1
/*--------------------------------------------------------------------------------------------------- 4.3.4.2. Tier 1 SQL Server workloads Achieving adequate performance is the primary goal. Consider setting the memory reservation equal to the provisioned memory, to avoid ballooning or swapping. When calculating the amount of memory to provision for the virtual machine, use the following formulas: VM Memory = SQL Max Server Memory + ThreadStack + OS Mem + VM Overhead ThreadStack = SQL Max Worker Threads * ThreadStackSize ThreadStackSize = 1MB on x86 = 2MB on x64 = 4MB on IA64 OS Mem: 1GB for every 4 CPU Cores
*/ DECLARE @OS_Mem FLOAT, @ThreadStack INT, @vCPU INT, @PhysicalMemory INT, @VMOverhead INT
IF OBJECT_ID('[Tempdb].[dbo].[#_XPMSVER]') IS NOT NULL DROP TABLE [dbo].[#_XPMSVER] CREATE TABLE [dbo].[#_XPMSVER]([IDX] [int] NULL ,[NAME] [varchar](100) NULL ,[INT_VALUE] [float] NULL ,[C_VALUE] [varchar](128) NULL ) ON [PRIMARY] INSERT INTO [#_XPMSVER] EXEC( 'master.dbo.xp_msver') DECLARE @PlatformType INT SELECT @PlatformType = CASE WHEN C_VALUE LIKE '%x86%' THEN 1 WHEN C_VALUE LIKE '%x64%' THEN 2 WHEN C_VALUE LIKE '%IA64%' THEN 4 END FROM [#_XPMSVER] WHERE name = 'Platform'
--OS Mem: 1GB for every 4 CPU Cores SELECT @OS_Mem = (cpu_count/hyperthread_ratio)/4.0, @vCPU = cpu_count, @PhysicalMemory = physical_memory_kb/1024 --MB FROM sys.dm_os_sys_info WITH (NOLOCK);
SELECT @ThreadStack = max_workers_count * @PlatformType FROM sys.dm_os_sys_info WITH (NOLOCK);
SELECT @VMOverhead = mo.Memory_MB FROM [Server].[VM_MemoryOverhead] mo WHERE mo.vCPU = CASE WHEN @vCPU > 8 THEN 8 ELSE @vCPU END AND @PhysicalMemory BETWEEN VM_Memory_MB_From AND VM_Memory_MB_Till;
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Memory' Type, 'Minimum Memory For This VM is ' + CONVERT(VARCHAR(50),@PhysicalMemory ) + 'MB and does not meet VM requirements: ' + CONVERT(VARCHAR(50),(CONVERT(BIGINT,value)) + @ThreadStack + @OS_Mem + @VMOverhead ) Message, 'http://www.vmware.com/files/pdf/solutions/SQL_Server_on_VMware-Best_Practices_Guide.pdf' URL, 'Major' Severity, 'VM Memory = SQL Max Server Memory + ThreadStack + OS Mem + VM Overhead ' Action FROM sys.configurations WITH (NOLOCK) WHERE name = 'max server memory (MB)' AND (CONVERT(BIGINT,value)) + @ThreadStack + @OS_Mem + @VMOverhead > @PhysicalMemory and value != '2147483647'; --------------------------------------------------------------------------------------------------- END
-- Memory INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Memory' Type, 'Max server memory configure worng ' + CONVERT(varchar(25),C.value) + 'MB. Physical memory: ' + CONVERT(varchar(25),I.physical_memory_kb/1024) + 'MB. You should leave about 10-12% for OS.' Message, NULL URL, 'Minor' Severity, 'Configure max server memory' Action FROM sys.configurations C WITH (NOLOCK) CROSS JOIN sys.dm_os_sys_info I WITH (NOLOCK) CROSS APPLY (SELECT TOP 1 CASE WHEN value = 1 THEN 0.12 ELSE 0.1 END Factor FROM sys.configurations iC WITH (NOLOCK) WHERE iC.name = 'clr enabled') uf WHERE C.name = 'max server memory (MB)' AND C.value > CONVERT(INT,((I.physical_memory_kb - (CASE WHEN (I.physical_memory_kb * uf.Factor) < 4194304 THEN 4194304.0 ELSE I.physical_memory_kb * uf.Factor END))/1024))
--Average Page Life Expectancy /* For those of you not familiar with Page Life Expectancy (PLE), this is the length of time that a database page will stay in the buffer cache without references. Microsoft recommends a minimum target of 300 seconds for PLE, which is roughly (5) minutes. I have to admit that even in my own environment, we rarely see PLE more than (3) to (4) minutes. I wondered what would the average DBA do in a situation where they do not have the luxury of using a 3rd party monitoring tool to capture (PLE)? In this post I decided to share a useful script that I wrote that will sample the DMV sys.dm_os_performance_counters table to provide an average PLE captured in (1) minute intervals. I hope this query will prove useful for those DBA's that do not have a 3rd party monitoring tool, or find themselves in a situation where they can only rely on a query to give them the results. */
/****************************************************************************** NOTES: This script provides a sampling of PLE based on (1) minute intervals from sys.dm_os_performance_counters. Originally written on December 29, 2012 by Akhamie Patrick *******************************************************************************/
DECLARE @counter INT --This will be used to iterate the sampling loop for the PLE measure. SET @counter = 0 CREATE TABLE #pleSample ( CaptureTime DATETIME , PageLifeExpectancy BIGINT ) WHILE @counter < 30 --Sampling will run approximately 1 minute. BEGIN --Captures Page Life Expectancy from sys.dm_os_performance_counters INSERT INTO #pleSample ( CaptureTime , PageLifeExpectancy ) SELECT CURRENT_TIMESTAMP , cntr_value FROM sys.dm_os_performance_counters WHERE [object_name] = N'SQLServer:Buffer Manager' AND counter_name = N'Page life expectancy' SET @counter = @counter + 1 WAITFOR DELAY '000:00:02' END IF OBJECT_ID('tempdb..#PLE') IS NOT NULL DROP TABLE #PLE --This query will return the average PLE based on a 1 minute sample. SELECT AVG(PageLifeExpectancy) AS [AveragePageLifeExpectancy] INTO #PLE FROM #pleSample IF OBJECT_ID('tempdb..#pleSample') IS NOT NULL DROP TABLE #pleSample
INSERT #Exeption SELECT @@SERVERNAME ServerName , 'Memory' Type , 'Page life expectancy(PLE) is to low - ' + CONVERT(VARCHAR(25), [AveragePageLifeExpectancy]) + 'sec. Physical memory: ' + CONVERT(VARCHAR(25), @PhysicalMemory) + 'MB' Message , NULL URL , 'Minor' Severity , 'PLE is a good measurement of memory pressure. Higher PLE is better. Watch the trend, not the absolute value.(300 * 4GB(on MAX server mem))' Action FROM #PLE CROSS APPLY ( SELECT CONVERT(INT,CASE WHEN CONVERT(INT,C.value)/1024.0 < (I.physical_memory_kb/1024.0/1024.0) THEN ((CONVERT(INT,C.value)/1024.0)/4) * 300 ELSE ((I.physical_memory_kb/1024.0/1024.0)/4) * 300 END )PLEvalue FROM sys.configurations C WITH (NOLOCK) CROSS JOIN sys.dm_os_sys_info I WITH (NOLOCK) WHERE C.name = 'max server memory (MB)')T WHERE [AveragePageLifeExpectancy] < t.PLEvalue -- Seconds OPTION (RECOMPILE); IF OBJECT_ID('tempdb..#PLE') IS NOT NULL DROP TABLE #PLE; INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Memory' Type, '"MAX_EVENTS_LIMIT" on XE is set to high(' + CONVERT(VARCHAR(15),ring_buffer_event_count)+ ')' Message, 'http://www.sqlskills.com/blogs/jonathan/why-i-hate-the-ring_buffer-target-in-extended-events/?utm_source=rss&utm_medium=rss&utm_campaign=why-i-hate-the-ring_buffer-target-in-extended-events' URL, 'Major' Severity, 'Set "MAX_EVENTS_LIMIT" to less than ' + CONVERT(VARCHAR(15),ring_buffer_event_count) Action FROM ( SELECT target_data.value('(RingBufferTarget/@eventCount)[1]', 'int') AS ring_buffer_event_count , target_data.value('count(RingBufferTarget/event)', 'int') AS event_node_count FROM ( SELECT CAST(target_data AS XML) AS target_data FROM sys.dm_xe_sessions AS s INNER JOIN sys.dm_xe_session_targets AS st ON s.address = st.event_session_address WHERE s.name = N'system_health' AND st.target_name = N'ring_buffer' ) AS n ) AS t WHERE ring_buffer_event_count > 10000 -- MAX_EVENTS_LIMIT OPTION (RECOMPILE);
----------------------------- Server --------------------------------- --Service INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Server' Type, servicename + ' startup state: ' + startup_type_desc Message, NULL URL, 'Major' Severity, 'Change service startup methud to Automatic' ACTION FROM sys.dm_server_services WITH ( NOLOCK ) WHERE servicename LIKE 'SQL Server%' AND startup_type != 2 --Automatic UNION ALL SELECT @@SERVERNAME ServerName, 'Server' Type, servicename + ' is in state: ' + status_desc Message, NULL URL, 'Major' Severity, 'Start Service' Action FROM sys.dm_server_services WITH ( NOLOCK ) WHERE servicename LIKE 'SQL Server%' AND status != 4 --Running UNION ALL SELECT @@SERVERNAME ServerName, 'Server' Type, servicename + ' service account is differnt from agent service' Message, NULL URL, 'Major' Severity, 'Change Service account of agent service to ' + s.service_account Action FROM sys.dm_server_services s WITH ( NOLOCK ) CROSS JOIN (SELECT service_account FROM sys.dm_server_services WITH ( NOLOCK ) WHERE servicename LIKE 'SQL Server Agent%') t WHERE servicename LIKE 'SQL Server (%' AND t.service_account != s.service_account OPTION ( RECOMPILE );
--configurations INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Server' Type, CONVERT(varchar(40),C.Name) + ' configure worng ' Message, NULL URL, 'Worning' Severity, 'Turn on - ' + CONVERT(varchar(25),C.Name) Action FROM sys.configurations C WITH (NOLOCK) WHERE C.name IN ('optimize for ad hoc workloads','backup compression default') AND C.value = 0 OPTION ( RECOMPILE );
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Server' Type, CONVERT(varchar(40),C.Name) + ' value(' + CONVERT(varchar(25),C.value) + ') != value_in_use(' + CONVERT(varchar(25),C.value_in_use) + ')' Message, NULL URL, 'Worning' Severity, 'Turn on - ' + CONVERT(varchar(25),C.Name) Action FROM sys.configurations C WHERE C.value != C.value_in_use; --IF @@VERSION LIKE '%Microsoft SQL Server 2014%' --EXEC ('INSERT #Exeption --SELECT @@SERVERNAME ServerName, -- ''Server'' Type, -- CONVERT(varchar(40),C.Name) + '' value('' + CONVERT(varchar(25),C.value) + '') != value_in_use('' + CONVERT(varchar(25),C.value_in_use) + '')'' Message, -- NULL URL, -- ''Worning'' Severity, -- ''Turn on - '' + CONVERT(varchar(25),C.Name) Action --FROM sys.configurations C --WHERE C.value != C.value_in_use;'); ------------------------------------------------------------------------------------------- --TempDB Configuration INSERT #Exeption SELECT TOP 1 @@SERVERNAME ServerName, 'Server' Type, 'TempDB files has different sizes' Message, 'http://www.confio.com/logicalread/sql-server-tempdb-best-practices-initial-sizing-w01/#.UzEbJfl_tCw' URL, 'Worning' Severity, 'Change initial size of tempdb' ACTION FROM sys.master_files MF WITH (NOLOCK) CROSS APPLY (SELECT TOP 1 size,file_id FROM sys.master_files WITH (NOLOCK) WHERE database_id = 2 AND type = 0)iMF WHERE database_id = 2 AND type = 0 AND iMF.file_id != MF.file_id AND iMF.size != MF.size UNION ALL SELECT TOP 1 @@SERVERNAME ServerName, 'Server' Type, 'TempDB files are lower then logical CPU count' Message, 'http://www.confio.com/logicalread/sql-server-tempdb-best-practices-multiple-files-w01/#.UzEbCfl_tCw' URL, 'Worning' Severity, 'Use of multiple data files of tempdb' ACTION FROM sys.dm_os_sys_info WITH (NOLOCK) CROSS APPLY (SELECT COUNT_BIG(1) TempDBcnt FROM sys.master_files WITH (NOLOCK) WHERE database_id = 2 AND type = 0) Tmp WHERE cpu_count > Tmp.TempDBcnt
-- Sustained values above 10 suggest further investigation in that area -- High Avg Task Counts are often caused by blocking or other resource contention INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Server' Type, 'Avg Task Count ' + CONVERT(varchar(25),AVG(current_tasks_count)) Message, NULL URL, 'Worning' Severity, 'High Avg Task Counts are often caused by blocking or other resource contention' Action FROM sys.dm_os_schedulers WITH ( NOLOCK ) WHERE scheduler_id < 255 HAVING AVG(current_tasks_count) > 10 UNION ALL -- High Avg Runnable Task Counts are a good sign of CPU pressure SELECT @@SERVERNAME ServerName, 'CPU' Type, 'Avg Runnable Task Count ' + CONVERT(varchar(25),AVG(runnable_tasks_count)) Message, NULL URL, 'Worning' Severity, 'High Avg Runnable Task Counts are a good sign of CPU pressure' Action FROM sys.dm_os_schedulers WITH ( NOLOCK ) WHERE scheduler_id < 255 HAVING AVG(runnable_tasks_count) > 10 UNION ALL -- High Avg Pending DiskIO Counts are a sign of disk pressure SELECT @@SERVERNAME ServerName, 'CPU' Type, 'Avg Pending DiskIO Count ' + CONVERT(varchar(25),AVG(pending_disk_io_count)) Message, NULL URL, 'Worning' Severity, 'High Avg Pending DiskIO Counts are a sign of disk pressure' Action FROM sys.dm_os_schedulers WITH ( NOLOCK ) WHERE scheduler_id < 255 HAVING AVG(pending_disk_io_count) > 10 OPTION ( RECOMPILE ); ---------------------------------------- TraceFlags ---------------------------------------- IF OBJECT_ID('tempdb..#TraceStatus') IS NOT NULL DROP TABLE #TraceStatus; CREATE TABLE #TraceStatus ( TraceFlag VARCHAR(10) , status BIT , Global BIT , Session BIT ); INSERT INTO #TraceStatus EXEC ( ' DBCC TRACESTATUS(-1) WITH NO_INFOMSGS') ---------------------------------------- TraceFlags ---------------------------------------- INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Server' Type, TS.Name Message, NULL URL, 'Minor' Severity, 'Turn Trace flag on' Action FROM (SELECT 'Is TF 1118(Immediately allocate an extent (8 pages)) On' Name,'1118' value --http://www.sqlskills.com/blogs/paul/misconceptions-around-tf-1118/ UNION ALL SELECT 'Is TF 1222(More info about deadlock) On' Name,'1222' value UNION ALL SELECT 'Is TF 3226(Suppress the success messages from backups) On' Name,'3226' value UNION ALL SELECT 'Is TF 3023(BACKUP WITH CHECKSUM) On' Name,'3023' value --http://www.sqlservercentral.com/blogs/nebraska-sql-from-dba_andy/2014/03/25/backup-checksums-and-trace-flag-3023/ UNION ALL SELECT 'Is TF 4199 On' Name,'4199' value) TS LEFT JOIN #TraceStatus GTS ON GTS.TraceFlag = TS.value WHERE GTS.TraceFlag IS NULL
--Error Log file DECLARE @NumErrorLogs INT; EXEC master.dbo.xp_instance_regread N'HKEY_LOCAL_MACHINE', N'Software\Microsoft\MSSQLServer\MSSQLServer', N'NumErrorLogs', @NumErrorLogs OUTPUT
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Server' Type, 'Number of Error Logs is -' + CONVERT(VARCHAR(10),ISNULL(@NumErrorLogs, -1))+ '. Change to 30 or more.' Message, NULL URL, 'Minor' Severity, '/*Configure SQL Server Error Logs*/USE [master] GO EXEC xp_instance_regwrite N''HKEY_LOCAL_MACHINE'', N''Software\Microsoft\MSSQLServer\MSSQLServer'', N''NumErrorLogs'', REG_DWORD, 30 GO' Action WHERE ISNULL(@NumErrorLogs, -1) < 30;
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'JOB' Type, 'JobName: ' + JobName + ' That run on ' + CONVERT(VARCHAR(25),RunDateTime) + ' took - ' + CONVERT(VARCHAR(25),RunDurationMinutes) + ' minutes' Message, NULL URL, 'Minor' Severity, NULL Action FROM (SELECT j.name AS 'JobName' , rdm.RunDateTime, rdm.RunDurationMinutes, ROW_NUMBER() OVER (PARTITION BY j.name ORDER BY rdm.RunDateTime DESC) RN FROM msdb.dbo.sysjobs j INNER JOIN msdb.dbo.sysjobhistory h ON j.job_id = h.job_id CROSS APPLY (SELECT msdb.dbo.agent_datetime(run_date, run_time) AS 'RunDateTime',( ( h.run_duration / 10000 * 3600 + ( h.run_duration / 100 ) % 100 * 60 + run_duration % 100 + 31 ) / 60 ) RunDurationMinutes)rdm WHERE j.enabled = 1 --Only Enabled Jobs AND rdm.RunDateTime > DATEADD(DAY,-3,GETDATE()) AND rdm.RunDurationMinutes > 55)t WHERE T.RN = 1;
-- Storage INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Storage' Type, 'Reads are averaging longer than ' + CASE Type WHEN 1 THEN '10' WHEN 99 THEN '10' ELSE '20' END + 'ms on drive - ' + [Drive] + CASE Type WHEN 1 THEN '(LOG files)' WHEN 99 THEN '(TempDB)' ELSE '(DATA files)' END + ' - ' + CONVERT(VARCHAR(20),RL.[Read Latency]) Message, 'http://technet.microsoft.com/en-us/library/aa995945(v=exchg.80).aspx' URL, 'Major' Severity, CASE Type WHEN 1 THEN 'Transaction log drives The drive that hosts the transaction log should have average write latencies below 10 ms. Spikes in write latencies should be under 50ms. Writes to the transaction log are synchronous. This means that, before a thread in the Store.exe process can perform another task, the thread must wait for the write to complete. Having low write latencies for the transaction logs is important to server performance. The average Read latency to the transaction log drives should be below 20 ms. Spikes in read latency should be under 50ms. Database Log Record Stalls per second should be less than 10. Database Log Threads Waiting should be less than 10. Ordinarily, Exchange servers do not read from the transaction logs. Therefore, the read latencies to that drive do not matter. However, because the transaction log write latencies are so important to Exchange performance, it is recommended that, on large servers, you do not use the drives that host transaction logs for any other purpose. In this case, the rate of reads (as measured by LogicalDisk\Disk Reads/sec) should be minimal compared to the rate of writes (LogicalDisk\Disk Writes/sec). The Exchange Server Analyzer will detect if the ratio of reads to writes on the transaction log drive is greater than 0.10 (more than one read for every ten writes). If there are more than 0.10 reads for every write, you should identify which application is reading from the transaction log drive, and then prevent this action from occurring.' WHEN 99 THEN 'TEMP and TMP drives The latency for the drives that contain the TEMP and TMP directories should have read and write latencies below 10 ms. The maximum value for the read or write latency should be below 50 ms.' ELSE 'Database drives The acceptable latency for the drives that contain Exchange database files ( *edb, and *stm files) are as below (higher values indicate a disk bottleneck): The maximum value for Logical Disk\Avg. Disk sec/Read on a database drive should be less than 50 ms. (0.050 seconds) The average value for Logical Disk\Avg. Disk sec/Read on a database drive should be less than 20 ms. (0.020 seconds)' END + ' --------------------------------------------------------------------------------------------------------------------------------------- If you are running a RAID-5 disk array, you may want to change to a RAID-10 disk array to improve the available supported IOPS of the disk subsystem. To improve the available supported IOPS, consider adding additional disks to your disk system. ' Action FROM ( SELECT LEFT(mf.physical_name, 2) AS Drive ,CASE WHEN MF.database_id = 2 THEN 99 ELSE MF.type END [type], SUM(num_of_reads) AS num_of_reads , SUM(io_stall_read_ms) AS io_stall_read_ms , SUM(num_of_writes) AS num_of_writes , SUM(io_stall_write_ms) AS io_stall_write_ms , SUM(num_of_bytes_read) AS num_of_bytes_read , SUM(num_of_bytes_written) AS num_of_bytes_written , SUM(io_stall) AS io_stall FROM sys.dm_io_virtual_file_stats(NULL, NULL) AS vfs INNER JOIN sys.master_files AS mf WITH ( NOLOCK ) ON vfs.database_id = mf.database_id AND vfs.file_id = mf.file_id WHERE MF.database_id NOT IN (1,3,4) -- Master,MSDB,Model GROUP BY LEFT(mf.physical_name, 2),CASE WHEN MF.database_id = 2 THEN 99 ELSE MF.type END ) AS tab CROSS APPLY (SELECT CASE WHEN num_of_reads = 0 THEN 0 ELSE ( io_stall_read_ms / num_of_reads ) END AS [Read Latency], CASE WHEN io_stall_write_ms = 0 THEN 0 ELSE ( io_stall_write_ms / num_of_writes ) END AS [Write Latency] , CASE WHEN ( num_of_reads = 0 AND num_of_writes = 0 ) THEN 0 ELSE ( io_stall / ( num_of_reads + num_of_writes ) ) END AS [Overall Latency] , CASE WHEN num_of_reads = 0 THEN 0 ELSE ( num_of_bytes_read / num_of_reads ) END AS [Avg Bytes/Read] , CASE WHEN io_stall_write_ms = 0 THEN 0 ELSE ( num_of_bytes_written / num_of_writes ) END AS [Avg Bytes/Write] , CASE WHEN ( num_of_reads = 0 AND num_of_writes = 0 ) THEN 0 ELSE ( ( num_of_bytes_read + num_of_bytes_written ) / ( num_of_reads + num_of_writes ) ) END AS [Avg Bytes/Transfer])RL
WHERE (tab.type IN (1,99) AND RL.[Read Latency] > 10) OR (tab.type != 1 AND RL.[Read Latency] > 20) UNION ALL SELECT @@SERVERNAME ServerName, 'Storage' Type, 'Writes are averaging longer than ' + CASE Type WHEN 1 THEN '10' WHEN 99 THEN '10' ELSE '20' END + 'ms on drive - ' + [Drive] + CASE Type WHEN 1 THEN '(LOG files)' WHEN 99 THEN '(TempDB)' ELSE '(DATA files)' END + ' - ' + CONVERT(VARCHAR(20),RL.[Write Latency]) Message, 'http://technet.microsoft.com/en-us/library/aa995945(v=exchg.80).aspx' URL, 'Major' Severity, CASE Type WHEN 1 THEN 'Transaction log drives The drive that hosts the transaction log should have average write latencies below 10 ms. Spikes in write latencies should be under 50ms. Writes to the transaction log are synchronous. This means that, before a thread in the Store.exe process can perform another task, the thread must wait for the write to complete. Having low write latencies for the transaction logs is important to server performance. The average Read latency to the transaction log drives should be below 20 ms. Spikes in read latency should be under 50ms. Database Log Record Stalls per second should be less than 10. Database Log Threads Waiting should be less than 10. Ordinarily, Exchange servers do not read from the transaction logs. Therefore, the read latencies to that drive do not matter. However, because the transaction log write latencies are so important to Exchange performance, it is recommended that, on large servers, you do not use the drives that host transaction logs for any other purpose. In this case, the rate of reads (as measured by LogicalDisk\Disk Reads/sec) should be minimal compared to the rate of writes (LogicalDisk\Disk Writes/sec). The Exchange Server Analyzer will detect if the ratio of reads to writes on the transaction log drive is greater than 0.10 (more than one read for every ten writes). If there are more than 0.10 reads for every write, you should identify which application is reading from the transaction log drive, and then prevent this action from occurring.' WHEN 99 THEN 'TEMP and TMP drives The latency for the drives that contain the TEMP and TMP directories should have read and write latencies below 10 ms. The maximum value for the read or write latency should be below 50 ms.' ELSE 'Database drives The acceptable latency for the drives that contain Exchange database files ( *edb, and *stm files) are as below (higher values indicate a disk bottleneck): The maximum value for Logical Disk\Avg. Disk sec/Read on a database drive should be less than 50 ms. (0.050 seconds) The average value for Logical Disk\Avg. Disk sec/Read on a database drive should be less than 20 ms. (0.020 seconds)' END + ' --------------------------------------------------------------------------------------------------------------------------------------- If you are running a RAID-5 disk array, you may want to change to a RAID-10 disk array to improve the available supported IOPS of the disk subsystem. To improve the available supported IOPS, consider adding additional disks to your disk system. ' Action FROM ( SELECT LEFT(mf.physical_name, 2) AS Drive ,CASE WHEN MF.database_id = 2 THEN 99 ELSE MF.type END [type], SUM(num_of_reads) AS num_of_reads , SUM(io_stall_read_ms) AS io_stall_read_ms , SUM(num_of_writes) AS num_of_writes , SUM(io_stall_write_ms) AS io_stall_write_ms , SUM(num_of_bytes_read) AS num_of_bytes_read , SUM(num_of_bytes_written) AS num_of_bytes_written , SUM(io_stall) AS io_stall FROM sys.dm_io_virtual_file_stats(NULL, NULL) AS vfs INNER JOIN sys.master_files AS mf WITH ( NOLOCK ) ON vfs.database_id = mf.database_id AND vfs.file_id = mf.file_id WHERE MF.database_id NOT IN (1,3,4) -- Master,MSDB,Model GROUP BY LEFT(mf.physical_name, 2),CASE WHEN MF.database_id = 2 THEN 99 ELSE MF.type END ) AS tab CROSS APPLY (SELECT CASE WHEN num_of_reads = 0 THEN 0 ELSE ( io_stall_read_ms / num_of_reads ) END AS [Read Latency], CASE WHEN io_stall_write_ms = 0 THEN 0 ELSE ( io_stall_write_ms / num_of_writes ) END AS [Write Latency] , CASE WHEN ( num_of_reads = 0 AND num_of_writes = 0 ) THEN 0 ELSE ( io_stall / ( num_of_reads + num_of_writes ) ) END AS [Overall Latency] , CASE WHEN num_of_reads = 0 THEN 0 ELSE ( num_of_bytes_read / num_of_reads ) END AS [Avg Bytes/Read] , CASE WHEN io_stall_write_ms = 0 THEN 0 ELSE ( num_of_bytes_written / num_of_writes ) END AS [Avg Bytes/Write] , CASE WHEN ( num_of_reads = 0 AND num_of_writes = 0 ) THEN 0 ELSE ( ( num_of_bytes_read + num_of_bytes_written ) / ( num_of_reads + num_of_writes ) ) END AS [Avg Bytes/Transfer])RL
WHERE (tab.type IN (1,99) AND RL.[Write Latency] > 10) OR (tab.type != 1 AND RL.[Write Latency] > 20) OPTION ( RECOMPILE );
INSERT #Exeption SELECT DISTINCT @@SERVERNAME ServerName, 'Storage' Type, vs.volume_mount_point + ' has ' + CAST(CAST(vs.available_bytes AS FLOAT) / CAST(vs.total_bytes AS FLOAT) * 100 AS VARCHAR(50)) + '% free space.' Message, NULL URL, 'Minor' Severity, 'Check what files located in ' + vs.volume_mount_point Action FROM sys.master_files AS f WITH ( NOLOCK ) CROSS APPLY sys.dm_os_volume_stats(f.database_id, f.[file_id]) AS vs WHERE CAST(CAST(vs.available_bytes AS FLOAT) / CAST(vs.total_bytes AS FLOAT) AS DECIMAL(18,2)) < 0.1 OPTION ( RECOMPILE );
--Your SQL Data and Log Drives Need a 1024Kb Starting offset, and a 64Kb Block Size /* so theres only one disk (Disk #0). Youre only interested in the Partition #0 for each disk, because thats the partition at the start of the disk (which contains the partition offset). My laptop has a proper starting offset: 1048576 bytes = 1048576/1024 = 1024KB. */ DECLARE @PS VARCHAR(4000) = 'powershell.exe "get-wmiobject win32_diskpartition | select name, startingoffset | foreach{$_.name+''|''+$_.startingoffset/1024+''*''}"'
DECLARE @output TABLE ( line VARCHAR(255) ); BEGIN TRY INSERT @output EXEC xp_cmdshell @PS; END TRY BEGIN CATCH --{TODO: } END CATCH
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Storage' Type, '"Starting Offset" on ' + RTRIM(LTRIM(SUBSTRING(line, 1, CHARINDEX('|', line) - 1))) + ' is - ' + SO.StartingOffset + 'Kb' Message, 'http://technet.microsoft.com/en-us/library/cc966412.aspx http://www.midnightdba.com/Jen/2014/04/decree-set-your-partition-offset-and-block-size-make-sql-server-faster/' URL, 'Minor' Severity, 'Ask your SAN guy to change the drive "Starting Offset" to 1024Kb' Action FROM @output CROSS APPLY (SELECT TOP 1 RTRIM(LTRIM(SUBSTRING(line, CHARINDEX('|', line) + 1, ( CHARINDEX('*', line) - 1) - CHARINDEX('|', line)))) AS [StartingOffset]) SO WHERE line IS NOT NULL AND line LIKE '%Partition #0%' AND SO.StartingOffset != '1024' ORDER BY 1;
--64kb Block Size DECLARE @cmd NVARCHAR(max) = '' SELECT @cmd += ' INSERT @output exec master..xp_cmdshell ''echo 1 > ' + t.physical_name + ':\TestFile.txt'' INSERT @output exec master..xp_cmdshell ''dir ' + t.physical_name + ':\TestFile.txt''
SELECT @BlockSize = REPLACE(REPLACE(SUBSTRING(line,charINDEX('')'',line)+1,LEN(line)-5),'' '',''''),''bytes'','''') FROM @output WHERE line LIKE ''%1 File%''
INSERT @output exec master..xp_cmdshell ''del ' + t.physical_name + ':\TestFile.txt''
