DECLARE @DISKS TABLE ( id INT IDENTITY , [DiskName] VARCHAR(10) , [Label] VARCHAR(200), [BlockSize] VARCHAR(200) )
INSERT INTO @DISKS SELECT RTRIM(LTRIM(SUBSTRING(line, 1, CHARINDEX('|', line) - 1))) AS drivename , RTRIM(LTRIM(SUBSTRING(line, CHARINDEX('&', line) + 1, ( CHARINDEX('^', line) - 1 ) - CHARINDEX('&', line)))) AS 'Label', RTRIM(LTRIM(SUBSTRING(line, CHARINDEX('^', line) + 1, ( CHARINDEX('*', line) - 1) - CHARINDEX('^', line)))) AS [BlockSize] FROM @output WHERE line LIKE '[A-Z][:]%' ORDER BY drivename
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Storage' Type, '"Block Size" on ' + DiskName + ' is - ' + [BlockSize] + 'Kb' Message, 'http://technet.microsoft.com/en-us/library/cc966412.aspx http://www.midnightdba.com/Jen/2014/04/decree-set-your-partition-offset-and-block-size-make-sql-server-faster/' URL, 'Minor' Severity, 'Ask from you SAN guy to set DATA and Log Drives to change the "Blok Size" to 64Kb Block Size' Action FROM @DISKS WHERE [BlockSize] < 64; -------------------------------------------------------- DECLARE @command VARCHAR(255)
SET @command = 'dir "%SystemRoot%\system32\config\software"'
INSERT INTO @output EXEC master.dbo.xp_cmdshell @command;
INSERT #Exeption SELECT @@SERVERNAME ServerName, 'Server' Type, 'Serious bug in SQL Server 2012 SP1, due to msiexec process keep running. registry file grow to 2GB' Message, 'http://connect.microsoft.com/SQLServer/feedback/details/770630/msiexec-exe-processes-keep-running-after-installation-of-sql-server-2012-sp1' URL, 'Minor' Severity, 'http://rusanu.com/2013/02/15/registry-bloat-after-sql-server-2012-sp1-installation/' Action FROM @output CROSS APPLY (SELECT LTRIM( REPLACE( SUBSTRING(line, CHARINDEX(')', line) + 1, LEN(line)) , ',', '') ) TrimLine) TL WHERE line LIKE '%File(s)%bytes' AND CONVERT (INT,CONVERT (INT,SUBSTRING(TL.TrimLine,0,CHARINDEX(' ', TL.TrimLine)))/1048576.0/128) = 2 -- 2GB AND SERVERPROPERTY('productversion') > '11.0.3000.0'
