SELECT * FROM #Exeption ORDER BY Type,Severity /* TODO -- /* --18272 --3634 The operating system returned the error '3(The system cannot find the path specified.)' while attempting 'DeleteFile' on 'D:\SQL\BACKUP\RestoreCheckpointDB23.CKP'. During restore restart, an I/O error occurred on checkpoint file 'D:\SQL\BACKUP\RestoreCheckpointDB24.CKP' (operating system error 3(The system cannot find the path specified.)). The statement is proceeding but cannot be restarted. Ensure that a valid storage location exists for the checkpoint file. */
DECLARE @instance_name NVARCHAR(200) , @system_instance_name NVARCHAR(200) , @registry_key NVARCHAR(512)
SET @instance_name = COALESCE(CONVERT(NVARCHAR(20), SERVERPROPERTY('InstanceName')),'MSSQLSERVER'); EXEC master.dbo.xp_regread N'HKEY_LOCAL_MACHINE',N'Software\Microsoft\Microsoft SQL Server\Instance Names\SQL',@instance_name, @system_instance_name OUTPUT; SET @registry_key = N'Software\Microsoft\Microsoft SQL Server\' + @system_instance_name + '\MSSQLServer'; DECLARE @BackupDirectory VARCHAR(100) --Default Backup Directory Path Check EXEC master..xp_regread @rootkey='HKEY_LOCAL_MACHINE', @key=@registry_key, @value_name='BackupDirectory', @BackupDirectory=@BackupDirectory OUTPUT SELECT @BackupDirectory -- CHECK IF THE PATH EXISTS!! /* --How to Fix EXEC master..xp_regwrite @rootkey='HKEY_LOCAL_MACHINE', @key=@registry_key, @value_name='BackupDirectory', @type='REG_SZ', @value= -- HERE YOU NEED TO WRITE THE NEW PATH */ --Default Log Path Check DECLARE @DefaultLog VARCHAR(100) EXEC master..xp_regread @rootkey='HKEY_LOCAL_MACHINE', @key=@registry_key, @value_name='DefaultLog', @DefaultLog=@DefaultLog OUTPUT SELECT @DefaultLog-- CHECK IF THE PATH EXISTS!! /* --How to Fix EXEC master..xp_regwrite @rootkey='HKEY_LOCAL_MACHINE', @key=@registry_key, @value_name='DefaultLog', @type='REG_SZ', @value='L:\SQL\Log' */
*/ END

BEGIN SET NOCOUNT ON;
SELECT GETDATE() [DateTaken],b.session_id 'Session ID', CAST(Db_name(a.database_id) AS VARCHAR(20)) 'Database Name', c.command, Substring(st.TEXT, ( c.statement_start_offset / 2 ) + 1, ( ( CASE c.statement_end_offset WHEN -1 THEN Datalength(st.TEXT) ELSE c.statement_end_offset END - c.statement_start_offset ) / 2 ) + 1) statement_text, Coalesce(Quotename(Db_name(st.dbid)) + N'.' + Quotename( Object_schema_name(st.objectid, st.dbid)) + N'.' + Quotename(Object_name(st.objectid, st.dbid)), '') command_text, c.wait_type, c.wait_time, a.database_transaction_log_bytes_used / 1024.0 / 1024.0 'MB used', a.database_transaction_log_bytes_used_system / 1024.0 / 1024.0 'MB used system', a.database_transaction_log_bytes_reserved / 1024.0 / 1024.0 'MB reserved', a.database_transaction_log_bytes_reserved_system / 1024.0 / 1024.0 'MB reserved system', a.database_transaction_log_record_count 'Record count' FROM sys.dm_tran_database_transactions a INNER JOIN sys.dm_tran_session_transactions b ON a.transaction_id = b.transaction_id INNER JOIN sys.dm_exec_requests c ON c.database_id = a.database_id AND c.session_id = b.session_id AND c.transaction_id = a.transaction_id CROSS APPLY sys.Dm_exec_sql_text(c.sql_handle) AS st WHERE a.database_id > 4
ORDER BY 'MB used' DESC; END

