	SET @AlertName = N'Error Number 825';
	IF NOT EXISTS(SELECT TOP 1 1 FROM msdb.dbo.sysalerts WHERE name = @AlertName)
	BEGIN

		EXEC msdb.dbo.sp_add_alert @name=@AlertName,
			@message_id=825,
