	------------------------------------------------------
	--  create jobs                                     --
	------------------------------------------------------

	DECLARE @jobId BINARY(16),
			@job_id UNIQUEIDENTIFIER, 
			@ReturnCode INT = 0


	SELECT @job_id = job_id FROM msdb.dbo.sysjobs_view WHERE name = N'_Admin_ :: CycleErrorLog'
	IF NOT @job_id IS null
		EXEC msdb.dbo.sp_delete_job @job_id = @job_id, @delete_unused_schedule = 1
	SET @job_id = NULL;
	-- add category
	IF NOT EXISTS (SELECT name FROM msdb.dbo.syscategories WHERE  name = @Operator AND category_class = 1) 
		BEGIN
			EXEC msdb.dbo.sp_add_category @class = N'JOB',@type = N'LOCAL',@name = @Operator
		END


---------------------------------------------------------------------------------------------------------------------

	IF NOT EXISTS(SELECT TOP 1 1 FROM msdb.dbo.sysjobs WHERE name = '_Admin_ :: CycleErrorLog')
	BEGIN
		SET @jobId = null
		EXEC  msdb.dbo.sp_add_job 
				@job_name=N'_Admin_ :: CycleErrorLog', 
				@enabled=1, 
				@notify_level_eventlog=0, 
				@notify_level_email=0, 
				@notify_level_netsend=0, 
				@notify_level_page=0, 
				@delete_level=0, 
				@description=N'sp_cycle_errorlog.', 
				@category_name=@Operator, 
				@owner_login_name=N'sa', 
				@job_id = @jobId OUTPUT

		EXEC msdb.dbo.sp_add_jobstep 
				@job_id=@jobId, 
				@step_name=N'CycleErrorLog', 
				@step_id=1, 
				@cmdexec_success_code=0, 
				@on_success_action=1, 
				@on_success_step_id=0, 
				@on_fail_action=2, 
				@on_fail_step_id=0, 
				@retry_attempts=0, 
				@retry_interval=0, 
				@os_run_priority=0, @subsystem=N'TSQL', 
				@command=N'EXEC sp_cycle_errorlog;', 
				@database_name=N'master', 
				@flags=0

		EXEC msdb.dbo.sp_update_job @job_id = @jobId, @start_step_id = 1

		EXEC msdb.dbo.sp_add_jobschedule @job_id=@jobId, @name=N'MidNight', 
				@enabled=1, 
				@freq_type=4, 
				@freq_interval=1, 
				@freq_subday_type=1, 
				@freq_subday_interval=0, 
				@freq_relative_interval=0, 
				@freq_recurrence_factor=0, 
				@active_start_date=20140331, 
				@active_end_date=99991231, 
				@active_start_time=1, 
				@active_end_time=235959, 
				@schedule_uid=N'994a993a-227f-463f-9742-f2cba8623403'

		BEGIN TRY 
			EXEC msdb.dbo.sp_add_jobserver @job_id = @jobId, @server_name = @@servername
		END TRY
		BEGIN CATCH
		END CATCH
	END
---------------------------------------------------------------------------------------------------------------------
	IF NOT EXISTS(SELECT TOP 1 1 FROM msdb.dbo.sysjobs WHERE name = '_Admin_ :: GetActiveJobLongRun')
	BEGIN
		SET @jobId = null
		-- _Admin_ :: Data Integrity :: ExecAll
		EXEC  msdb.dbo.sp_add_job @job_name=N'_Admin_ :: GetActiveJobLongRun', 
				@enabled=1, 
				@notify_level_eventlog=0, 
				@notify_level_email=2, 
				@notify_level_netsend=0, 
				@notify_level_page=0, 
				@delete_level=0, 
				@description=N'Run all Data Integrity SPs', 
				@category_name=@Operator, 
				@owner_login_name=N'sa', 
				@notify_email_operator_name=@Operator, @job_id = @jobId OUTPUT

		EXEC msdb.dbo.sp_add_jobstep @job_id=@jobId, @step_name=N'GetActiveJobLongRun', 
				@step_id=1, 
				@cmdexec_success_code=0, 
				@on_success_action=1, 
				@on_success_step_id=0, 
				@on_fail_action=2, 
				@on_fail_step_id=0, 
				@retry_attempts=0, 
				@retry_interval=0, 
				@os_run_priority=0, @subsystem=N'TSQL', 
				@command=N'DECLARE @SendMail int = 1
EXECUTE  [Report].[usp_General_GetActiveJobLongRun] @SendMail', 
				@database_name=N'_Admin', 
				@flags=0

		EXEC msdb.dbo.sp_update_job @job_id = @jobId, @start_step_id = 1


		EXEC msdb.dbo.sp_add_jobschedule @job_id = @jobId, @name=N'EveryMorning', 
				@enabled=1, 
				@freq_type=4, 
				@freq_interval=1, 
				@freq_subday_type=1, 
				@freq_subday_interval=0, 
				@freq_relative_interval=0, 
				@freq_recurrence_factor=1, 
				@active_start_date=20150308, 
				@active_end_date=99991231, 
				@active_start_time=70000, 
				@active_end_time=235959, @schedule_id = @schedule_id OUTPUT
		BEGIN TRY 
			EXEC msdb.dbo.sp_add_jobserver @job_id = @jobId, @server_name = @@servername
		END TRY
		BEGIN CATCH
		END CATCH
	END
---------------------------------------------------------------------------------------------------------------------
	RETURN 1;
END

BEGIN SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
IF @Mode IS NULL SET @Mode = 0; DECLARE @job_id UNIQUEIDENTIFIER DECLARE @Run_id UNIQUEIDENTIFIER = NEWID(); DECLARE @JOBstart_execution_date DATETIME, @JOBstop_execution_date DATETIME, @Error nvarchar(2048) --search for job_id if none was provided SELECT @job_id = COALESCE(@job_id,job_id) FROM msdb.dbo.sysjobs WHERE name like '%' + @job_name + '%';
IF @Date IS NULL BEGIN SELECT TOP 1 @JOBstart_execution_date = t1.start_execution_date ,@JOBstop_execution_date = t1.stop_execution_date FROM msdb.dbo.sysjobactivity t1 WHERE t1.job_id = COALESCE(@job_id,t1.job_id)--If no job_id detected, return last run job ORDER BY last_executed_step_date DESC; END ELSE BEGIN SELECT @JOBstart_execution_date = min(msdb.dbo.agent_datetime(run_date, run_time)) ,@JOBstop_execution_date = MAX(msdb.dbo.agent_datetime(run_date, run_time)) FROM msdb.dbo.sysjobhistory t1 WHERE t1.job_id = COALESCE(@job_id,t1.job_id)--If no job_id detected, return last run job AND CONVERT(date, RTRIM(run_date)) = @Date; END
--Dynamic SQL DECLARE @SELECT NVARCHAR(MAX) = N''; DECLARE @FROM NVARCHAR(MAX) = N''; DECLARE @WHERE NVARCHAR(MAX) = N'';
SELECT @SELECT = N'SELECT t1.name as JobName ,t2.step_id as StepID ,CASE WHEN t2.step_name = ''(Job outcome)'' THEN ''**** Total Status ****'' else t2.step_name end as StepName,
DT.FullDateTime, ' + IIF(@Mode = 0 ,N'dt.JobDurationHHMMSS',N't2.run_duration') + ' [JobDuration], ' + IIF(@Mode != 0 ,N'CAT.[PKG_RunDuration],',N'') + IIF(@Mode IN (4,5) ,N'EM.[Elapsed time],EM.message_time, EM.message_desc, EM.message_source_desc, EM.message,',N'') + N' CASE t2.run_status WHEN 0 THEN ''Failed'' WHEN 1 THEN ''Succeeded'' WHEN 2 THEN ''Retry'' WHEN 3 THEN ''Cancelled'' WHEN 4 THEN ''In Progress'' END as ExecutionStatus ,t2.message as MessageGenerated ' + IIF(@Mode != 0 ,N',CAT.package_name, CONVERT(DATETIME,CAT.start_time) [PKG_StartTime], CONVERT(DATETIME,CAT.end_time ) [PKG_EndTime]',N'') + IIF(@Mode = 2,N' ,CAT.execution_id, CAT.folder_name, CAT.project_name, CAT.package_name, CAT.environment_name, CAT.project_lsn , CAT.executed_as_name , CAT.use32bitruntime , CAT.created_time , CAT.start_time , CAT.end_time , CAT.caller_name , CAT.stopped_by_name , CAT.server_name , CAT.total_physical_memory_kb , CAT.available_physical_memory_kb , CAT.total_page_file_kb , CAT.available_page_file_kb , CAT.cpu_count , CAT.name , CAT.last_deployed_time , CAT.validation_status , CAT.package_id , CAT.[PKG_Name] , CAT.version_major , CAT.version_minor , CAT.version_build , CAT.version_comments , CAT.[PKG_ValidationStatus] ',N'') + ',@Run_id [RunID]' --,CAT.* --'load_stg.dtsx' 'load_stg.dtsx' SELECT @FROM = N' FROM msdb.dbo.sysjobs t1 INNER JOIN msdb.dbo.sysjobhistory t2 ON t1.job_id = t2.job_id CROSS APPLY (SELECT TOP 1 CONVERT(CHAR(10), CAST(STR(t2.run_date,8, 0) AS DATETIME), 121) as RunDate, STUFF(STUFF(RIGHT(''000000'' + CAST ( t2.run_time AS VARCHAR(6 ) ) ,6),5,0,'':''),3,0,'':'') as RunTime, CONVERT(CHAR(10), CAST(STR(t2.run_date,8, 0) AS DATETIME), 121) + '' '' + STUFF(STUFF(RIGHT(''000000'' + CAST ( t2.run_time AS VARCHAR(6 ) ) ,6),5,0,'':''),3,0,'':'') + ''.000'' FullDateTime , JobDurationHHMMSS = STUFF(STUFF(REPLACE(STR(t2.run_duration,7,0),'' '',''0''),4,0,'':''),7,0,'':'') )DT ' + IIF(@Mode != 0 ,N'outer apply (SELECT TOP 1 E.execution_id, E.folder_name, E.project_name, E.package_name, E.environment_name, E.project_lsn , E.executed_as_name , E.use32bitruntime , E.created_time , E.start_time , E.end_time , E.caller_name , E.stopped_by_name , E.server_name , E.total_physical_memory_kb , E.available_physical_memory_kb , E.total_page_file_kb , E.available_page_file_kb , E.cpu_count , F.name , P.last_deployed_time , P.validation_status , PKG.package_id , PKG.name [PKG_Name] , PKG.version_major , PKG.version_minor , PKG.version_build , PKG.version_comments , PKG.validation_status [PKG_ValidationStatus] , DATEDIFF(MINUTE,E.start_time,E.end_time)[PKG_RunDuration] FROM SSISDB.catalog.executions AS E INNER JOIN ssisdb.catalog.folders AS F ON F.name = E.folder_name INNER JOIN SSISDB.catalog.projects AS P ON P.folder_id = F.folder_id AND P.name = E.project_name INNER JOIN SSISDB.catalog.packages AS PKG ON PKG.project_id = P.project_id AND PKG.name = E.package_name WHERE convert(Datetime,start_time) >= DT.FullDateTime--DATEADD(MINUTE,-5,DT.FullDateTime) AND (LOWER(E.package_name) = LOWER(t2.step_name+ ''.dtsx'') COLLATE DATABASE_DEFAULT OR LOWER(E.package_name) = LOWER(replace(t2.step_name,'' '',''_'')+ ''.dtsx'') COLLATE DATABASE_DEFAULT) ORDER BY E.execution_id ASC )Cat',N'') SELECT @FROM += IIF(@Mode IN (4,5),N'
OUTER APPLY (SELECT TOP 1 O.operation_id FROM SSISDB.catalog.event_messages em INNER JOIN SSISDB.catalog.operations o on em.operation_id=o.operation_id WHERE o.object_name = ''OP_ETL'' AND EM.package_name = CAT.package_name ORDER BY O.operation_id DESC )OID OUTER APPLY (SELECT CONVERT(DATETIME,OM.message_time) message_time , EM.message_desc , D.message_source_desc , OM.message ,SUBSTRING(OM.message,charindex(''Elapsed time:'',OM.message) + 13,12)[Elapsed time] FROM SSISDB.catalog.operation_messages AS OM INNER JOIN SSISDB.catalog.operations AS O ON O.operation_id = OM.operation_id INNER JOIN ( VALUES (-1,''Unknown'') , (120,''Error'') , (110,''Warning'') , (70,''Information'') , (10,''Pre-validate'') , (20,''Post-validate'') , (30,''Pre-execute'') , (40,''Post-execute'') , (60,''Progress'') , (50,''StatusChange'') , (100,''QueryCancel'') , (130,''TaskFailed'') , (90,''Diagnostic'') , (200,''Custom'') , (140,''DiagnosticEx Whenever an Execute Package task executes a child package, it logs this event. The event message consists of the parameter values passed to child packages. The value of the message column for DiagnosticEx is XML text.'') , (400,''NonDiagnostic'') , (80,''VariableValueChanged'') ) EM (message_type, message_desc) ON EM.message_type = OM.message_type INNER JOIN ( VALUES (10,''Entry APIs, such as T-SQL and CLR Stored procedures'') , (20,''External process used to run package (ISServerExec.exe)'') , (30,''Package-level objects'') , (40,''Control Flow tasks'') , (50,''Control Flow containers'') , (60,''Data Flow task'') ) D (message_source_type, message_source_desc) ON D.message_source_type = OM.message_source_type WHERE OM.operation_id = OID.operation_id ' + IIF(@Mode = 4 , N'AND OM.message_type = 40' , N'' ) +N')EM ',N'') SELECT @WHERE = N' WHERE --Filter on the most recent job_id t1.job_id = @job_id --Filter out job steps that do not fall between start_execution_date and stop_execution_date AND DT.FullDateTime BETWEEN @JOBstart_execution_date AND @JOBstop_execution_date ORDER BY t2.step_id ASC'+ IIF(@Mode IN (4,5),N',EM.message_time ASC',N'')
SELECT @SELECT += @FROM + @WHERE;
begin try EXEC sp_executesql @SELECT ,N'@job_id UNIQUEIDENTIFIER ,@JOBstart_execution_date DATETIME,@JOBstop_execution_date DATETIME,@Run_id UNIQUEIDENTIFIER',@job_id = @job_id, @JOBstart_execution_date = @JOBstart_execution_date, @JOBstop_execution_date = @JOBstop_execution_date,@Run_id = @Run_id; PRINT @SELECT END TRY BEGIN CATCH SET @Error = ERROR_MESSAGE() EXEC [dbo].[PrintMax] @SELECT; RAISERROR(@Error,16,1); END CATCH
END

BEGIN SET NOCOUNT ON;
DECLARE @RunNumber uniqueidentifier SELECT TOP 1 @RunNumber = RunNumber FROM SSIS.JobExecuteInfo JEI WHERE JEI.FullDateTime BETWEEN format(@RunDate,'yyyyMMdd 21:30:00.000') AND format(@RunDate,'yyyyMMdd 22:30:00.000'); WITH CTE AS ( SELECT JEI.JobName,JEI.StepID,JEI.StepName, CONVERT(nvarchar(MAX),REPLACE(JEI.PackageName,'.dtsx','') + '::>' + CONVERT(nvarchar(20),PH.ID)) [Hierarchy], 1 [Level], DATEDIFF(MINUTE,JEI.PackageStartTime,JEI.MessageTime)[AggDuration],PH.executable_name, JEI.FullDateTime,JEI.JobDuration,JEI.PackageRunDuration,JEI.ElapsedTime,JEI.MessageTime,JEI.MessageDescription,JEI.MessageSourceDescription,JEI.Message,JEI.MessageExecutionStatus,JEI.MessageGenerated,JEI.PackageName,JEI.PackageStartTime,JEI.PackageEndTime, PH.ID,PH.Parent_PackageHierarchy FROM SSIS.JobExecuteInfo JEI INNER JOIN SSIS.PackageHierarchy PH ON PH.PackageName = JEI.PackageName COLLATE DATABASE_DEFAULT AND JEI.message LIKE PH.[executable_name] + '%' COLLATE DATABASE_DEFAULT WHERE PH.Parent_PackageHierarchy IS NULL AND JEI.RunNumber = @RunNumber --AND PH.ID NOT IN (SELECT [SheardEndConector] FROM SSIS.PackageHierarchy WHERE [SheardEndConector] IS NOT NULL) UNION ALL SELECT JEI.JobName,JEI.StepID,JEI.StepName, c.[Hierarchy] + '>' + CONVERT(nvarchar(20),PH.ID) [Hierarchy], C.[Level] + 1 [Level], DATEDIFF(MINUTE,JEI.PackageStartTime,JEI.MessageTime)[AggDuration],PH.executable_name, JEI.FullDateTime,JEI.JobDuration,JEI.PackageRunDuration,JEI.ElapsedTime,JEI.MessageTime,JEI.MessageDescription,JEI.MessageSourceDescription,JEI.Message,JEI.MessageExecutionStatus,JEI.MessageGenerated,JEI.PackageName,JEI.PackageStartTime,JEI.PackageEndTime, PH.ID,PH.Parent_PackageHierarchy FROM CTE C INNER JOIN SSIS.JobExecuteInfo JEI ON C.PackageName = JEI.PackageName AND JEI.RunNumber = @RunNumber INNER JOIN SSIS.PackageHierarchy PH ON PH.PackageName = JEI.PackageName COLLATE DATABASE_DEFAULT AND JEI.message LIKE PH.[executable_name] + '%' COLLATE DATABASE_DEFAULT AND PH.Parent_PackageHierarchy = C.ID WHERE PH.Parent_PackageHierarchy IS NOT NULL -- ) SELECT *,MAX([AggDuration]) OVER(PARTITION BY C.PackageName) FROM CTE c ORDER BY C.StepID ASC,IIF(C.message LIKE '%:Finished,%',999,C.Parent_PackageHierarchy) ASC
END

BEGIN
	SET NOCOUNT ON;
    DECLARE @rc INT;
    DECLARE @TraceID INT;
    DECLARE @StopTime DATETIME = GETDATE();

    BEGIN TRY
        IF @FilePath IS NULL
            SELECT  @FilePath = N'H:\Temp\Qure' + N'\Qure_'
                    + CONVERT(VARCHAR(20), GETUTCDATE(), 112) + '_'
                    + REPLACE(CONVERT(VARCHAR(20), GETUTCDATE(), 108), ':', '');

        IF ISNULL(@maxfilesize, 0) <= 0
            SET @maxfilesize = 50;

        SET @StopTime = DATEADD(MINUTE, @DurationInMinutes, @StopTime);

        EXEC @rc = sp_trace_create @TraceID OUTPUT, 0, @FilePath, @maxfilesize,
            @StopTime; 
        IF ( @rc != 0 )
            RAISERROR('could not create trace',16,1);



		-- Set the events
        DECLARE @on BIT;
        SET @on = 1;
		EXEC sp_trace_setevent @TraceID, 10, 1, @on;
		EXEC sp_trace_setevent @TraceID, 10, 10, @on;
		EXEC sp_trace_setevent @TraceID, 10, 6, @on;
		EXEC sp_trace_setevent @TraceID, 10, 8, @on;
		EXEC sp_trace_setevent @TraceID, 10, 11, @on;
		EXEC sp_trace_setevent @TraceID, 10, 12, @on;
		EXEC sp_trace_setevent @TraceID, 10, 13, @on;
		EXEC sp_trace_setevent @TraceID, 10, 14, @on;
		EXEC sp_trace_setevent @TraceID, 10, 15, @on;
		EXEC sp_trace_setevent @TraceID, 10, 16, @on;
		EXEC sp_trace_setevent @TraceID, 10, 17, @on;
		EXEC sp_trace_setevent @TraceID, 10, 18, @on;
		EXEC sp_trace_setevent @TraceID, 10, 26, @on;
		EXEC sp_trace_setevent @TraceID, 10, 31, @on;
		EXEC sp_trace_setevent @TraceID, 10, 35, @on;
		EXEC sp_trace_setevent @TraceID, 10, 48, @on;
		EXEC sp_trace_setevent @TraceID, 10, 60, @on;
		EXEC sp_trace_setevent @TraceID, 12, 1, @on;
		EXEC sp_trace_setevent @TraceID, 12, 11, @on;
		EXEC sp_trace_setevent @TraceID, 12, 6, @on;
		EXEC sp_trace_setevent @TraceID, 12, 8, @on;
		EXEC sp_trace_setevent @TraceID, 12, 10, @on;
		EXEC sp_trace_setevent @TraceID, 12, 12, @on;
		EXEC sp_trace_setevent @TraceID, 12, 13, @on;
		EXEC sp_trace_setevent @TraceID, 12, 14, @on;
		EXEC sp_trace_setevent @TraceID, 12, 15, @on;
		EXEC sp_trace_setevent @TraceID, 12, 16, @on;
		EXEC sp_trace_setevent @TraceID, 12, 17, @on;
		EXEC sp_trace_setevent @TraceID, 12, 18, @on;
		EXEC sp_trace_setevent @TraceID, 12, 26, @on;
		EXEC sp_trace_setevent @TraceID, 12, 31, @on;
		EXEC sp_trace_setevent @TraceID, 12, 35, @on;
		EXEC sp_trace_setevent @TraceID, 12, 48, @on;
		EXEC sp_trace_setevent @TraceID, 12, 60, @on;


	-- Set the Filters
        DECLARE @intfilter INT;
        DECLARE @bigintfilter BIGINT;
		IF @HostName IS NOT NULL
			EXEC sp_trace_setfilter @TraceID, 8, 0, 6, @HostName;-- N'SPIDER2'
	-- Set the trace status to start
        EXEC sp_trace_setstatus @TraceID, 1;

	-- display trace id for future references
	--select TraceID=@TraceID

        RETURN(0);
    END TRY
    BEGIN CATCH
        THROW;
        RETURN (-1);
    END CATCH;
END

/*
This procedures starts a server side trace for the blocking report event.

Parameters:
@FilePath - The location of the trace file.  If you don't send any value it will use the path d:\trace.  Make
sure that you have this path or always send the needed path.  You can also modify the code to have a different default path.

@maxfilesize - As the name implies the trace file's maxsimum size

@DurationInMinutes - How many minutes the trace will run until it will stop running aotumaticly

*/

CREATE PROCEDURE [Utility].[usp_PerformanceTuning_SetQureTrace]
    (
      @FilePath NVARCHAR(150) = NULL ,
      @maxfilesize BIGINT = 50 ,
      @DurationInMinutes INT = 120,
	  @HostName sysname = NULL
    )
AS
